-- Define the system context
context BatteryChargingSystem {
  -- Define the battery model
  Block Battery {
    property float batteryLevel;
  }

  -- Define the charging system model
  Block ChargingSystem {
    internal Block Battery: Battery;

    -- Define the charging operation
    Operation Charging {
      precondition: batteryLevel < 100%;
      postcondition: batteryLevel >= 100%;

      -- Define the charging behavior
      behavior ChargingBehavior {
        entryAction:
          Battery.batteryLevel < 100% implies
            Battery.batteryLevel := Battery.batteryLevel + 1;

        activity ChargingActivity {
          initialNode: entry;
          finalNode: termination;
          node "Charging" {
            transition toCharging when Battery.batteryLevel < 100%;
            transition toTermination when Battery.batteryLevel >= 100%;
          }
        }
      }
    }
  }
}

