block Sensor {
  -- Define the attributes
  attribute status : String = "idle";

  -- Define the operations
  operation update() {
    self.status = "ready";
  }
}

-- Define the SensorManager block
block SensorManager {
  -- Define the internal model elements
  internal Sensor[] sensors;

  -- Define the operations
  operation registerSensor(in sensor : Sensor) {
    self.sensors <- self.sensors + sensor;
  }

  operation checkReadySensors() : Integer {
    var readySensorsCount : Integer = 0;

    for (sensor in self.sensors) {
      if (sensor.status = "ready") {
        sensor.update();
        readySensorsCount = readySensorsCount + 1;
      }
    }

    return readySensorsCount;
  }
}
