Block 'MassManagementSystem' {
  Attribute mass : Real;

  Operation calculateTotalMass() : Real {
    -- Calculate the total mass of the system and its subcomponents
    Real totalMass = mass;
    for (SubComponent subComponent : self.allSubComponents) {
      totalMass += subComponent.mass;
    }
    return totalMass;
  }

  Operation filterSubComponents(minMass : Real) : Set(SubComponent) {
    -- Filter subcomponents based on mass threshold
    Set(SubComponent) filteredSubComponents = {};
    for (SubComponent subComponent : self.allSubComponents) {
      if (subComponent.mass >= minMass) {
        filteredSubComponents.add(subComponent);
      }
    }
    return filteredSubComponents;
  }
