-- Define the Automobile System
model AutomobileAssemblySystem

-- Define the Vehicle Assembly Block
Block VehicleAssembly {
    -- Define the Input and Output Interfaces
    Provided GearboxInput : Input Gearbox;
    Provided EngineInput : Input Engine;
    Provided FinalAssemblyOutput : Output Vehicle;

    -- Define the Internal Structure
    Part VehicleBody;

    -- Define the Assembly Operations
    Assembly GearboxAssembly {
        Precondition: GearboxInput.isAvailable();
        Postcondition: GearboxInput.isConsumed();
        Result: this.hasGearbox(GearboxAssembly.Result);
    }

    Assembly EngineAssembly {
        Precondition: EngineInput.isAvailable();
        Postcondition: EngineInput.isConsumed();
        Result: this.hasEngine(EngineAssembly.Result);
    }

    Assembly FinalAssembly {
        Precondition: this.hasGearbox() and this.hasEngine();
        Postcondition: Vehicle.isAvailable();
        Result: Vehicle;
    }
}

