package InformationClassificationSystem {

    enum def InformationClassificationLevel {
        enum unclassified;
        enum confidential;
        enum secret;
    }

    enum def GradeLevel {
        enum A;
        enum B;
        enum C;
        enum D;
        enum F;
    }

    attribute def InformationClassificationCode {
        attribute code : String;
    }

    attribute def InformationClassificationColor {
        attribute color : String;
    }

    attribute def GradePoints {
        attribute points : Real;
    }

    part def InformationClassification {
        attribute level : InformationClassificationLevel;
        attribute code : String;
        attribute color : String;

        constraint {
            (level == InformationClassificationLevel::unclassified) implies 
                (code == "uncl" and color == "green")
        }

        constraint {
            (level == InformationClassificationLevel::confidential) implies 
                (code == "conf" and color == "yellow")
        }

        constraint {
            (level == InformationClassificationLevel::secret) implies 
                (code == "secr" and color == "red")
        }
    }

    part def GradeClassification {
        attribute grade : GradeLevel;
        attribute points : Real;

        constraint {
            (grade == GradeLevel::A) implies (points == 4.0)
        }

        constraint {
            (grade == GradeLevel::B) implies (points == 3.0)
        }

        constraint {
            (grade == GradeLevel::C) implies (points == 2.0)
        }

        constraint {
            (grade == GradeLevel::D) implies (points == 1.0)
        }

        constraint {
            (grade == GradeLevel::F) implies (points == 0.0)
        }
    }

    part def InformationManagementSystem {
        part informationClassifier : InformationClassification[*];
        part gradeClassifier : GradeClassification[*];

        action classifyInformation {
            in item info : InformationItem;
            out item classifiedInfo : ClassifiedInformation;
        }

        action assignGrade {
            in item gradedItem : GradableItem;
            out item gradedResult : GradedItem;
        }
    }

    item def InformationItem {
        attribute content : String;
    }

    item def ClassifiedInformation :> InformationItem {
        attribute classification : InformationClassification;
    }

    item def GradableItem {
        attribute name : String;
    }

    item def GradedItem :> GradableItem {
        attribute gradeClassification : GradeClassification;
    }
}

