        attribute isRunning : Boolean = false;

        port control : ~EngineControlInterface;
        port torqueOutput : TorqueInterface;

        action generateTorque {
            in fuelCmd : Real;
            out torque : Real;
        }
    }

    part def Transmission {
        attribute gearRatio : Real;
        attribute amplifiedTorque : Real;

        port torqueInput : ~TorqueInterface;
        port torqueOutput : TorqueInterface;
        port control : ~TransmissionControlInterface;

        action amplifyTorque {
            in inputTorque : Real;
            in ratio : Real;
            out outputTorque : Real;
        }
    }

    part def TorqueDistributor {
        attribute distributedTorque : Real[4];

        port torqueInput : ~TorqueInterface;
        port wheelTorqueOutput : TorqueInterface[4];

        action distributeTorque {
            in totalTorque : Real;
            out wheelTorques : Real[4];
        }
    }

    part def Wheel {
        attribute appliedTorque : Real;

        port torqueInput : ~TorqueInterface;
    }

    interface def FuelCommandInterface {
        flow fuelCommand : Real;
    }

    interface def EngineControlInterface {
        flow startSignal : Boolean;
        flow shutdownSignal : Boolean;
        flow fuelCommand : Real;
    }

    interface def TransmissionControlInterface {
        flow gearCommand : Real;
    }

    interface def TorqueInterface {
        flow torque : Real;
    }

    part vehiclePowertrain : PowertrainController {
        part engine : Engine;
        part transmission : Transmission;
        part torqueDistributor : TorqueDistributor;
        part wheels : Wheel[4];

        connect vehiclePowertrain.engineControl to engine.control;
        connect vehiclePowertrain.transmissionControl to transmission.control;
        connect engine.torqueOutput to transmission.torqueInput;
        connect transmission.torqueOutput to torqueDistributor.torqueInput;

        for i in 1..4 {
            connect torqueDistributor.wheelTorqueOutput[i] to wheels[i].torqueInput;
        }

        state machine PowertrainStates {
            entry state off;
            state running {
                entry action {
                    engine.isRunning := true;
                }
                do action {
                    engine.generateTorque(vehiclePowertrain.fuelCommand) >> engine.outputTorque;
                    transmission.amplifyTorque(engine.outputTorque, transmission.gearRatio) >> transmission.amplifiedTorque;
                    torqueDistributor.distributeTorque(transmission.amplifiedTorque) >> torqueDistributor.distributedTorque;
                }
                exit action {
                    engine.isRunning := false;
                    engine.outputTorque := 0;
                }
            }

            transition off_to_running : off --> running {
                guard vehiclePowertrain.engineStartSignal;
            }

            transition running_to_off : running --> off {
                guard vehiclePowertrain.engineShutdownSignal;
            }
        }
    }

    requirement integratedManagement {
        doc /* The system shall achieve integrated management of the vehicle powertrain system according to the driver's fuel input command */
        satisfy vehiclePowertrain;
    }

    requirement torqueGeneration {
        doc /* The system shall generate corresponding engine output torque based on the fuel command when engine start signal is activated */
        satisfy engine.generateTorque;
    }

    requirement torqueTransmission {
        doc /* The system shall amplify and transmit torque sequentially through the transmission */
        satisfy transmission.amplifyTorque;
    }

    requirement torqueDistribution {
        doc /* The system shall distribute torque to each wheel */
        satisfy torqueDistributor.distributeTorque;
    }

    requirement powerTermination {
        doc /* The system shall terminate power output process when engine shutdown signal is triggered */
        satisfy vehiclePowertrain.PowertrainStates.running_to_off;
    }

    requirement performanceEfficiency {
        doc /* The system shall ensure automatic control accurately and efficiently in power demand response, torque transmission, and power distribution */
        refine integratedManagement;
    }
}

