package VehicleTrailerSystem {
    import ScalarValues::*;

    part def Vehicle {
        part chassis : Chassis;
        part towBall : TowBall;
    }

    part def Trailer {
        part trailerChassis : TrailerChassis;
        part coupler : CouplerMechanism;
    }

    part def Chassis {
        attribute mass : Real;
    }

    part def TrailerChassis {
        attribute mass : Real;
    }

    part def TowBall {
        attribute diameter : Real;
        attribute height : Real;
    }

    part def CouplerMechanism {
        attribute latchState : LatchStateType;

        action engage {
            in towBall : TowBall;
            out result : Boolean;
        }

        action disengage {
            out result : Boolean;
        }
    }

    enum def LatchStateType {
        enum latched;
        enum unlatched;
    }

    connection def TowConnection {
        end vehicle : Vehicle[1];
        end trailer : Trailer[0..1];
    }

    action def HitchOperation {
        in vehicle : Vehicle;
        in trailer : Trailer;
        out connection : TowConnection;

        action connectCoupler {
            in item coupler = trailer.coupler;
            in item towBall = vehicle.towBall;

            perform coupler.engage(towBall);
        }
    }

    action def UnhitchOperation {
        in connection : TowConnection;

        action disconnectCoupler {
            in item coupler = connection.trailer.coupler;

            perform coupler.disengage();
        }
    }

    requirement def SecureConnection {
        doc /* When connected, the trailer's coupler shall securely latch onto the vehicle's tow ball */

        subject connection : TowConnection;

        require constraint {
            connection.trailer != null implies 
            connection.trailer.coupler.latchState == LatchStateType::latched
        }
    }

    requirement def SmoothDisconnection {
        doc /* Disconnection shall allow the vehicle and trailer to separate smoothly */

        subject unhitchOp : UnhitchOperation;

        require constraint {
            unhitchOp.connection.trailer.coupler.latchState == LatchStateType::unlatched
        }
    }
}

