package AutomobileAssemblyModel {

    part def VehicleAssembly {
        attribute vin : String;
        attribute assemblyStatus : String;
    }

    part def Gearbox {
        attribute serialNumber : String;
        attribute type : String;
        attribute functional : Boolean = true;
    }

    part def Engine {
        attribute serialNumber : String;
        attribute displacement : Real;
        attribute functional : Boolean = true;
    }

    part def AssemblyWithoutEngine :> VehicleAssembly {
        part gearbox : Gearbox;
    }

    part def FullyAssembledVehicle :> VehicleAssembly {
        part gearbox : Gearbox;
        part engine : Engine;

        attribute powerDeliveryCapable : Boolean = gearbox.functional and engine.functional;
    }

    action def InstallGearbox {
        in vehicleAssembly : VehicleAssembly;
        in gearbox : Gearbox;
        out assemblyWithGearbox : AssemblyWithoutEngine;

        bind assemblyWithGearbox.gearbox = gearbox;
        bind assemblyWithGearbox.vin = vehicleAssembly.vin;
        bind assemblyWithGearbox.assemblyStatus = "Gearbox Installed";
    }

    action def InstallEngine {
        in assemblyWithGearbox : AssemblyWithoutEngine;
        in engine : Engine;
        out fullyAssembledVehicle : FullyAssembledVehicle;

        bind fullyAssembledVehicle.gearbox = assemblyWithGearbox.gearbox;
        bind fullyAssembledVehicle.engine = engine;
        bind fullyAssembledVehicle.vin = assemblyWithGearbox.vin;
        bind fullyAssembledVehicle.assemblyStatus = "Fully Assembled";
    }

    action def FinalAssemblyProcess {
        in initialVehicleAssembly : VehicleAssembly;
        in gearboxUnit : Gearbox;
        in engineUnit : Engine;
        out completeVehicle : FullyAssembledVehicle;

        action installGearboxStep : InstallGearbox {
            in vehicleAssembly = initialVehicleAssembly;
            in gearbox = gearboxUnit;
        }

        flow installGearboxStep.assemblyWithGearbox to installEngineStep.assemblyWithGearbox;

        action installEngineStep : InstallEngine {
            in engine = engineUnit;
        }

        flow installEngineStep.fullyAssembledVehicle to completeVehicle;

        assert constraint {
            doc /* Assembly sequence must be maintained: gearbox before engine */
            installGearboxStep happens before installEngineStep
        }
    }

    requirement def AssemblySequenceRequirement {
        doc /* The gearbox must be installed before the engine */
        subject finalAssembly : FinalAssemblyProcess;
        require constraint {
            finalAssembly.installGearboxStep happens before finalAssembly.installEngineStep
        }
    }

    requirement def ComponentFunctionalityRequirement {
        doc /* The fully assembled vehicle must have functional gearbox and engine units */
        subject vehicle : FullyAssembledVehicle;
        require constraint {
            vehicle.gearbox.functional == true and vehicle.engine.functional == true
        }
    }

    requirement def AssemblyIntegrityRequirement {
        doc /* Each assembly step must have clear inputs and outputs */
        subject process : FinalAssemblyProcess;
        require constraint {
            process.initialVehicleAssembly != null and
            process.gearboxUnit != null and
            process.engineUnit != null and
            process.completeVehicle != null
        }
    }
}

package VehicleControlSystem {
    import ScalarValues::*;

    part def VehicleA {
        attribute temperature : Real;
        attribute safetyThreshold : Real = 85.0;

        port brakePedalInput : ~BrakePedalSignal;
        port startSignalInput : ~StartSignal;
        port vehicleOnSignalInput : ~VehicleOnSignal;
        port restoreSignalInput : ~RestoreSignal;
        port temperatureSensor : ~TemperatureReading;
        port overTempSignalOutput : OverTempSignal;
        port powerSupplyOutput : PowerSupply;
        port parkingBrakeOutput : ParkingBrake;

        exhibit state vehicleStates {
            entry state off;
            state starting;
            state running {
                entry action {
                    perform selfCheck;
                    send powerSupplyOutput;
                }
                exit action {
                    send parkingBrakeOutput;
                }
            }

            transition off_to_starting : off --> starting
                when brakePedalInput & startSignalInput;

            transition starting_to_running : starting --> running
                when vehicleOnSignalInput;

            transition running_to_off : running --> off;
        }

        exhibit state healthStates parallel {
            state normal;
            state degraded {
                entry action {
                    send overTempSignalOutput;
                }
            }
            state maintenance;

            transition normal_to_degraded : normal --> degraded
                when temperature > safetyThreshold;

            transition degraded_to_normal : degraded --> normal
                when restoreSignalInput;

            transition maintenance_to_normal : maintenance --> normal
                when restoreSignalInput;
        }

        action selfCheck;

        constraint temperatureMonitoring {
            temperature == temperatureSensor.value
        }
    }

    part def VehicleController {
        port startupSignalInput : ~StartupSignal;
        port shutdownSignalInput : ~ShutdownSignal;
        port vehicleControlOutput : VehicleControlSignal;

        exhibit state controllerStates {
            state idle;
            state active;

            transition idle_to_active : idle --> active
                when startupSignalInput;

            transition active_to_idle : active --> idle
                when shutdownSignalInput;
        }
    }

    interface def BrakePedalSignal;
    interface def StartSignal;
    interface def VehicleOnSignal;
    interface def RestoreSignal;
    interface def TemperatureReading {
        attribute value : Real;
    }
    interface def OverTempSignal;
    interface def PowerSupply;
    interface def ParkingBrake;
    interface def StartupSignal;
    interface def ShutdownSignal;
    interface def VehicleControlSignal;

    part vehicleSystem : VehicleControlSystem {
        part vehicle : VehicleA;
        part controller : VehicleController;

        interface : VehicleControlInterface connect controller.vehicleControlOutput to vehicle.startSignalInput;
    }

    interface def VehicleControlInterface;
}

package VehicleSystem {
    import ScalarValues::*;

    attribute def Temperature;
    attribute def FuelLevel;
    attribute def MaintenanceInterval;

    enum def VehicleState {
        enum shutdown;
        enum starting;
        enum running;
        enum degraded;
        enum maintenance;
    }

    enum def SignalType {
        enum startSignal;
        enum warningSignal;
        enum shutdownSignal;
    }

    part def Vehicle {
        attribute currentState : VehicleState = VehicleState::shutdown;
        attribute temperature : Temperature;
        attribute fuel : FuelLevel;
        attribute maintenanceInterval : MaintenanceInterval;
        attribute brakePedalPressed : Boolean = false;
        attribute startSignalReceived : Boolean = false;
        attribute isOverheating : Boolean = false;

        port controlPort {
            in item startCommand;
            in item shutdownCommand;
            out item warningSignal;
            out item statusSignal;
        }

        action selfInspection {
            doc /* Performs vehicle self-inspection */
        }

        action powerSupply {
            doc /* Manages power supply procedures */
        }

        action performMaintenance {
            doc /* Executes maintenance procedures */
        }

        action recoverFromFault {
            doc /* Recovers from fault conditions */
        }

        state vehicleStates {
            entry state shutdown;
            state starting;
            state running;
            state degraded;
            state maintenance;

            transition shutdown_to_starting
                first shutdown
                accept controlPort.startCommand
                if brakePedalPressed and startSignalReceived
                do selfInspection then powerSupply
                then starting;

            transition starting_to_running
                first starting
                then running;

            transition running_to_degraded
                first running
                if isOverheating
                do send warningSignal via controlPort
                then degraded;

            transition degraded_to_maintenance
                first degraded
                then maintenance;

            transition maintenance_to_running
                first maintenance
                do performMaintenance then recoverFromFault
                then running;

            transition any_to_shutdown
                first running, degraded, maintenance
                accept controlPort.shutdownCommand
                then shutdown;
        }
    }

    part def VehicleController {
        port vehiclePort {
            out item startCommand;
            out item shutdownCommand;
            in item warningSignal;
            in item statusSignal;
        }

        action processControlSignal {
            in signal : SignalType;
            doc /* Processes control signals */
        }

        action processStatusSignal {
            in status : VehicleState;
            doc /* Processes vehicle status signals */
        }

        action sendStartCommand {
            doc /* Sends start command to vehicle */
            send startCommand via vehiclePort;
        }

        action sendShutdownCommand {
            doc /* Sends shutdown command to vehicle */
            send shutdownCommand via vehiclePort;
        }
    }

    part vehicleSystem {
        part vehicle : Vehicle;
        part controller : VehicleController;

        interface vehicleControlInterface connect 
            vehicle.controlPort to controller.vehiclePort;

        action monitorTemperature {
            doc /* Continuously monitors vehicle temperature */
            loop {
                if vehicle.temperature > temperatureThreshold {
                    vehicle.isOverheating := true;
                }
            }
        }

        attribute temperatureThreshold : Temperature;

        requirement startupRequirement {
            doc /* Vehicle shall only start when brake pedal is pressed and start signal is received */
            verify vehicle.currentState == VehicleState::starting 
                implies (vehicle.brakePedalPressed and vehicle.startSignalReceived);
        }

        requirement safetyRequirement {
            doc /* Vehicle shall enter degraded mode when overheating is detected */
            verify vehicle.isOverheating 
                implies vehicle.currentState == VehicleState::degraded;
        }

        requirement maintenanceRequirement {
            doc /* Vehicle shall support regular maintenance and fault recovery */
            verify vehicle.currentState == VehicleState::maintenance 
                implies eventually vehicle.currentState == VehicleState::running;
        }
    }
}

package VehicleDynamicsSimulation {
    import ScalarValues::*;
    import Quantities::*;
    import MeasurementReferences::*;
    import Time::*;
    import SpaceTime::*;

    enum def VehicleStateKind {
        enum on;
        enum off;
        enum driving;
    }

    attribute def Mass :> MassValue;
    attribute def Position :> LengthValue;
    attribute def Velocity :> SpeedValue;
    attribute def Acceleration :> AccelerationValue;
    attribute def Angle :> PlaneAngleValue;
    attribute def FrictionCoefficient :> Real;

    item def Vehicle {
        attribute mass : Mass;
        attribute position : Position[3];
        attribute velocity : Velocity[3];
        attribute acceleration : Acceleration[3];
        attribute currentState : VehicleStateKind;

        port vehicleStatePort : VehicleStatePort;
    }

    item def Road {
        attribute slope : Angle;
        attribute frictionCoefficient : FrictionCoefficient;
        attribute length : Position;

        port roadConditionPort : RoadConditionPort;
    }

    port def VehicleStatePort {
        out attribute vehiclePosition : Position[3];
        out attribute vehicleVelocity : Velocity[3];
        out attribute vehicleAcceleration : Acceleration[3];
        out attribute vehicleState : VehicleStateKind;
    }

    port def RoadConditionPort {
        out attribute roadSlope : Angle;
        out attribute roadFriction : FrictionCoefficient;
    }

    attribute def SimulationSnapshot {
        attribute timeStamp : TimeInstantValue;
        attribute vehicleState : VehicleStateKind;
        attribute vehiclePosition : Position[3];
        attribute vehicleVelocity : Velocity[3];
        attribute vehicleAcceleration : Acceleration[3];
        attribute roadSlope : Angle;
        attribute roadFriction : FrictionCoefficient;
    }

    part def SpatiotemporalSimulationSystem {
        part vehicle : Vehicle;
        part road : Road;

        attribute simulationStartTime : TimeInstantValue;
        attribute simulationEndTime : TimeInstantValue;
        attribute simulationTimeStep : DurationValue;
        attribute snapshots : SimulationSnapshot[*] ordered;

        interface vehicleRoadInterface connect vehicle.vehicleStatePort to road.roadConditionPort;

        action simulateVehicleDynamics {
            in startTime : TimeInstantValue;
            in endTime : TimeInstantValue;
            in timeStep : DurationValue;
            out snapshotSeries : SimulationSnapshot[*];

            action startVehicle {
                assign vehicle.currentState := VehicleStateKind::on;
            }

            action updateVehicleState {
                in currentTime : TimeInstantValue;
                in roadSlope : Angle;
                in roadFriction : FrictionCoefficient;

                assign vehicle.currentState := VehicleStateKind::driving;
            }

            action shutdownVehicle {
                assign vehicle.currentState := VehicleStateKind::off;
            }

            action recordSnapshot {
                in time : TimeInstantValue;
                out snapshot : SimulationSnapshot;
            }
        }

        requirement trackStateTransitions {
            doc /* The system shall track vehicle state transitions from on through driving to off */
        }

        requirement multipleSnapshots {
            doc /* The system shall support recording snapshots at multiple time points */
        }

        requirement vehicleDynamicsAnalysis {
            doc /* The system shall provide detailed data for vehicle dynamics analysis */
        }

        requirement roadAdaptabilityAssessment {
            doc /* The system shall support road adaptability assessment */
        }

        requirement virtualEnvironmentPrediction {
            doc /* The system shall enable performance prediction in virtual environment */
        }
    }
}

package AutomotiveConfigurationSystem {

    part def Component1;
    part def Component2;
    part def Component3;
    part def Component4;
    part def Component5;
    part def Component6;

    part def SubsystemA {
        variant part config1 {
            part c1 : Component1;
            part c2 : Component2;
        }
        variant part config2 {
            part c2 : Component2;
            part c3 : Component3;
        }
    }

    part def SubsystemB {
        variant part config1 {
            part c4 : Component4;
            part c5 : Component5;
        }
        variant part config2 {
            part c5 : Component5;
            part c6 : Component6;
        }
    }

    part def Automobile {
        part subsystemA : SubsystemA[0..1];
        part subsystemB : SubsystemB[0..1];

        constraint configurationConstraint {
            (subsystemA.config2 == null) or (subsystemB.config1 != null)
        }
    }

    part configurationA : Automobile {
        part :>> subsystemA {
            part :>> config1;
        }
        part :>> subsystemB {
            part :>> config1;
        }
    }

    part configurationB : Automobile {
        part :>> subsystemA {
            part :>> config2;
        }
        part :>> subsystemB {
            part :>> config1;
        }
    }
}

package VehicleConfigurationSystem {

    // Base component definitions
    part def Part1;
    part def Part2;
    part def Part3 {
        port p1;
    }
    part def Part4;
    part def Part5 {
        port p2;
        variant behavior behaviorVariant1;
        variant behavior behaviorVariant2;
    }
    part def Part6;

    // Abstract subsystem definitions
    abstract part def SubsystemA {
        port portA;
    }

    abstract part def SubsystemB {
        port portB;
    }

    // Subsystem A variants
    part def SubsystemA_Variant1 :> SubsystemA {
        part component1 : Part1;
        part component2 : Part2;
        part component3 : Part3;
    }

    part def SubsystemA_Variant2 :> SubsystemA {
        part component1 : Part1;
        part component3 : Part3;
        part component4 : Part4;
    }

    // Subsystem B variants
    part def SubsystemB_Variant1 :> SubsystemB {
        part component5 : Part5;
        part component6 : Part6;
    }

    part def SubsystemB_Variant2 :> SubsystemB {
        part component4 : Part4;
        part component5 : Part5;
    }

    // Vehicle platform definition
    part def VehiclePlatform {
        part subsystemA : SubsystemA;
        part subsystemB : SubsystemB;

        interface : subsystemA.portA to subsystemB.portB;
    }

    // Configuration definitions
    part def VehicleConfigA :> VehiclePlatform {
        part :>> subsystemA : SubsystemA_Variant1;
        part :>> subsystemB : SubsystemB_Variant1;
    }

    part def VehicleConfigB :> VehiclePlatform {
        part :>> subsystemA : SubsystemA_Variant2;
        part :>> subsystemB : SubsystemB_Variant2;
    }

    // Constraints
    constraint def ConfigurationConsistency {
        doc /* Ensures configuration consistency and rationality */
    }

    // Variation points
    variation part def ConfigurableVehicle :> VehiclePlatform {
        variant part :>> subsystemA {
            variant : SubsystemA_Variant1;
            variant : SubsystemA_Variant2;
        }
        variant part :>> subsystemB {
            variant : SubsystemB_Variant1;
            variant : SubsystemB_Variant2;
        }
    }
}

