        attribute :>> powerOutput = 300.0;
    }

    part def FourCylinderEngine :> Engine {
        attribute :>> cylinders = 4;
        attribute :>> powerOutput = 150.0;
    }

    part def AutomaticTransmission :> Transmission {
        attribute :>> type = "Automatic";
        attribute :>> gearCount = 8;
    }

    part def ManualTransmission :> Transmission {
        attribute :>> type = "Manual";
        attribute :>> gearCount = 6;
    }

    part def WideRimWheel :> Wheel {
        attribute :>> rimType = "Wide";
        attribute :>> width = 255.0;
    }

    part def NarrowRimWheel :> Wheel {
        attribute :>> rimType = "Narrow";
        attribute :>> width = 195.0;
    }

    // Configuration constraints
    constraint def HighPerformanceConfiguration {
        doc /* High performance requires 6-cylinder engine, automatic transmission, and wide wheels */

        in vehicle : Vehicle;

        vehicle.performanceLevel == "High" implies (
            vehicle.engine istype SixCylinderEngine and
            vehicle.transmission istype AutomaticTransmission and
            vehicle.wheels->forAll(w | w istype WideRimWheel)
        )
    }

    constraint def StandardPerformanceConfiguration {
        doc /* Standard performance requires 4-cylinder engine, manual transmission, and narrow wheels */

        in vehicle : Vehicle;

        vehicle.performanceLevel == "Standard" implies (
            vehicle.engine istype FourCylinderEngine and
            vehicle.transmission istype ManualTransmission and
            vehicle.wheels->forAll(w | w istype NarrowRimWheel)
        )
    }

    constraint def ComponentCompatibility {
        doc /* Ensures compatibility between engine and transmission */

        in vehicle : Vehicle;

        (vehicle.engine istype SixCylinderEngine implies 
            vehicle.transmission istype AutomaticTransmission) and
        (vehicle.engine istype FourCylinderEngine implies 
            vehicle.transmission istype ManualTransmission)
    }

    // Use case for configuration selection
    use case def ConfigureVehicle {
        subject vehicle : Vehicle;

        objective {
            doc /* Configure vehicle based on performance requirements and user preferences */
        }

        include selectPerformanceLevel;
        include selectModelVariant;
        include validateConfiguration;
    }

    use case selectPerformanceLevel {
        doc /* User selects desired performance level */
    }

    use case selectModelVariant {
        doc /* User selects vehicle model variant */
    }

    use case validateConfiguration {
        doc /* System validates component compatibility */
    }

    // Requirement definitions
    requirement def FlexibleConfiguration {
        doc /* System shall provide flexible solutions for vehicle product line configuration */

        subject vehicle : Vehicle;
    }

    requirement def AutomaticMatching {
        doc /* System shall automatically match suitable combinations based on requirements */

        subject vehicle : Vehicle;

        require constraint HighPerformanceConfiguration;
        require constraint StandardPerformanceConfiguration;
    }

    requirement def MarketResponsiveness {
        doc /* System shall enable quick response to market demands */

        subject vehicle : Vehicle;

        assume {
            doc /* Multiple model variants are available */
            vehicle.modelVariant in ["Sedan", "SUV", "Coupe", "Hatchback"]
        }
    }

    // Analysis context
    analysis def ProductLineAnalysis {
        subject vehicleProductLine : Vehicle[*];

        return configurationsCount = vehicleProductLine->size();
        return highPerformanceCount = vehicleProductLine->select(v | v.performanceLevel == "High")->size();
        return standardPerformanceCount = vehicleProductLine->select(v | v.performanceLevel == "Standard")->size();
    }
}

package VehicleManagementSystem {
    import ScalarValues::*;
    import Quantities::*;
    import MeasurementReferences::*;
    import ISQ::*;

    // Attribute definitions
    attribute def MassValue :> MassValue;
    attribute def TorqueValue :> TorqueValue;
    attribute def ReliabilityValue :> Real;

    // Enumeration for fuel status
    enum def FuelStatus {
        enum fullFuel;
        enum emptyFuel;
    }

    // Enumeration for mass grades
    enum def MassGrade {
        enum grade2000kg;
        enum grade2500kg;
    }

    // Part definitions
    part def Vehicle {
        attribute actualMass : MassValue;
        attribute massGrade : MassGrade;
        attribute fuelStatus : FuelStatus;
        attribute maxMassLimit : MassValue;

        part engine : Engine;
        part transmission : Transmission;

        interface driveInterface : DriveInterface
            connect engine.torqueOutput to transmission.torqueInput;
    }

    part def Engine {
        attribute outputTorque : TorqueValue;
        port torqueOutput : TorquePort;
    }

    part def Transmission {
        port torqueInput : TorquePort;
    }

    // Port definition
    port def TorquePort {
        attribute torque : TorqueValue;
    }

    // Interface definition
    interface def DriveInterface {
        end engineSide : TorquePort;
        end transmissionSide : TorquePort;
    }

    // Requirements
    requirement def VehicleSpecifications {
        doc /* This requirement defines the primary specifications for vehicles */

        requirement massManagement {
            doc /* Vehicle mass must be managed according to grade and fuel status */
            subject vehicle : Vehicle;

            requirement massGradeCompliance {
                doc /* Vehicle must comply with assigned mass grade */
                require constraint {
                    (vehicle.massGrade == MassGrade::grade2000kg) implies 
                    (vehicle.maxMassLimit == 2000[kg])
                    and
                    (vehicle.massGrade == MassGrade::grade2500kg) implies 
                    (vehicle.maxMassLimit == 2500[kg])
                }
            }

            requirement overloadPrevention {
                doc /* Vehicle actual mass must not exceed maximum limit */
                require constraint {
                    vehicle.actualMass <= vehicle.maxMassLimit
                }
            }
        }

        requirement fuelStatusManagement {
            doc /* Fuel status requirements for different vehicle configurations */
            subject vehicle : Vehicle;

            requirement fuelMassCompliance {
                doc /* Mass requirements based on fuel status */
                require constraint {
                    (vehicle.fuelStatus == FuelStatus::fullFuel) implies
                    (vehicle.actualMass <= vehicle.maxMassLimit)
                }
            }
        }
    }

    requirement def PowertrainRequirements {
        doc /* Requirements for vehicle powertrain functionality */
        subject vehicle : Vehicle;

        requirement powerDelivery {
            doc /* Engine must deliver torque through transmission */
            require constraint {
                vehicle.engine.outputTorque > 0[N*m]
            }
        }

        requirement driveInterfaceConnection {
            doc /* Engine and transmission must be effectively connected */
            require constraint {
                vehicle.driveInterface != null
            }
        }
    }

    requirement def ReliabilityRequirements {
        doc /* Vehicle reliability and durability requirements */
        subject vehicle : Vehicle;
        attribute targetReliability : ReliabilityValue;

        requirement lifecyclePerformance {
            doc /* Vehicle must maintain specified performance throughout lifecycle */
            attribute measuredReliability : ReliabilityValue;
            require constraint {
                measuredReliability >= targetReliability
            }
        }
    }

    // Verification cases
    verification def MassVerification {
        subject vehicleUnderTest : Vehicle;
        objective {
            doc /* Verify vehicle mass compliance */
        }

        verify requirement VehicleSpecifications::massManagement::overloadPrevention;
    }

    verification def PowertrainVerification {
        subject vehicleUnderTest : Vehicle;
        objective {
            doc /* Verify powertrain functionality */
        }

        verify requirement PowertrainRequirements::powerDelivery;
        verify requirement PowertrainRequirements::driveInterfaceConnection;
    }
}

package VehicleMassComplianceSystem {
    import ScalarValues::*;
    import Quantities::*;
    import MeasurementReferences::*;
    import ISQ::*;

    attribute def MassValue :> ScalarQuantityValue {
        attribute num : Real;
        attribute mRef : MassUnit;
    }

    attribute def ComplianceStatus {
        attribute isCompliant : Boolean;
    }

    item def Vehicle {
        attribute actualMass : MassValue;
        attribute vehicleID : String;
    }

    item def WeighingEquipment {
        attribute equipmentID : String;
        attribute accuracy : MassValue;
    }

    actor def TestOperator {
        attribute operatorID : String;
        attribute name : String;
    }

    requirement def MaxPermissibleMassRequirement {
        doc /* The vehicle mass shall not exceed the maximum permissible mass */
        attribute maxPermissibleMass : MassValue = MassValue(num = 2500.0, mRef = kg);

        require constraint {
            doc /* Vehicle actual mass must be less than or equal to maximum permissible mass */
        }
    }

    action def MeasureVehicleMass {
        in vehicle : Vehicle;
        in equipment : WeighingEquipment;
        in operator : TestOperator;
        out measuredMass : MassValue;

        action useEquipment {
            doc /* Operator uses weighing equipment to measure vehicle mass */
        }

        action recordMeasurement {
            doc /* Record the measured mass value */
        }

        flow from useEquipment.done to recordMeasurement.start;
    }

    action def VerifyMassCompliance {
        in measuredMass : MassValue;
        in requirement : MaxPermissibleMassRequirement;
        out status : ComplianceStatus;

        action compareMass {
            doc /* Compare measured mass with maximum permissible mass */
            bind status.isCompliant = (measuredMass.num <= requirement.maxPermissibleMass.num);
        }
    }

    action def TestVehicleCompliance {
        in vehicle : Vehicle;
        in equipment : WeighingEquipment;
        in operator : TestOperator;
        in requirement : MaxPermissibleMassRequirement;
        out result : ComplianceStatus;

        action measure : MeasureVehicleMass {
            in vehicle = TestVehicleCompliance::vehicle;
            in equipment = TestVehicleCompliance::equipment;
            in operator = TestVehicleCompliance::operator;
        }

        action verify : VerifyMassCompliance {
            in measuredMass = measure.measuredMass;
            in requirement = TestVehicleCompliance::requirement;
        }

        flow from measure.done to verify.start;
        bind result = verify.status;
    }

    part def VehicleMassComplianceSystemContext {
        part vehicles : Vehicle[1..*];
        part weighingEquipment : WeighingEquipment;
        part testOperator : TestOperator;
        part massRequirement : MaxPermissibleMassRequirement;

        perform testSequence : TestVehicleCompliance[1..*] {
            doc /* Sequential testing of multiple vehicles */
        }
    }
}

package WaterSupplySystem {
    import ScalarValues::*;

    // Define water flow interface
    interface def WaterFlow {
        attribute temperature : Real;
        attribute flowRate : Real;
        attribute pressure : Real;
    }

    // Define port definitions
    port def WaterOutlet {
        out waterFlow : WaterFlow;
    }

    port def WaterInlet {
        in waterFlow : WaterFlow;
    }

    // Define parts
    part def SpigotBank {
        port hotWaterOut : WaterOutlet;
        port coldWaterOut : WaterOutlet;

        attribute mainSupplyPressure : Real;
    }

    part def Spigot {
        port waterIn : WaterInlet;
        port waterOut : WaterOutlet;

        attribute isHot : Boolean;
    }

    part def FaucetInlet {
        port waterIn : WaterInlet;

        attribute inletType : String;
    }

    part def Faucet {
        part hotInlet : FaucetInlet {
            attribute :>> inletType = "hot";
        }
        part coldInlet : FaucetInlet {
            attribute :>> inletType = "cold";
        }

        attribute location : String;
    }

    // Define the main system
    part def WaterSupplyNetwork {
        part spigotBank : SpigotBank;
        part hotSpigot : Spigot {
            attribute :>> isHot = true;
        }
        part coldSpigot : Spigot {
            attribute :>> isHot = false;
        }
        part faucets : Faucet[1..*];

        // Interface connections
        interface hotWaterSupply : WaterFlow connect 
            spigotBank.hotWaterOut to hotSpigot.waterIn;

        interface coldWaterSupply : WaterFlow connect 
            spigotBank.coldWaterOut to coldSpigot.waterIn;

        interface hotDistribution : WaterFlow connect 
            hotSpigot.waterOut to faucets.hotInlet.waterIn;

        interface coldDistribution : WaterFlow connect 
            coldSpigot.waterOut to faucets.coldInlet.waterIn;
    }

    // Requirements
    requirement def ReliableDelivery {
        doc /* System shall reliably deliver hot and cold water from main supply 
             to all downstream faucets */
        subject waterSystem : WaterSupplyNetwork;

        require constraint {
            waterSystem.spigotBank.mainSupplyPressure > 0
        }
    }

    requirement def SimultaneousSupply {
        doc /* System shall ensure simultaneous supply to multiple water usage points */
        subject waterSystem : WaterSupplyNetwork;

        require constraint {
            waterSystem.faucets->size() >= 1
        }
    }

    requirement def CorrectConnection {
        doc /* System must guarantee correct connection of hot and cold water pipelines */
        subject waterSystem : WaterSupplyNetwork;

        require constraint {
            waterSystem.hotSpigot.isHot == true and
            waterSystem.coldSpigot.isHot == false
        }
    }
}

package PowertrainOptimizationSystem {
    import ScalarValues::*;
    import Quantities::*;
    import MeasurementReferences::*;
    import ISQ::*;

    // Define attributes for performance indicators
    attribute def PowerAttribute :> ScalarQuantityValue {
        attribute quantityDimension = ISQ::power;
    }

    attribute def MassAttribute :> ScalarQuantityValue {
        attribute quantityDimension = ISQ::mass;
    }

    attribute def EfficiencyAttribute :> Real;

    attribute def ReliabilityAttribute :> Real {
        assert constraint { 0.0 <= self <= 1.0 }
    }

    attribute def CostAttribute :> ScalarQuantityValue {
        attribute quantityDimension = ISQ::currency;
    }

    // Component definitions
    part def Cylinder {
        attribute bore :> LengthValue;
        attribute stroke :> LengthValue;
        attribute compressionRatio :> Real;
    }

    part def Piston {
        attribute mass :> MassAttribute;
        attribute diameter :> LengthValue;
    }

    part def ConnectingRod {
        attribute length :> LengthValue;
        attribute mass :> MassAttribute;
    }

    part def Crankshaft {
        attribute mass :> MassAttribute;
        attribute mainJournalDiameter :> LengthValue;
    }

    // Engine configuration definition
    part def Engine {
        attribute engineType : String;
        attribute numberOfCylinders : Integer;
        attribute power :> PowerAttribute;
        attribute mass :> MassAttribute;
        attribute efficiency :> EfficiencyAttribute;
        attribute reliability :> ReliabilityAttribute;
        attribute cost :> CostAttribute;

        part cylinders : Cylinder[1..*];
        part pistons : Piston[1..*];
        part connectingRods : ConnectingRod[1..*];
        part crankshaft : Crankshaft;

        assert constraint cylinderCount { 
            cylinders.size() == numberOfCylinders 
        }
        assert constraint pistonCount { 
            pistons.size() == numberOfCylinders 
        }
        assert constraint rodCount { 
            connectingRods.size() == numberOfCylinders 
        }
    }

    // Specific engine configurations
    part def FourCylinderEngine :> Engine {
        attribute redefines numberOfCylinders = 4;
        attribute redefines engineType = "I4";
    }

    part def SixCylinderEngine :> Engine {
        attribute redefines numberOfCylinders = 6;
        attribute redefines engineType = "I6";
    }

    // Trade-off analysis
    analysis def MultiAttributeTradeOffAnalysis {
        in candidate : Engine[1..*];
        out optimalEngine : Engine;
        out tradeOffScore : Real[0..*];

        attribute powerWeight : Real = 0.25;
        attribute massWeight : Real = 0.20;
        attribute efficiencyWeight : Real = 0.25;
        attribute reliabilityWeight : Real = 0.15;
        attribute costWeight : Real = 0.15;

        assert constraint weightsSum {
            powerWeight + massWeight + efficiencyWeight + 
            reliabilityWeight + costWeight == 1.0
        }

        calc def calculateScore {
            in engine : Engine;
            return score : Real;
        }
    }

    // Evaluation model
    calc def PerformanceEvaluation {
        in engine : Engine;
        out performanceScore : Real;
        out performanceBreakdown : Real[5];
    }

    // Decision support
    requirement def OptimalEngineSelection {
        doc /* The system shall automatically recommend the optimal engine 
             solution based on multi-attribute trade-off analysis */

        subject system : PowertrainConfigurationSystem;

        require constraint {
            system.selectedEngine == system.tradeOffAnalysis.optimalEngine
        }
    }

    requirement def DecisionTransparency {
        doc /* The system shall provide explanations on the rationality 
             of engine selection */

        subject system : PowertrainConfigurationSystem;

        require constraint {
            system.selectionExplanation != null
        }
    }

    // System definition
    part def PowertrainConfigurationSystem {
        part engineLibrary : Engine[1..*];
        part tradeOffAnalysis : MultiAttributeTradeOffAnalysis;
        attribute selectedEngine : Engine;
        attribute selectionExplanation : String;

        perform action assembleEngine {
            in engineType : String;
            in cylinderCount : Integer;
            out assembledEngine : Engine;
        }

        perform action evaluateEngines {
            in engines : Engine[1..*];
            out evaluationResults : Real[0..*];
        }

        perform action selectOptimalEngine {
            in evaluationResults : Real[0..*];
            in engines : Engine[1..*];
            out optimal : Engine;
            out explanation : String;
        }
    }

    // Use case
    use case def ConfigurePowertrain {
        subject vehicle : Vehicle;
        actor engineer : SystemEngineer;

        objective {
            doc /* Select optimal engine configuration based on 
                 performance, mass, efficiency, reliability, and cost */
        }

        include use case defineRequirements;
        include use case modelEngineOptions;
        include use case performTradeOffAnalysis;
        include use case reviewRecommendation;
    }

    // Stakeholders
    part def Vehicle;
    part def SystemEngineer;
}

package FuelEconomySystem {
    import ScalarValues::*;
    import Quantities::*;
    import MeasurementReferences::*;
    import ISQ::*;
    import USCustomaryUnits::*;

    attribute def FuelEconomy :> ScalarQuantityValue {
        attribute quantityDimension = length / volume;
        attribute unit = mile / gallon;
    }

    attribute def Load :> ScalarQuantityValue {
        attribute quantityDimension = mass;
        attribute unit = pound;
    }

    enum def DrivingCondition {
        urban;
        highway;
    }

    requirement def FuelEconomyRequirement {
        doc /* The vehicle fuel economy must meet regulatory and performance requirements */

        attribute testLoad : Load = 1000 [pound];
        attribute urbanMinimum : FuelEconomy = 25 [mile/gallon];
        attribute highwayMinimum : FuelEconomy = 30 [mile/gallon];

        requirement urbanFuelEconomy {
            doc /* Under urban driving conditions, range per gallon must not be less than 25 miles */
            assume constraint { testLoad == 1000 [pound] }
            require constraint { measuredUrbanFuelEconomy >= urbanMinimum }
        }

        requirement highwayFuelEconomy {
            doc /* Under highway conditions, range per gallon must not be less than 30 miles */
            assume constraint { testLoad == 1000 [pound] }
            require constraint { measuredHighwayFuelEconomy >= highwayMinimum }
        }

        attribute measuredUrbanFuelEconomy : FuelEconomy;
        attribute measuredHighwayFuelEconomy : FuelEconomy;
    }

    part def Vehicle {
        attribute fuelEconomy : FuelEconomy;
        attribute operatingCondition : DrivingCondition;
        attribute currentLoad : Load;

        part powertrain : Powertrain;
    }

    part def Powertrain {
        attribute efficiency : Real;
    }

    analysis def FuelEconomyAnalysis {
        subject vehicle : Vehicle;

        return urbanResult : FuelEconomy;
        return highwayResult : FuelEconomy;

        objective {
            doc /* Analyze fuel consumption under various conditions */
        }
    }

    verification def FuelEconomyVerification {
        subject vehicleUnderTest : Vehicle;

        objective {
            doc /* Verify fuel economy meets requirements under standard test conditions */
        }

        return verdict : VerdictKind;
    }

    part fuelEconomySystem : Vehicle {
        satisfy requirement fuelEconomyReq : FuelEconomyRequirement;
    }
}

package VehicleLongitudinalDynamicsSimulation {
    import SI::*;
    import ISQ::*;
    import ScalarValues::*;

    part def VehicleDynamicsSystem {
        attribute mass : MassValue;
        attribute initialPosition : LengthValue;
        attribute initialVelocity : SpeedValue;
        attribute timeStep : TimeValue;

        port powerInput : PowerSegmentPort;
        port dynamicsOutput : DynamicsResultPort;

        perform action simulateDynamics {
            in vehicleMass : MassValue = mass;
            in initPos : LengthValue = initialPosition;
            in initVel : SpeedValue = initialVelocity;
            in powerData : PowerSegment[*];
            in dt : TimeValue = timeStep;

            out accelerationSequence : AccelerationValue[*];
            out velocitySequence : SpeedValue[*];
            out positionSequence : LengthValue[*];
        }
    }

    port def PowerSegmentPort {
        in item powerSegments : PowerSegment[*];
    }

    port def DynamicsResultPort {
        out item acceleration : AccelerationValue[*];
        out item velocity : SpeedValue[*];
        out item position : LengthValue[*];
    }

    attribute def PowerSegment {
        attribute power : PowerValue;
        attribute duration : TimeValue;
    }

    requirement def FunctionalRequirements {
        doc /* The system shall calculate vehicle acceleration, velocity, and position 
               based on input power and dynamic equations */

        requirement calculateAcceleration {
            doc /* System shall compute acceleration at each time step using 
                   F = P/v and a = F/m */
        }

        requirement calculateVelocity {
            doc /* System shall integrate acceleration to obtain velocity sequence */
        }

        requirement calculatePosition {
            doc /* System shall integrate velocity to obtain position sequence */
        }

        requirement processSegmentedPower {
            doc /* System shall handle segmented power input data */
        }
    }

    requirement def NonFunctionalRequirements {
        requirement numericalAccuracy {
            doc /* Calculations shall maintain numerical stability and accuracy 
                   with specified time step */
        }

        requirement performanceAnalysisSupport {
            doc /* System shall provide data for vehicle performance analysis */
        }

        requirement controlStrategySupport {
            doc /* System shall support control strategy development */
        }

        requirement behaviorEvaluationSupport {
            doc /* System shall enable driving behavior evaluation */
        }
    }

    analysis def LongitudinalDynamicsAnalysis {
        subject system : VehicleDynamicsSystem;

        objective {
            doc /* Analyze straight-line dynamic characteristics of vehicle motion */
        }

        return accelerationProfile : AccelerationValue[*];
        return velocityProfile : SpeedValue[*]; 
        return displacementProfile : LengthValue[*];
    }

    verification def DynamicsCalculationVerification {
        subject system : VehicleDynamicsSystem;

        objective {
            doc /* Verify correct implementation of dynamic equations */
        }

        verify requirement FunctionalRequirements::calculateAcceleration;
        verify requirement FunctionalRequirements::calculateVelocity;
        verify requirement FunctionalRequirements::calculatePosition;
    }
}

