package VehicleSafetySecuritySystem {
    import ScalarValues::*;

    enum def SafetyFeatureType {
        mandatory;
        optional;
    }

    enum def ComponentCategory {
        safety;
        security;
    }

    attribute def DisplayFormat {
        attribute formatType : String;
    }

    part def Vehicle {
        part seatBelts : SeatBelt[1..*];
        part airbags : Airbag[0..*];
        part bumpers : Bumper[1..*];
        part alarmDevices : AlarmDevice[0..*];
        part keylessEntrySystem : KeylessEntry[0..1];
        part antiLockBrakingSystem : ABS[0..1];

        attribute hasCompleteSafetyConfig : Boolean;
    }

    abstract part def VehicleComponent {
        attribute componentName : String;
        attribute category : ComponentCategory;
    }

    abstract part def SafetyComponent :> VehicleComponent {
        attribute safetyType : SafetyFeatureType;
        attribute category : ComponentCategory default ComponentCategory::safety;
    }

    abstract part def SecurityComponent :> VehicleComponent {
        attribute category : ComponentCategory default ComponentCategory::security;
    }

    part def SeatBelt :> SafetyComponent {
        attribute safetyType : SafetyFeatureType default SafetyFeatureType::mandatory;
    }

    part def Airbag :> SafetyComponent {
        attribute safetyType : SafetyFeatureType default SafetyFeatureType::optional;
    }

    part def Bumper :> SafetyComponent {
        attribute safetyType : SafetyFeatureType default SafetyFeatureType::mandatory;
    }

    part def ABS :> SafetyComponent {
        attribute safetyType : SafetyFeatureType default SafetyFeatureType::optional;
    }

    part def AlarmDevice :> SecurityComponent;

    part def KeylessEntry :> SecurityComponent;

    part def SafetySecurityManagementSystem {
        part managedVehicle : Vehicle;
        part displaySubsystem : DisplaySubsystem;
        part filteringEngine : FilteringEngine;

        interface vehicleDataInterface connect managedVehicle to filteringEngine;
        interface filteredDataInterface connect filteringEngine to displaySubsystem;
    }

    part def FilteringEngine {
        action filterByCategory {
            in vehicle : Vehicle;
            in filterCategory : ComponentCategory;
            out filteredComponents : VehicleComponent[0..*];
        }

        action filterBySafetyType {
            in vehicle : Vehicle;
            in filterType : SafetyFeatureType;
            out filteredComponents : SafetyComponent[0..*];
        }
    }

    part def DisplaySubsystem {
        attribute supportedFormats : DisplayFormat[1..*];

        action displayAsTree {
            in components : VehicleComponent[0..*];
        }

        action displayAsTable {
            in components : VehicleComponent[0..*];
        }

        action showSafetyConfiguration {
            in vehicle : Vehicle;
        }
    }

    requirement def SafetyVisualizationRequirement {
        doc /* System shall display safety and security configurations */
        subject system : SafetySecurityManagementSystem;

        require constraint {
            system.displaySubsystem.supportedFormats->size() >= 2
        }
    }

    requirement def MandatorySafetyRequirement {
        doc /* Vehicle must have mandatory safety features */
        subject vehicle : Vehicle;

        require constraint {
            vehicle.seatBelts->size() >= 1 and
            vehicle.bumpers->size() >= 1
        }
    }

    requirement def ComponentCategorizationRequirement {
        doc /* All components must be categorizable by safety or security */
        subject component : VehicleComponent;

        require constraint {
            component.category == ComponentCategory::safety or
            component.category == ComponentCategory::security
        }
    }
}

