package LayeredSystemArchitecture {

    part def System {
        attribute x : Real;
        attribute y : Real;
        attribute z : Real;

        constraint zDependency {
            z == f(x, y)
        }

        part applicationLayer : ApplicationLayer;
        part serviceLayer : ServiceLayer;
        part dataLayer : DataLayer;

        interface applicationServiceInterface : ApplicationServiceInterface 
            connect applicationLayer.servicePort to serviceLayer.applicationPort;

        interface serviceDataInterface : ServiceDataInterface
            connect serviceLayer.dataPort to dataLayer.servicePort;
    }

    part def ApplicationLayer {
        port servicePort : ApplicationServiceInterface;

        action executeApplicationLogic {
            out item request;
            in item response;

            send request via servicePort;
            accept response via servicePort;
        }
    }

    part def ServiceLayer {
        port applicationPort : ~ApplicationServiceInterface;
        port dataPort : ServiceDataInterface;

        action processServiceRequest {
            in item request;
            out item response;

            accept request via applicationPort;
            send dataRequest via dataPort;
            accept dataResponse via dataPort;
            send response via applicationPort;
        }
    }

    part def DataLayer {
        port servicePort : ~ServiceDataInterface;

        action handleDataOperation {
            in item dataRequest;
            out item dataResponse;

            accept dataRequest via servicePort;
            send dataResponse via servicePort;
        }
    }

    interface def ApplicationServiceInterface {
        end applicationEnd;
        end serviceEnd;
    }

    interface def ServiceDataInterface {
        end serviceEnd;
        end dataEnd;
    }
}

package PowertrainSystem {
    import ScalarValues::*;

    requirement def TorqueGenerationRequirement {
        doc /* This system is designed to realize the torque generation function within the powertrain system */

        requirement torqueCapability {
            doc /* The system should include relevant components capable of generating torque */
        }

        requirement minimumTorque {
            doc /* The torque produced during operation must be greater than zero newton-meters */
            attribute requiredTorque :> ISQ::torque;
            require constraint { requiredTorque > 0 [N*m] }
        }

        requirement allocationMapping {
            doc /* The logic layer's torque generator function should be effectively allocated and mapped to the physical layer */
        }
    }

    part def Powertrain {
        attribute generatedTorque :> ISQ::torque;

        perform action generateTorque {
            out torqueOutput :> ISQ::torque = generatedTorque;
        }

        part engine : Engine;

        satisfy torqueGenerationReq : TorqueGenerationRequirement {
            requirement minimumTorque {
                attribute :>> requiredTorque = generatedTorque;
            }
        }
    }

    part def Engine {
        attribute outputTorque :> ISQ::torque;

        action engineTorqueGeneration {
            out engineTorque :> ISQ::torque = outputTorque;
        }
    }

    action def TorqueGeneratorFunction {
        doc /* Logic layer torque generator function */
        out torque :> ISQ::torque;
    }

    allocation def TorqueGenerationAllocation {
        doc /* Allocation from logical function to physical component */
        end logical : TorqueGeneratorFunction;
        end physical : Engine::engineTorqueGeneration;
    }

    usage powertrainInstance : Powertrain {
        :>> generatedTorque;

        allocation : TorqueGenerationAllocation {
            allocate TorqueGeneratorFunction to engine.engineTorqueGeneration;
        }

        assert constraint torqueValidation {
            generatedTorque > 0 [N*m]
        }
    }
}

