package 'Information Security Classification System' {

    enum def SecurityClassificationLevel {
        enum Unclassified;
        enum Confidential;
        enum Secret;
    }

    attribute def ClassificationAttribute {
        attribute level : SecurityClassificationLevel;
        attribute displayLabel : String;
    }

    part def Component {
        attribute classification : ClassificationAttribute;
        attribute componentId : String;
        attribute componentName : String;
    }

    part def ClassificationSystem {
        part components : Component[*];

        action classifyComponent {
            in component : Component;
            in securityLevel : SecurityClassificationLevel;
            out classifiedComponent : Component;
        }

        action recognizeClassification {
            in component : Component;
            out recognizedLevel : SecurityClassificationLevel;
            out displayAttribute : String;
        }

        action displayClassification {
            in component : Component;
            out classificationDisplay : String;
        }
    }

    requirement def InformationSecurityRequirement {
        doc /* All components must be classified and labeled */

        requirement classificationRequirement {
            doc /* Every component must be assigned a security classification level */
            subject comp : Component;
            require constraint {
                comp.classification.level in SecurityClassificationLevel
            }
        }

        requirement confidentialRecognitionRequirement {
            doc /* System must recognize and display confidential classification */
            subject sys : ClassificationSystem;
            assume constraint {
                sys.components->exists(c | c.classification.level == SecurityClassificationLevel::Confidential)
            }
            require constraint {
                sys.recognizeClassification.recognizedLevel == SecurityClassificationLevel::Confidential 
                implies sys.displayClassification.classificationDisplay == "Confidential"
            }
        }
    }

    verification def ClassificationVerification {
        subject sys : ClassificationSystem;

        verify requirement InformationSecurityRequirement::classificationRequirement {
            return sys.components->forAll(c | c.classification.level != null);
        }

        verify requirement InformationSecurityRequirement::confidentialRecognitionRequirement {
            return sys.components
                ->select(c | c.classification.level == SecurityClassificationLevel::Confidential)
                ->forAll(c | sys.recognizeClassification(c).recognizedLevel == SecurityClassificationLevel::Confidential);
        }
    }
}

