package PhysicalQuantityManagementSystem {
    import ISQ::*;
    import SI::*;

    attribute def PhysicalQuantity {
        attribute value : Real;
        attribute unit : Unit;
        attribute dimension : PhysicalDimension;
    }

    attribute def Unit {
        attribute symbol : String;
        attribute name : String;
        attribute conversionFactor : Real;
        attribute baseUnit : Unit[0..1];
    }

    attribute def PhysicalDimension {
        attribute length : Integer = 0;
        attribute mass : Integer = 0;
        attribute time : Integer = 0;
        attribute electricCurrent : Integer = 0;
        attribute temperature : Integer = 0;
        attribute amountOfSubstance : Integer = 0;
        attribute luminousIntensity : Integer = 0;
    }

    part def QuantityManager {
        attribute quantities : PhysicalQuantity[*];

        action convertUnit(in source : PhysicalQuantity, in targetUnit : Unit) : PhysicalQuantity {
            assert constraint { source.dimension == targetUnit.dimension }
        }

        action validateConsistency() : Boolean;

        action importQuantity(in external : PhysicalQuantity) : Boolean;

        action extendSystem(in newUnit : Unit, in newDimension : PhysicalDimension) : Boolean;
    }

    part def UnitRegistry {
        attribute registeredUnits : Unit[*];
        attribute baseUnits : Unit[7];

        action registerUnit(in unit : Unit) : Boolean;
        action findCompatibleUnits(in dimension : PhysicalDimension) : Unit[*];
    }

    part def DataProcessor {
        action processQuantityData(in data : PhysicalQuantity[*]) : PhysicalQuantity[*];
        action aggregateQuantities(in quantities : PhysicalQuantity[*]) : PhysicalQuantity;
    }

    part def PhysicalQuantitySystem {
        part quantityManager : QuantityManager;
        part unitRegistry : UnitRegistry;
        part dataProcessor : DataProcessor;

        interface def QuantityInterface {
            in item importData : PhysicalQuantity;
            out item exportData : PhysicalQuantity;
        }

        port quantityPort : QuantityInterface;

        connection : Connect(
            quantityManager.quantities,
            dataProcessor.processQuantityData.data
        );
    }

    requirement def SystemRequirements {
        doc /* The system must support ISQ base quantities */
        requirement supportISQQuantities {
            subject system : PhysicalQuantitySystem;
            assert constraint {
                system.unitRegistry.baseUnits->size() == 7
            }
        }

        requirement accurateRepresentation {
            doc /* Ensure accurate representation of physical quantities */
            subject quantity : PhysicalQuantity;
            assert constraint {
                quantity.value != null and
                quantity.unit != null and
                quantity.dimension != null
            }
        }

        requirement consistencyMaintenance {
            doc /* Maintain consistency of information */
            subject manager : QuantityManager;
            assert constraint {
                manager.validateConsistency() == true
            }
        }

        requirement extensibility {
            doc /* Allow import and extension */
            subject system : PhysicalQuantitySystem;
            verify test {
                system.quantityManager.importQuantity != null and
                system.quantityManager.extendSystem != null
            }
        }
    }

    allocation allocate SystemRequirements::* to PhysicalQuantitySystem;
}

