            }

            perform action validateUnit {
                in unitName : ScalarValues::String;
                out isValid : ScalarValues::Boolean;
            }
        }

        part def MeasurementQuantity {
            attribute value : ScalarValues::Real;
            attribute unit : ScalarValues::String;
            attribute dimension : ScalarValues::String;
        }
    }

    part def UnitSystemAdapter {
        part isqInterface : ISQInterface;
        part usCustomaryInterface : USCustomaryInterface;
        part converter : UnitConverter;

        interface def ISQInterface {
            end supplierPort : ~MeasurementPort;
        }

        interface def USCustomaryInterface {
            end consumerPort : MeasurementPort;
        }

        port def MeasurementPort {
            out measurement : MeasurementQuantity;
        }

        connection : Connect connect isqInterface.supplierPort to converter.inputPort;
        connection : Connect connect converter.outputPort to usCustomaryInterface.consumerPort;
    }

    requirement def AccuracyRequirement {
        doc /* Conversion accuracy shall maintain precision within 0.0001% */
        attribute id = "REQ-001";
        attribute text = "System shall maintain conversion accuracy within 0.0001%";
    }

    requirement def StandardizationRequirement {
        doc /* All units must conform to authoritative standards */
        attribute id = "REQ-002";
        attribute text = "All unit definitions shall conform to ISO 80000 and NIST standards";
    }

    requirement def InteroperabilityRequirement {
        doc /* System must support bidirectional conversion */
        attribute id = "REQ-003";
        attribute text = "System shall support bidirectional conversion between ISQ and US Customary Units";
    }

    constraint def ConversionAccuracy {
        attribute actualError : ScalarValues::Real;
        attribute maxAllowedError : ScalarValues::Real = 0.00001;
        assert constraint { actualError <= maxAllowedError }
    }

    verification def VerifyConversionAccuracy {
        subject converter : UnitConverter;
        objective : AccuracyRequirement;

        return verdict : VerdictKind;
    }

    enum def VerdictKind {
        pass;
        fail;
        inconclusive;
    }

    package ApplicationDomains {
        part def EngineeringApplication {
            part unitSystem : UnitSystemAdapter;
            attribute domain = "Engineering";
        }

        part def ScientificResearchApplication {
            part unitSystem : UnitSystemAdapter;
            attribute domain = "Scientific Research";
        }

        part def DailyLifeApplication {
            part unitSystem : UnitSystemAdapter;
            attribute domain = "Daily Life";
        }
    }
}

package HeadlampTiltAdjustmentSystem {
    import ISQ::*;
    import SI::*;
    import ScalarValues::*;

    attribute def TiltAngle :> ScalarQuantityValue {
        attribute quantityDimension = ISQ::PlaneAngleDimension;
        attribute measurementUnit = SI::degree;
    }

    requirement def HeadlampTiltRequirement {
        doc /* The tilt angle of the headlamp beam must be between 50 degrees and 80 degrees */

        attribute tiltAngle : TiltAngle;

        require constraint tiltAngleRange {
            (tiltAngle.magnitude >= 50.0) and (tiltAngle.magnitude <= 80.0)
        }
    }

    part def HeadlampTiltAdjustmentKnob {
        attribute currentTiltAngle : TiltAngle;

        port tiltOutput : ~TiltAngle;

        action adjustTilt {
            in newAngle : TiltAngle;
            out resultAngle : TiltAngle;

            bind currentTiltAngle = newAngle;
            bind resultAngle = currentTiltAngle;
            bind tiltOutput = currentTiltAngle;
        }
    }

    part def HeadlampBeam {
        attribute beamTiltAngle : TiltAngle;

        port tiltInput : TiltAngle;

        bind beamTiltAngle = tiltInput;
    }

    part def HeadlampTiltVerificationSystem {
        part adjustmentKnob : HeadlampTiltAdjustmentKnob;
        part headlampBeam : HeadlampBeam;

        interface : adjustmentKnob.tiltOutput to headlampBeam.tiltInput;

        satisfy requirement headlampTiltReq : HeadlampTiltRequirement {
            bind tiltAngle = headlampBeam.beamTiltAngle;
        }

        action verifyTiltAngle {
            in measuredAngle : TiltAngle;
            out isValid : Boolean;

            assert constraint validationCheck {
                (measuredAngle.magnitude >= 50.0) and (measuredAngle.magnitude <= 80.0)
            }

            bind isValid = validationCheck;
        }
    }
}

