package SpacecraftAscentTrajectorySystem {
    import ScalarValues::*;
    import Quantities::*;
    import MeasurementReferences::*;
    import Time::*;
    import SpaceTime::*;
    import ISQ::*;

    attribute def UTCTimeStamp {
        attribute year : Integer;
        attribute month : Integer;
        attribute day : Integer;
        attribute hour : Integer;
        attribute minute : Integer;
        attribute second : Real;
    }

    attribute def Position3D {
        attribute x : LengthValue;
        attribute y : LengthValue;
        attribute z : LengthValue;
    }

    attribute def Velocity3D {
        attribute vx : SpeedValue;
        attribute vy : SpeedValue;
        attribute vz : SpeedValue;
    }

    attribute def TrajectoryDataPoint {
        attribute timeFromLiftoff : TimeValue;
        attribute position : Position3D;
        attribute velocity : Velocity3D;
    }

    part def SpacecraftTrajectoryRecorder {
        attribute missionLiftoffTime : UTCTimeStamp;
        attribute trajectoryData : TrajectoryDataPoint[1..*] ordered;

        requirement collectTrajectoryData {
            doc /* The system shall sample and record spacecraft trajectory during ascent phase */
        }

        requirement useReferenceTime {
            doc /* The system shall use mission liftoff time as reference time zero */
            assume constraint { missionLiftoffTime.year == 2020 }
            assume constraint { missionLiftoffTime.month == 8 }
            assume constraint { missionLiftoffTime.day == 23 }
            assume constraint { missionLiftoffTime.hour == 22 }
            assume constraint { missionLiftoffTime.minute == 42 }
            assume constraint { missionLiftoffTime.second == 32.924534 }
        }

        requirement recordKeyParameters {
            doc /* The system shall collect position and velocity at different time points */
            require constraint { trajectoryData->forAll(dp | dp.position.x.unit == m) }
            require constraint { trajectoryData->forAll(dp | dp.position.y.unit == m) }
            require constraint { trajectoryData->forAll(dp | dp.position.z.unit == m) }
            require constraint { trajectoryData->forAll(dp | dp.velocity.vx.unit == m/s) }
            require constraint { trajectoryData->forAll(dp | dp.velocity.vy.unit == m/s) }
            require constraint { trajectoryData->forAll(dp | dp.velocity.vz.unit == m/s) }
        }

        requirement associateTimeWithData {
            doc /* Each sampled data set must associate time point with position and velocity */
            require constraint { trajectoryData->size() >= 1 }
            require constraint { trajectoryData->forAll(dp | dp.timeFromLiftoff.unit == s) }
        }

        action sampleTrajectory {
            in currentTime : TimeValue;
            in currentPosition : Position3D;
            in currentVelocity : Velocity3D;
            out dataPoint : TrajectoryDataPoint;

            bind dataPoint.timeFromLiftoff = currentTime;
            bind dataPoint.position = currentPosition;
            bind dataPoint.velocity = currentVelocity;
        }
    }
}

package AlloyManagementSystem {
    import ISQ::*;
    import SI::*;
    import ScalarValues::*;

    // Define custom unit for tensile strength
    attribute def TensileStrengthUnit :> DerivedUnit {
        attribute lengthPowerFactor :> UnitPowerFactor {
            attribute powerFactor :> Real = -2;
            attribute unit :> SIDerivedUnit = m;
        }
        attribute forcePowerFactor :> UnitPowerFactor {
            attribute powerFactor :> Real = 1;
            attribute unit :> SIDerivedUnit = N;
        }
    }

    // Define tensile strength quantity
    attribute def TensileStrengthValue :> ScalarQuantityValue {
        attribute num :> Real;
        attribute mRef :> MeasurementReference {
            attribute unit = N/(mm**2);
        }
    }

    // Base substance definition
    abstract item def Substance {
        attribute name : String;
        attribute atomicMass : MassValue;
    }

    // Material definition extending substance
    abstract item def Material :> Substance {
        attribute tensileStrength : TensileStrengthValue;
    }

    // Metal definition
    item def Metal :> Material;

    // Alloy component with mass fraction
    part def AlloyComponent {
        ref material : Material;
        attribute massFraction : Real {
            assert constraint { 0.0 <= massFraction and massFraction <= 1.0 }
        }
    }

    // Alloy definition
    item def Alloy :> Material {
        part components : AlloyComponent[2..*];

        assert constraint sumToOne {
            components.massFraction->sum() == 1.0
        }
    }

    // Define specific metals
    item iron : Metal {
        :>> name = "Iron";
        :>> atomicMass = 55.845 [g/mol];
        :>> tensileStrength = 200 [N/(mm**2)];
    }

    item carbon : Metal {
        :>> name = "Carbon";
        :>> atomicMass = 12.011 [g/mol];
        :>> tensileStrength = 130 [N/(mm**2)];
    }

    item manganese : Metal {
        :>> name = "Manganese";
        :>> atomicMass = 54.938 [g/mol];
        :>> tensileStrength = 650 [N/(mm**2)];
    }

    // Define steel alloy instance
    item steel980 : Alloy {
        :>> name = "Steel 980";
        :>> tensileStrength = 980 [N/(mm**2)];

        part :>> components {
            part ironComponent : AlloyComponent {
                :>> material = iron;
                :>> massFraction = 0.982;
            }
            part carbonComponent : AlloyComponent {
                :>> material = carbon;
                :>> massFraction = 0.008;
            }
            part manganeseComponent : AlloyComponent {
                :>> material = manganese;
                :>> massFraction = 0.010;
            }
        }
    }
}

