package PubSubSystem {
    import ScalarValues::*;

    part def Message {
        attribute content : String;
        attribute timestamp : String;
        attribute messageId : String;
    }

    part def Topic {
        attribute name : String;
        attribute description : String;
    }

    part def Publisher {
        attribute publisherId : String;
        attribute name : String;
    }

    part def Subscriber {
        attribute subscriberId : String;
        attribute name : String;
    }

    part def MessageBroker {
        attribute brokerId : String;
        port publishPort : PublishInterface;
        port subscribePort : SubscribeInterface;
        port deliveryPort : DeliveryInterface;

        private attribute topicRegistry : Topic[0..*];
        private attribute subscriptionRegistry : Subscription[0..*];
    }

    part def Subscription {
        ref subscriber : Subscriber;
        ref topic : Topic;
        attribute subscriptionTime : String;
    }

    interface def PublishInterface {
        in item publishedMessage : Message;
        in item targetTopic : Topic;
        out item acknowledgment : Boolean;
    }

    interface def SubscribeInterface {
        in item subscriptionRequest : SubscriptionRequest;
        out item subscriptionConfirmation : Boolean;
    }

    interface def DeliveryInterface {
        out item deliveredMessage : Message;
        out item deliveryTopic : Topic;
        in item deliveryAcknowledgment : Boolean;
    }

    item def SubscriptionRequest {
        attribute subscriberId : String;
        attribute topicName : String;
    }

    action def PublishMessage {
        in publisher : Publisher;
        in message : Message;
        in topic : Topic;
        out success : Boolean;

        bind publishAction.publishPort.publishedMessage = message;
        bind publishAction.publishPort.targetTopic = topic;

        action publishAction : SendMessage {
            in publishPort : PublishInterface;
        }

        then action receiveAck {
            out success : Boolean;
        }
    }

    action def SubscribeToTopic {
        in subscriber : Subscriber;
        in topic : Topic;
        out success : Boolean;

        action createRequest {
            out request : SubscriptionRequest;
        }

        then action sendSubscription {
            in subscribePort : SubscribeInterface;
        }

        then action receiveConfirmation {
            out success : Boolean;
        }
    }

    action def DistributeMessage {
        in message : Message;
        in topic : Topic;
        in subscriptions : Subscription[0..*];

        action identifySubscribers {
            in topic : Topic;
            in subscriptions : Subscription[0..*];
            out targetSubscribers : Subscriber[0..*];
        }

        then action deliverToSubscribers {
            in message : Message;
            in subscribers : Subscriber[0..*];

            for subscriber in subscribers {
                perform deliverMessage {
                    in deliveryPort : DeliveryInterface;
                    in message : Message;
                    in topic : Topic;
                }
            }
        }
    }

    part pubSubSystem : PubSubSystem {
        part broker : MessageBroker;
        part publishers : Publisher[0..*];
        part subscribers : Subscriber[0..*];

        connection publishConnection : PublisherToBroker 
            connect publishers.publishPort to broker.publishPort;

        connection subscribeConnection : SubscriberToBroker
            connect subscribers.subscribePort to broker.subscribePort;

        connection deliveryConnection : BrokerToSubscriber
            connect broker.deliveryPort to subscribers.deliveryPort;
    }

    requirement def MessageDeliveryRequirement {
        doc /* When a producer publishes a new message, the server must promptly 
        receive and distribute it to corresponding subscribers */

        subject broker : MessageBroker;

        require constraint {
            when (broker.publishPort.publishedMessage != null) {
                broker.deliveryPort.deliveredMessage == broker.publishPort.publishedMessage
            }
        }
    }

    requirement def DecouplingRequirement {
        doc /* Users only need to declare topics to subscribe without knowing 
        specific publishers or other subscribers */

        subject system : PubSubSystem;

        require constraint {
            subscribers.subscribePort.subscriptionRequest.topicName != null and
            subscribers.subscribePort.subscriptionRequest.publisherId == null
        }
    }

    requirement def EfficiencyRequirement {
        doc /* Efficient processing of message subscription, publishing, 
        and distribution flows */

        subject broker : MessageBroker;
        attribute maxProcessingTime : Real;

        require constraint {
            broker.processingTime <= maxProcessingTime
        }
    }
}

package FuelSystem {
    import ScalarValues::*;

    part def FuelTankAssembly {
        attribute fuelLevel : Real;
        attribute fuelTemperature : Real;

        port fuelSupplyPort : FuelFlow;
        port fuelReturnPort : ~FuelFlow;
    }

    part def Engine {
        attribute fuelConsumptionRate : Real;

        port fuelInletPort : ~FuelFlow;
        port fuelReturnPort : FuelFlow;
    }

    part def FuelLine {
        attribute flowRate : Real;
        attribute pressure : Real;
    }

    interface def FuelFlow {
        attribute flowRate : Real;
        attribute temperature : Real;
        attribute pressure : Real;
    }

    part vehicleFuelSystem {
        part fuelTank : FuelTankAssembly;
        part engine : Engine;
        part supplyLine : FuelLine;
        part returnLine : FuelLine;

        interface fuelSupplyInterface : FuelFlow connect fuelTank.fuelSupplyPort to engine.fuelInletPort;
        interface fuelReturnInterface : FuelFlow connect engine.fuelReturnPort to fuelTank.fuelReturnPort;

        action fuelCirculation {
            action supplyFuel {
                in source : FuelTankAssembly = fuelTank;
                out target : Engine = engine;

                bind source.fuelSupplyPort = fuelSupplyInterface;
            }

            action returnUnusedFuel {
                in source : Engine = engine;
                out target : FuelTankAssembly = fuelTank;

                bind source.fuelReturnPort = fuelReturnInterface;
            }

            flow from supplyFuel.target to returnUnusedFuel.source;
            flow from returnUnusedFuel.target to supplyFuel.source;
        }

        requirement continuousFuelSupply {
            doc /* The system shall provide continuous and stable fuel supply during vehicle operation */

            require constraint { fuelTank.fuelLevel > 0 }
        }

        requirement temperatureMonitoring {
            doc /* The system shall monitor and transmit fuel temperature information */

            subject fuelTank : FuelTankAssembly;

            require constraint { fuelTank.fuelTemperature >= -40 and fuelTank.fuelTemperature <= 85 }
        }

        requirement fuelRecycling {
            doc /* The system shall effectively recycle excess fuel */

            verify requirement { returnLine.flowRate >= 0 }
        }
    }
}

package PubSubSystem {
    import ScalarValues::*;

    part def Producer {
        port messageOut : MessagePort;

        action publishMessage {
            in topic : String;
            in content : String;
            out message : Message;
        }
    }

    part def Consumer {
        port messageIn : MessagePort;
        port subscriptionOut : SubscriptionPort;

        action subscribe {
            in topic : String;
            out subscription : Subscription;
        }

        action receiveMessage {
            in message : Message;
        }
    }

    part def Server {
        port messageIn : MessagePort;
        port messageOut : MessagePort[0..*];
        port subscriptionIn : SubscriptionPort[0..*];

        attribute subscriptions : Subscription[0..*];

        action processSubscription {
            in subscription : Subscription;
        }

        action processMessage {
            in message : Message;
        }

        action distributeMessage {
            in message : Message;
            in subscribers : Consumer[0..*];
        }
    }

    item def Message {
        attribute topic : String;
        attribute content : String;
        attribute timestamp : String;
    }

    item def Subscription {
        attribute topic : String;
        attribute consumerId : String;
    }

    port def MessagePort {
        out item message : Message;
    }

    port def SubscriptionPort {
        out item subscription : Subscription;
    }

    part pubSubSystem : PubSubSystem {
        part producers : Producer[1..*];
        part server : Server;
        part consumers : Consumer[1..*];

        interface producerToServer connect producers.messageOut to server.messageIn;
        interface consumerToServer connect consumers.subscriptionOut to server.subscriptionIn;
        interface serverToConsumer connect server.messageOut to consumers.messageIn;

        action systemOperation {
            action consumerSubscription {
                perform consumers.subscribe;
                then server.processSubscription;
            }

            action messagePublishing {
                perform producers.publishMessage;
                then server.processMessage;
                then server.distributeMessage;
            }
        }
    }
}

package TransportationSystem {
    import ScalarValues::*;

    part def Vehicle {
        attribute maxPassengers : Integer = 4;
        attribute maxFuelCapacity : Real;
        attribute currentFuelLevel : Real;
        attribute fuelWarningThreshold : Real = 0.1;

        port driverEntry : ~PersonEntry;
        port passengerEntry : ~PersonEntry[0..4];
        port fuelIntake : FuelFlow;
    }

    part def Person {
        attribute role : String;
    }

    part def Driver :> Person {
        attribute role : String = "driver";
    }

    part def Passenger :> Person {
        attribute role : String = "passenger";
    }

    part def GasStation {
        port fuelOutput : ~FuelFlow;
    }

    part def Environment {
        attribute conditions : String;
    }

    interface def PersonEntry {
        end supplierPort : Person;
        end consumerPort : Vehicle;
    }

    interface def FuelFlow {
        end source : GasStation;
        end target : Vehicle;
        flow of fuel : Real;
    }

    use case def ProvideTransportation {
        subject vehicle : Vehicle;
        actor driver : Driver;
        actor passengers : Passenger[0..4];
        actor gasStation : GasStation;
        actor environment : Environment;

        include enterVehicle : EnterVehicle;
        include operateVehicle : OperateVehicle;
        include refuelVehicle : RefuelVehicle;
        include exitVehicle : ExitVehicle;
    }

    use case def EnterVehicle {
        subject vehicle : Vehicle;
        actor driver : Driver;
        actor passengers : Passenger[0..*];
    }

    use case def OperateVehicle {
        subject vehicle : Vehicle;
        actor driver : Driver;

        constraint {
            doc /* Driver operates vehicle to complete travel tasks */
        }
    }

    use case def RefuelVehicle {
        subject vehicle : Vehicle;
        actor driver : Driver;
        actor gasStation : GasStation;

        precondition fuelLow {
            vehicle.currentFuelLevel < (vehicle.maxFuelCapacity * vehicle.fuelWarningThreshold)
        }

        postcondition fuelReplenished {
            vehicle.currentFuelLevel > (vehicle.maxFuelCapacity * vehicle.fuelWarningThreshold)
        }
    }

    use case def ExitVehicle {
        subject vehicle : Vehicle;
        actor driver : Driver;
        actor passengers : Passenger[0..*];

        postcondition allExited {
            doc /* All individuals exit vehicle safely */
        }
    }

    requirement def TransportationRequirements {
        doc /* System shall provide transportation services for users */

        requirement userCapacity {
            doc /* System shall accommodate driver and up to 4 passengers */
            require constraint {
                vehicle.maxPassengers == 4
            }
        }

        requirement fuelMonitoring {
            doc /* System shall monitor fuel level and enable refueling when below 10% */
            require constraint {
                vehicle.fuelWarningThreshold == 0.1
            }
        }

        requirement environmentalConsideration {
            doc /* System shall take environmental factors into account */
        }

        requirement safeBoarding {
            doc /* System shall ensure smooth boarding and alighting of all occupants */
        }

        requirement efficientTravel {
            doc /* System shall guarantee efficient and safe travel experience */
        }
    }
}

