package FuelSystemManagement {
    import ScalarValues::*;

    attribute def Temperature {
        attribute value : Real;
        attribute unit : String = "C";
    }

    item def Fuel {
        attribute temperature : Temperature;
    }

    flow def FuelFlow {
        ref item fuel : Fuel;
    }

    port def FuelPort {
        out flow fuelSupply : FuelFlow;
        in flow fuelReturn : FuelFlow;
    }

    port def EnginePort {
        in flow fuelIn : FuelFlow;
        out flow fuelOut : FuelFlow;
    }

    part def FuelTankAssembly {
        port fuelInterface : FuelPort;

        attribute currentFuelTemperature : Temperature;

        action supplyFuel {
            out item suppliedFuel : Fuel;
            bind suppliedFuel.temperature = currentFuelTemperature;
            flow suppliedFuel to fuelInterface.fuelSupply.fuel;
        }

        action receiveFuel {
            in item returnedFuel : Fuel;
            flow returnedFuel from fuelInterface.fuelReturn.fuel;
        }
    }

    part def Engine {
        port fuelInterface : EnginePort;

        action consumeFuel {
            in item incomingFuel : Fuel;
            out item unusedFuel : Fuel;
            flow incomingFuel from fuelInterface.fuelIn.fuel;
            flow unusedFuel to fuelInterface.fuelOut.fuel;
        }
    }

    part def VehicleFuelSystem {
        part fuelTank : FuelTankAssembly;
        part engine : Engine;

        interface fuelConnection connect fuelTank.fuelInterface to engine.fuelInterface {
            flow fuelTank.fuelInterface.fuelSupply to engine.fuelInterface.fuelIn;
            flow engine.fuelInterface.fuelOut to fuelTank.fuelInterface.fuelReturn;
        }

        requirement efficientFuelSupply {
            doc /* The system shall provide efficient fuel supply from tank to engine */
