package 'Photography System' {
    import ScalarValues::*;

    part def Camera {
        attribute focusClarity : Boolean;
        attribute imageQuality : String;
    }

    part def Scene {
        attribute isSelected : Boolean;
    }

    part def Image {
        attribute sharpness : Real;
        attribute quality : String;
    }

    use case def 'Capture Photograph' {
        subject camera : Camera;
        objective {
            doc /* Provide convenient one-click shooting experience 
                   while ensuring photo clarity and quality */
        }

        include use case selectScene : 'Select Scene';
        include use case focusScene : 'Focus Scene';
        include use case checkFocus : 'Check Focus Clarity';
        include use case takePhoto : 'Take Photo';
        include use case generateImage : 'Generate Final Image';

        assert constraint {
            doc /* Focus must be clear before taking photo */
        }
    }

    action def PhotographyWorkflow {
        in scene : Scene;
        out finalImage : Image;

        action selectScene {
            in inputScene : Scene;
            out selectedScene : Scene {
                :>> isSelected = true;
            }
        }

        action focusOnScene {
            in selectedScene : Scene;
            out focusResult : Camera {
                :>> focusClarity;
            }
        }

        action checkFocusClarity {
            in focus : Camera;
            out isClear : Boolean = focus.focusClarity;
        }

        action capturePhoto {
            in focusedScene : Scene;
            out capturedImage : Image;
        }

        action generateFinalImage {
            in capturedImage : Image;
            out finalImage : Image {
                :>> quality = "high";
            }
        }

        flow from selectScene.selectedScene to focusOnScene.selectedScene;
        flow from focusOnScene.focusResult to checkFocusClarity.focus;

        succession selectScene then focusOnScene;
        succession focusOnScene then checkFocusClarity;

        if checkFocusClarity.isClear {
            succession checkFocusClarity then capturePhoto;
            succession capturePhoto then generateFinalImage;
            flow from selectScene.selectedScene to capturePhoto.focusedScene;
            flow from capturePhoto.capturedImage to generateFinalImage.capturedImage;
            flow from generateFinalImage.finalImage to finalImage;
        }
    }

    requirement def ImageQualityRequirement {
        doc /* System shall ensure optimal image sharpness and quality */
        subject camera : Camera;

        require constraint {
            camera.focusClarity == true before camera.imageQuality == "captured";
        }
    }

    requirement def UserExperienceRequirement {
        doc /* System shall provide convenient one-click shooting experience */
        subject workflow : PhotographyWorkflow;

        attribute executionMode : String = "automatic";
        attribute userInteraction : String = "one-click";
    }
}

