package BatteryChargingControlSystem {
    import ScalarValues::*;

    part def Battery {
        attribute currentLevel : Real;
        attribute maxCapacity : Real = 100.0;

        port powerInput : PowerPort;
        port statusOutput : StatusPort;
    }

    part def ChargingController {
        attribute isCharging : Boolean = false;
        attribute targetLevel : Real = 100.0;

        port powerControl : PowerPort;
        port batteryStatus : StatusPort;
        port chargerControl : ChargerPort;
    }

    part def Charger {
        attribute isActive : Boolean = false;
        attribute chargingRate : Real;

        port powerSupply : PowerPort;
        port controlInput : ChargerPort;
    }

    port def PowerPort {
        attribute voltage : Real;
        attribute current : Real;
    }

    port def StatusPort {
        attribute batteryLevel : Real;
        attribute chargingStatus : Boolean;
    }

    port def ChargerPort {
        attribute enableCharging : Boolean;
    }

    part batteryChargingSystem : BatteryChargingControlSystem {
        part battery : Battery;
        part controller : ChargingController;
        part charger : Charger;

        interface : PowerInterface connect charger.powerSupply to battery.powerInput;
        interface : StatusInterface connect battery.statusOutput to controller.batteryStatus;
        interface : ChargerInterface connect controller.chargerControl to charger.controlInput;

        requirement automaticCharging {
            doc /* System shall automatically supply charge when battery level is below 100% */
            verify batteryLevelMonitoring;
        }

        requirement chargingTermination {
            doc /* System shall automatically terminate charging when battery level reaches or exceeds 100% */
            verify chargingControl;
        }

        requirement noManualIntervention {
            doc /* System shall operate without manual intervention */
        }

        action batteryLevelMonitoring {
            in batteryStatus : StatusPort;
            out chargingDecision : Boolean;

            if (batteryStatus.batteryLevel < 100.0) {
                chargingDecision = true;
            } else {
                chargingDecision = false;
            }
        }

        action chargingControl {
            in shouldCharge : Boolean;
            out chargerCommand : ChargerPort;

            chargerCommand.enableCharging = shouldCharge;
        }

        state batteryState {
            entry state idle;
            state charging;
            state full;

            transition idle_to_charging
                first idle
                accept batteryLevel : Real when batteryLevel < 100.0
                then charging;

            transition charging_to_full
                first charging
                accept batteryLevel : Real when batteryLevel >= 100.0
                then full;

            transition full_to_charging
                first full
                accept batteryLevel : Real when batteryLevel < 100.0
                then charging;
        }
    }

    interface def PowerInterface {
        end supplier : PowerPort;
        end consumer : PowerPort;
    }

    interface def StatusInterface {
        end source : StatusPort;
        end receiver : StatusPort;
    }

    interface def ChargerInterface {
        end controller : ChargerPort;
        end charger : ChargerPort;
    }
}

package BatteryChargingSystem {

    part def BatteryChargingController {
        attribute batteryLevel : Real;
        attribute isCharging : Boolean = false;

        action monitorBatteryLevel {
            out currentLevel : Real;
        }

        action startCharging {
            doc /* Initiates the charging process */
        }

        action stopCharging {
            doc /* Terminates the charging process */
        }

        action replenishBattery {
            doc /* Performs battery replenishment */
        }
    }

    part def Battery {
        attribute currentLevel : Real;
        attribute maxLevel : Real = 100.0;
    }

    part batteryChargingSystem : BatteryChargingController {
        part battery : Battery;

        requirement continuousMonitoring {
            doc /* The system shall continuously monitor the battery level during charging */
            verify by analysis;
        }

        requirement automaticReplenishment {
            doc /* The system shall automatically replenish the battery when level is below 100% */
            verify by test;
        }

        requirement automaticTermination {
            doc /* The system shall automatically terminate charging when battery level reaches or exceeds 100% */
            verify by test;
        }

        requirement noManualIntervention {
            doc /* The system shall operate fully automated without manual intervention */
            verify by inspection;
        }

        requirement preventOvercharging {
            doc /* The system shall prevent battery overcharging */
            verify by test;
        }

        state chargingStateMachine {
            entry state idle;
            state monitoring;
            state charging;
            state terminated;

            transition idle_to_monitoring
                first idle
                then monitoring
                guard isCharging;

            transition monitoring_to_charging
                first monitoring
                then charging
                guard batteryLevel < 100.0;

            transition charging_to_monitoring
                first charging
                then monitoring;

            transition monitoring_to_terminated
                first monitoring
                then terminated
                guard batteryLevel >= 100.0;
        }

        action chargingProcess {
            first start;
            then loop {
                action : monitorBatteryLevel {
                    out batteryLevel;
                }

                if batteryLevel < 100.0 {
                    then action : replenishBattery;
                } else {
                    then done;
                }
            }
            then action : stopCharging;
        }
    }
}

