package CameraSystem {
    import ScalarValues::*;

    item def Scene {
        attribute id : String;
        attribute description : String;
    }

    item def Image {
        attribute clarity : Real;
        attribute quality : String;
    }

    item def Photo {
        attribute id : String;
        attribute timestamp : String;
        attribute image : Image;
    }

    part def AutofocusSubsystem {
        in item selectedScene : Scene;
        out item focusedImage : Image;

        action focus {
            in scene : Scene;
            out image : Image;
        }
    }

    part def ImagingSubsystem {
        in item focusedImage : Image;
        out item capturedPhoto : Photo;

        action capture {
            in image : Image;
            out photo : Photo;
        }
    }

    part def StorageSubsystem {
        in item photo : Photo;

        action save {
            in photoToSave : Photo;
        }
    }

    part def Camera {
        part autofocus : AutofocusSubsystem;
        part imaging : ImagingSubsystem;
        part storage : StorageSubsystem;

        port userInterface {
            in item sceneSelection : Scene;
            out item generatedPhoto : Photo;
        }

        action takePhoto {
            in scene : Scene;
            out photo : Photo;

            bind scene = autofocus.selectedScene;

            action step focusOnScene : autofocus.focus {
                in scene = scene;
                out image;
            }
            then action step captureImage : imaging.capture {
                in image = focusOnScene.image;
                out photo;
            }
            then action step savePhoto : storage.save {
                in photoToSave = captureImage.photo;
            }

            bind photo = captureImage.photo;
        }
    }

    requirement def ImageQualityRequirement {
        doc /* The system shall produce clear and realistic images */
        attribute clarity : Real;
        require constraint { clarity >= 0.95 }
    }

    requirement def AutomationRequirement {
        doc /* The system shall automatically complete focusing and imaging operations */
        subject camera : Camera;
        require constraint { camera.autofocus.focus.isAutomatic == true }
    }

    requirement def UsabilityRequirement {
        doc /* Users shall not need to concern themselves with internal technical details */
        subject camera : Camera;
    }

    requirement def PhotoStorageRequirement {
        doc /* All photos taken shall be recorded and saved by the system */
        subject storage : StorageSubsystem;
    }

    usage cameraSystemUsage : Camera {
        satisfy requirement imageQuality : ImageQualityRequirement;
        satisfy requirement automation : AutomationRequirement;
        satisfy requirement usability : UsabilityRequirement;
        satisfy requirement photoStorage : PhotoStorageRequirement;
