package DigitalCameraSystem {
    import ScalarValues::*;
    import ISQ::*;
    import SI::*;

    requirement def CameraSystemRequirements {
        doc /* The digital camera system shall enable automated shooting process */

        requirement singleOperationReq {
            doc /* Users shall complete entire shooting procedure with single operation */
        }

        requirement autoFocusReq {
            doc /* Camera shall automatically focus on selected scene */
        }

        requirement imageQualityReq {
            doc /* System shall output high-quality photos */
        }

        requirement efficiencyReq {
            doc /* System shall ensure efficient and convenient shooting experience */
        }
    }

    part def Scene {
        attribute sceneType : String;
        attribute lightingConditions : Real;
        attribute distance : Real;
    }

    part def FocusModule {
        attribute focalLength : Real;
        attribute aperture : Real;

        action autoFocus(in targetScene : Scene) : Boolean;
    }

    part def ImagingModule {
        attribute sensorResolution : Real;
        attribute imageFormat : String;

        action captureImage() : Image;
        action processImage(in rawImage : Image) : Image;
    }

    item def Image {
        attribute resolution : Real;
        attribute quality : String;
        attribute format : String;
    }

    part def Camera {
        part focusModule : FocusModule;
        part imagingModule : ImagingModule;

        port userInterface : UserInterface;
        port sceneInput : SceneInterface;

        action performShooting(in selectedScene : Scene) : Image {
            first start;
            then action focus : Boolean = focusModule.autoFocus(selectedScene);
            then action capture : Image = imagingModule.captureImage();
            then action process : Image = imagingModule.processImage(capture);
            then done;
        }
    }

    interface def UserInterface {
        end userEnd;
        end cameraEnd;

        flow userEnd.selectedScene : Scene to cameraEnd.selectedScene : Scene;
        flow cameraEnd.finalPhoto : Image to userEnd.finalPhoto : Image;
    }

    interface def SceneInterface {
        end sceneEnd;
        end cameraEnd;

        flow sceneEnd.sceneData : Scene to cameraEnd.sceneData : Scene;
    }

    part def User {
        action selectScene() : Scene;
        action receivePhoto(in photo : Image);
    }

    part digitalCameraSystem : Camera {
        satisfy requirement CameraSystemRequirements;

        state shootingStates {
            entry state idle;
            state focusing;
            state imaging;
            state processing;
            state complete;

            transition idle_to_focusing
                first idle
                accept selectedScene : Scene
                then focusing;

            transition focusing_to_imaging
                first focusing
                guard focusModule.autoFocus
                then imaging;

            transition imaging_to_processing
                first imaging
                then processing;

            transition processing_to_complete
                first processing
                then complete;

            transition complete_to_idle
                first complete
                then idle;
        }
    }

    usage cameraOperation : Camera {
        part user : User;
        part scene : Scene;

        connect user.userInterface to cameraOperation.userInterface;
        connect scene.sceneInterface to cameraOperation.sceneInput;

        action shootingSequence {
            first action userSelectsScene : Scene = user.selectScene();
            then action cameraShoot : Image = cameraOperation.performShooting(userSelectsScene);
            then action userReceives = user.receivePhoto(cameraShoot);
        }
    }
}

