package SensorManagementSystem {
    import ScalarValues::*;

    part def Sensor {
        attribute id : String;
        attribute status : SensorStatus;
        attribute lastUpdateTime : String;
    }

    enum def SensorStatus {
        enum ready;
        enum busy;
        enum offline;
        enum updating;
    }

    part def SensorManager {
        part sensors : Sensor[0..*];

        action monitorSensors {
            in sensors : Sensor[0..*];
            out sensorsToUpdate : Sensor[0..*];

            action traverseSensors {
                for sensor in sensors {
                    if (sensor.status == SensorStatus::ready) {
                        then sensorsToUpdate = sensorsToUpdate + sensor;
                    }
                }
            }
        }

        action updateSensor {
            in sensor : Sensor;
            out updatedSensor : Sensor;

            first sensor.status := SensorStatus::updating;
            then performUpdate;
            then sensor.status := SensorStatus::ready;
            then sensor.lastUpdateTime := currentTime;
            then updatedSensor := sensor;
        }

        action performUpdate;

        action manageSensors {
            loop {
                perform monitorSensors(sensors, sensorsToUpdate);
                for sensor in sensorsToUpdate {
                    perform updateSensor(sensor, updatedSensor);
                }
            }
        }
    }

    requirement def MultiSensorSupport {
        doc /* The system shall support managing multiple sensors */
        subject sensorManager : SensorManager;
        require constraint { sensorManager.sensors.size() >= 0 }
    }

    requirement def AutomaticMonitoring {
        doc /* The system shall automatically monitor sensor status */
        subject sensorManager : SensorManager;
    }

    requirement def ReadyStateDetection {
        doc /* The system shall detect sensors in ready state */
        subject sensorManager : SensorManager;
    }

    requirement def AutomaticUpdate {
        doc /* The system shall automatically update ready sensors */
        subject sensorManager : SensorManager;
    }

    requirement def TraversalCapability {
        doc /* The system shall traverse all registered sensors */
        subject sensorManager : SensorManager;
    }

    requirement def TimelyProcessing {
        doc /* The system shall promptly process sensors meeting ready condition */
        subject sensorManager : SensorManager;
    }

    requirement def SystemResponsiveness {
        doc /* The system shall enhance overall responsiveness */
        subject sensorManager : SensorManager;
    }

    requirement def SystemReliability {
        doc /* The system shall enhance overall reliability */
        subject sensorManager : SensorManager;
    }
}

