package VehiclePowerManagementSystem {
    import ScalarValues::*;

    part def VehiclePowerController {
        port userCommands : UserCommandPort;
        port vehicleSignals : VehicleSignalPort;
        port powerOutput : PowerOutputPort;
        port safetyControls : SafetyControlPort;

        attribute powerState : PowerStateType;

        state powerStateMachine {
            entry state off;
            state startPhase;
            state poweredOn {
                entry action {
                    perform selfDiagnosis();
                    send powerOutput.supplyPower();
                }
                do action {
                    send powerOutput.continuousPowerSupply();
                }
            }

            transition off_to_startPhase
                first off
                accept userCommands.startCommand
                then startPhase;

            transition startPhase_to_poweredOn
                first startPhase
                accept vehicleSignals.vehicleStartedSignal
                then poweredOn;

            transition poweredOn_to_off
                first poweredOn
                accept userCommands.shutdownCommand
                do action {
                    perform safetyOperations();
                    send safetyControls.engageParkingBrake();
                    send powerOutput.cutOffPower();
                }
                then off;
        }

        action selfDiagnosis();
        action safetyOperations();
    }

    port def UserCommandPort {
        in startCommand : StartCommand;
        in shutdownCommand : ShutdownCommand;
    }

    port def VehicleSignalPort {
        in vehicleStartedSignal : VehicleStartedSignal;
    }

    port def PowerOutputPort {
        out supplyPower : PowerSupplySignal;
        out continuousPowerSupply : PowerSupplySignal;
        out cutOffPower : PowerCutOffSignal;
    }

    port def SafetyControlPort {
        out engageParkingBrake : ParkingBrakeSignal;
    }

    enum def PowerStateType {
        enum off;
        enum startPhase;
        enum poweredOn;
    }

    item def StartCommand;
    item def ShutdownCommand;
    item def VehicleStartedSignal;
    item def PowerSupplySignal;
    item def PowerCutOffSignal;
    item def ParkingBrakeSignal;

