package VehicleHealthMonitoringSystem {
    import ScalarValues::*;
    import ISQ::*;
    import SI::*;
    import Time::*;

    part def VehicleHealthMonitor {
        attribute currentTemperature : TemperatureValue;
        attribute maxAllowableTemperature : TemperatureValue;
        attribute maintenanceInterval : DurationValue;
        attribute lastMaintenanceTime : TimeInstantValue;
        attribute nextMaintenanceTime : TimeInstantValue;

        port temperatureSensor : ~TemperatureSensorInterface;
        port controllerInterface : VehicleControllerInterface;

        state healthStatus {
            entry state normal;
            state degraded;
            state maintenance;

            transition normal_to_degraded
                first normal
                then degraded
                guard currentTemperature > maxAllowableTemperature
                do send controllerInterface.overTemperatureWarning;

            transition degraded_to_normal
                first degraded
                then normal
                guard currentTemperature <= maxAllowableTemperature;

            transition normal_to_maintenance
                first normal
                then maintenance
                guard currentTime >= nextMaintenanceTime;

            transition maintenance_to_normal
                first maintenance
                then normal
                after 48 [h]
                do action {
                    lastMaintenanceTime := currentTime;
                    nextMaintenanceTime := currentTime + maintenanceInterval;
                }
        }
    }

    interface def TemperatureSensorInterface {
        out temperatureReading : TemperatureValue;
    }

    interface def VehicleControllerInterface {
        in overTemperatureWarning : WarningSignal;
    }

    abstract def WarningSignal;

    attribute def TemperatureValue :> ScalarQuantityValue {
        attribute redefines num : Real;
        attribute redefines mRef : TemperatureUnit;
    }

    attribute def DurationValue :> ScalarQuantityValue {
        attribute redefines num : Real;
        attribute redefines mRef : DurationUnit;
    }

    usage vehicleHealthSystem : VehicleHealthMonitor {
        attribute redefines maxAllowableTemperature = 90 [C];
        attribute redefines maintenanceInterval = 30 [day];
    }
}

