        attribute localClock : Time;
        port requestPort {
            in item startCommand;
            in item requestMessage;
        }

        state serverBehavior {
            entry state off;
            state waiting;
            state response;

            transition startup 
                first start then off;

            transition startTransition
                first off
                accept startCommand via requestPort
                then waiting;

            transition requestTransition
                first waiting
                accept requestMessage via requestPort
                then response;

