package VehicleStartStopSystem {

    part def VehicleController;
    part def BrakePedal;
    part def ParkingBrake;

    part def VehicleStartStopControlSystem {
        port startSignalPort : ~SignalPort;
        port vehicleOnSignalPort : ~SignalPort;
        port shutdownSignalPort : ~SignalPort;
        port brakeStatusPort : ~StatusPort;
        port startControlPort : SignalPort;
        port powerSupplyPort : PowerPort;
        port parkingBrakeControlPort : ControlPort;

        attribute systemState : SystemStateType;

        perform action selfCheck;

        state machine VehicleStateMachine {
            entry state off;
            state startUp;
            state started {
                entry action {
                    perform selfCheck;
                    send powerSupplyPort.supplyPower();
                }
                do action {
                    send powerSupplyPort.continuousPower();
                }
            }

            transition off_to_startUp
                first off
                accept startSignalPort.signal
                then startUp;

            transition startUp_to_started
                first startUp
                accept vehicleOnSignalPort.signal
                if brakeStatusPort.isDepressed
                do send startControlPort.startSignal()
                then started;

            transition started_to_off
                first started
                accept shutdownSignalPort.signal
                do {
                    send parkingBrakeControlPort.applyBrake();
                    systemState := SystemStateType::off;
                }
                then off;
        }
    }

    enum def SystemStateType {
        enum off;
        enum startUp;
        enum started;
    }

    port def SignalPort {
        out signal : Boolean;
    }

    port def StatusPort {
        in isDepressed : Boolean;
    }

    port def ControlPort {
        out startSignal : Boolean;
        out applyBrake : Boolean;
    }

    port def PowerPort {
        out supplyPower : Boolean;
        out continuousPower : Boolean;
    }

    part vehicleSystem : VehicleStartStopControlSystem {
        part controller : VehicleController;
        part brake : BrakePedal;
        part parkingBrake : ParkingBrake;

        connect vehicleSystem.startControlPort to controller.controlInput;
        connect brake.status to vehicleSystem.brakeStatusPort;
        connect vehicleSystem.parkingBrakeControlPort to parkingBrake.control;
    }

    requirement def SafetyRequirement {
        doc /* Vehicle can only be started when brake pedal is depressed */
        require constraint { 
            vehicleSystem.systemState == SystemStateType::started 
            implies brake.status.isDepressed == true 
        }
    }

    requirement def ShutdownSafetyRequirement {
        doc /* Parking brake must be applied before shutdown */
        require constraint {
            vehicleSystem.systemState == SystemStateType::off
            implies parkingBrake.isApplied == true
        }
    }
}

