        doc /* Fuel control command value */
    }

    // Message definitions
    item def SetSpeedCommand {
        attribute targetSpeed : Speed;
    }

    item def SpeedReading {
        attribute currentSpeed : Speed;
    }

    item def FuelControlCommand {
        attribute fuelAdjustment : FuelCommand;
    }

    // Part definitions
    part def Driver {
        port setSpeedOut : ~SetSpeedCommand;
    }

    part def CruiseController {
        port setSpeedIn : SetSpeedCommand;
        port speedIn : SpeedReading;
        port fuelCommandOut : ~FuelControlCommand;

        private attribute setSpeed : Speed;
        private attribute actualSpeed : Speed;

        action processSetSpeed {
            in cmd : SetSpeedCommand;
            bind cmd = setSpeedIn;
            assign setSpeed := cmd.targetSpeed;
        }

        action readSpeed {
            in reading : SpeedReading;
            bind reading = speedIn;
            assign actualSpeed := reading.currentSpeed;
        }

        action calculateFuelCommand {
            out cmd : FuelControlCommand;
            bind cmd = fuelCommandOut;
            assign cmd.fuelAdjustment := (setSpeed - actualSpeed) * 0.1;
        }
    }

    part def SpeedSensor {
        port speedOut : ~SpeedReading;

        action measureSpeed {
            out reading : SpeedReading;
            bind reading = speedOut;
        }
    }

    part def EngineController {
        port fuelCommandIn : FuelControlCommand;
        port engineCommandOut : ~FuelControlCommand;

        action processFuelCommand {
            in cmd : FuelControlCommand;
            out engineCmd : FuelControlCommand;
            bind cmd = fuelCommandIn;
            bind engineCmd = engineCommandOut;
            assign engineCmd := cmd;
        }
    }

    part def Engine {
        port fuelCommandIn : FuelControlCommand;

        action adjustFuel {
            in cmd : FuelControlCommand;
            bind cmd = fuelCommandIn;
        }
    }

    // System composition
    part cruiseControlSystem {
        part driver : Driver;
        part controller : CruiseController;
        part sensor : SpeedSensor;
        part engineController : EngineController;
        part engine : Engine;

        // Connections
        interface setSpeedInterface connect driver.setSpeedOut to controller.setSpeedIn;
        interface speedInterface connect sensor.speedOut to controller.speedIn;
        interface fuelCommandInterface connect controller.fuelCommandOut to engineController.fuelCommandIn;
        interface engineInterface connect engineController.engineCommandOut to engine.fuelCommandIn;

        // Requirements
        requirement reliability {
            doc /* The system shall ensure reliable message transmission between components */
        }

        requirement efficiency {
            doc /* The system shall process commands efficiently with minimal latency */
        }

        requirement safety {
            doc /* The system shall operate safely under all conditions */
        }
    }
}

package VehicleLifecycleManagement {
    import ScalarValues::*;

    part def Vehicle {
        attribute vin : String;
        attribute model : String;
        attribute manufactureDate : String;
    }

    part def Person {
        attribute id : String;
        attribute name : String;
    }

    part def Owner :> Person;
    part def Driver :> Person;

    occurrence def VehicleLifecycle {
        attribute deliveryDate : String;
        attribute scrapDate : String[0..1];

        ref vehicle : Vehicle;

        occurrence assembly : AssemblyStage {
            in item vehicle : Vehicle;
            out item assembledVehicle : Vehicle;
        }

        occurrence delivery : DeliveryStage {
            in item assembledVehicle : Vehicle;
            out item deliveredVehicle : Vehicle;
            attribute actualDeliveryDate : String;
        }

        occurrence ownership : OwnershipPeriod[1..*] ordered {
            ref item ownedVehicle : Vehicle;
            ref item owner : Owner[1];
            attribute startDate : String;
            attribute endDate : String[0..1];

            occurrence driving : DrivingOperation[0..*] {
                ref item drivenVehicle : Vehicle;
                ref item driver : Driver[1];
                attribute drivingDate : String;
                attribute startTime : String;
                attribute endTime : String;
            }
        }

        occurrence endOfLife : ScrapStage[0..1] {
            in item vehicleToScrap : Vehicle;
            attribute scrapDate : String;
            attribute reason : String;
        }

        succession assembly then delivery;
        succession delivery.deliveredVehicle to ownership.ownedVehicle;
        succession ownership then endOfLife;
    }

    action def AssemblyStage {
        in item vehicle : Vehicle;
        out item assembledVehicle : Vehicle;
    }

    action def DeliveryStage {
        in item assembledVehicle : Vehicle;
        out item deliveredVehicle : Vehicle;
    }

    action def DrivingOperation {
        in item vehicle : Vehicle;
        in item driver : Driver;
    }

    action def ScrapStage {
        in item vehicleToScrap : Vehicle;
    }

    requirement def TraceabilityRequirement {
        doc /* Each ownership stage must be traceable to a unique owner */
        subject lifecycle : VehicleLifecycle;
        require constraint {
            lifecycle.ownership->forAll(o1, o2 | 
                (o1 != o2) implies (o1.owner != o2.owner or o1.startDate != o2.startDate))
        }
    }

    requirement def LifecycleDataManagement {
        doc /* System shall record and manage all lifecycle data */
        subject lifecycle : VehicleLifecycle;
        require constraint {
            lifecycle.assembly != null and
            lifecycle.delivery != null and
            lifecycle.delivery.actualDeliveryDate != null and
            lifecycle.ownership->size() >= 1
        }
    }
}

