package VehicleMassManagementSystem {
    import ScalarValues::*;
    import Quantities::*;
    import MeasurementReferences::*;
    import SI::*;

    attribute def MassValue :> ScalarQuantityValue {
        attribute quantityDimension :>> num = 1;
        attribute measurementReference :>> mRef = kg;
    }

    abstract part def Component {
        attribute id : String;
        attribute componentMass : MassValue;
        attribute totalMass : MassValue;
    }

    abstract part def CompositeComponent :> Component {
        part subComponents : Component[0..*];

        calc constraint {
            totalMass == componentMass + sum(subComponents.totalMass)
        }
    }

    part def SimpleComponent :> Component {
        calc constraint {
            totalMass == componentMass
        }
    }

    part def Engine :> SimpleComponent {
        attribute engineType : String;
        attribute displacement : Real;
    }

    part def Transmission :> SimpleComponent {
        attribute transmissionType : String;
        attribute gearCount : Integer;
    }

    part def Body :> SimpleComponent {
        attribute bodyType : String;
        attribute material : String;
    }

    part def Vehicle :> CompositeComponent {
        attribute vehicleModel : String;
        attribute vin : String;

        part engine : Engine[1];
        part transmission : Transmission[1];
        part body : Body[1];
        part otherComponents : Component[0..*];

        :>> subComponents = (engine, transmission, body, otherComponents);
    }

    calc def sum(masses : MassValue[0..*]) : MassValue {
        if isEmpty(masses) ? 0[kg]
        else if size(masses) == 1 ? masses#(1)
        else masses#(1) + sum(masses[2..size(masses)])
    }
}

