package VehicleMaintenanceSystem {
    import ScalarValues::*;
    import ISQ::*;
    import SI::*;

    enum def VehicleStatus {
        enum operational;
        enum underMaintenance;
    }

    enum def HealthStatus {
        enum healthy;
        enum needsMaintenance;
    }

    attribute def Temperature :> ScalarQuantityValue {
        attribute quantityDimension = ISQ::temperature;
        attribute unit = SI::K;
    }

    part def Vehicle {
        attribute vehicleID : String;
        attribute nextMaintenanceTime : Time;
        attribute maintenanceInterval : Duration;
        attribute maxAllowedTemperature : Temperature;
        attribute currentTemperature : Temperature;
        attribute status : VehicleStatus = VehicleStatus::operational;
        attribute healthStatus : HealthStatus = HealthStatus::healthy;
        attribute lastHealthyStateEndTime : Time;
        attribute actualMaintenanceStartTime : Time;

        state vehicleStates {
            entry state;
            state operational {
                entry action {
                    status := VehicleStatus::operational;
                    healthStatus := HealthStatus::healthy;
                }
            }
            state underMaintenance {
                entry action {
                    status := VehicleStatus::underMaintenance;
                }
                constraint maintenanceDuration {
                    doc /* Maintenance duration must not exceed 48 hours */
                    (currentTime - actualMaintenanceStartTime) <= 48 [h]
                }
            }

            transition operational_to_maintenance
                first operational
                then underMaintenance
                guard (currentTime >= nextMaintenanceTime);

            transition maintenance_to_operational
                first underMaintenance
                then operational
                effect action {
                    nextMaintenanceTime := actualMaintenanceStartTime + maintenanceInterval;
                    healthStatus := HealthStatus::healthy;
                }
        }
    }

    part def MaintenanceScheduler {
        part vehicles : Vehicle[*];

        action checkMaintenanceSchedule {
            for vehicle in vehicles {
                if (currentTime >= vehicle.nextMaintenanceTime) {
                    send MaintenanceRequired() to vehicle;
                }
            }
        }

        action initiateMaintenanceFor(in vehicle : Vehicle) {
            vehicle.lastHealthyStateEndTime := currentTime;
            vehicle.actualMaintenanceStartTime := currentTime;
            assert constraint {
                doc /* Actual maintenance start time must be later than scheduled */
                vehicle.actualMaintenanceStartTime > vehicle.nextMaintenanceTime
            }
            assert constraint {
                doc /* Maintenance must start within 2 seconds after healthy state ends */
                (vehicle.actualMaintenanceStartTime - vehicle.lastHealthyStateEndTime) <= 2 [s]
            }
        }
    }

    requirement def MaintenanceTimingRequirement {
        doc /* The actual maintenance start time must be later than the scheduled maintenance time */
        subject vehicle : Vehicle;
        require constraint {
            vehicle.actualMaintenanceStartTime > vehicle.nextMaintenanceTime
        }
    }

    requirement def MaintenanceStartDelayRequirement {
        doc /* Maintenance must be initiated within 2 seconds after the previous healthy state ends */
        subject vehicle : Vehicle;
        require constraint {
            (vehicle.actualMaintenanceStartTime - vehicle.lastHealthyStateEndTime) <= 2 [s]
        }
    }

    requirement def MaintenanceDurationRequirement {
        doc /* The duration of the maintenance must not exceed 48 hours */
        subject vehicle : Vehicle;
        require constraint {
            (currentTime - vehicle.actualMaintenanceStartTime) <= 48 [h]
        }
    }

    part vehicleMaintenanceSystem {
        part scheduler : MaintenanceScheduler;
        part fleet : Vehicle[*];

        bind scheduler.vehicles = fleet;
    }
}

package VehicleSystemRequirements {
