             * according to the speed curve */

        attribute engineSpeed : AngularVelocityValue;
        attribute engineTorque : TorqueValue;

        requirement torqueGenerationCapability {
            doc /* Engine shall generate torque as a function of speed */
        }
    }

    requirement def PowerTransmissionRequirement {
        doc /* The torque produced by the engine must be effectively transmitted 
             * to the transmission through the clutch interface */

        attribute engineOutputTorque : TorqueValue;
        attribute transmissionInputTorque : TorqueValue;
        attribute clutchEfficiency : Real;

        require constraint {
            transmissionInputTorque == engineOutputTorque * clutchEfficiency
        }

        requirement smoothPowerDelivery {
            doc /* Ensure that power is smoothly delivered to the vehicle's drive system */
        }
    }

    part def Engine {
        port engineOutput : PowerPort;
        attribute speed : AngularVelocityValue;
        attribute torque : TorqueValue;
    }

    part def Clutch {
        port clutchInput : PowerPort;
        port clutchOutput : PowerPort;
        attribute efficiency : Real;
    }

    part def Transmission {
        port transmissionInput : PowerPort;
        port transmissionOutput : PowerPort;
    }

    part def Vehicle {
        attribute totalMass : MassValue;
        attribute curbWeight : MassValue;
        attribute fuelMass : MassValue;

        part engine : Engine;
        part clutch : Clutch;
        part transmission : Transmission;

        interface : PowerInterface connect engine.engineOutput to clutch.clutchInput;
        interface : PowerInterface connect clutch.clutchOutput to transmission.transmissionInput;

        satisfy vehicleMassReq : VehicleMassRequirement by Vehicle {
            redefines maxMassLimit;
            redefines curbWeight = Vehicle::curbWeight;
            redefines fuelMass = Vehicle::fuelMass;
        }

        satisfy fuelMassReq : FuelMassRequirement by Vehicle {
            redefines fuelMass = Vehicle::fuelMass;
        }

        satisfy engineTorqueReq : EngineTorqueRequirement by engine {
            redefines engineSpeed = engine::speed;
            redefines engineTorque = engine::torque;
        }

        satisfy powerTransReq : PowerTransmissionRequirement by Vehicle {
            redefines engineOutputTorque = engine::torque;
            redefines clutchEfficiency = clutch::efficiency;
        }
    }

    port def PowerPort {
        attribute torque : TorqueValue;
        attribute speed : AngularVelocityValue;
    }

    interface def PowerInterface {
        end source : PowerPort;
        end target : PowerPort;

        flow source.torque to target.torque;
        flow source.speed to target.speed;
    }
}

