package VehicleSystemRequirements {
    import ScalarValues::*;

    requirement def VehicleMassRequirement {
        doc /* The actual mass of the vehicle must not exceed the specified limit under different fuel conditions */

        attribute curbMass : MassValue;
        attribute fuelMass : MassValue;
        attribute totalVehicleMass : MassValue = curbMass + fuelMass;

        requirement fullTankMassLimit {
            doc /* When fuel tank is full, total vehicle mass must not exceed 2000 kg */
            assume constraint { fuelMass == fuelMass@max }
            require constraint { totalVehicleMass <= 2000[kg] }
        }

        requirement emptyTankMassLimit {
            doc /* When fuel tank is empty, total vehicle mass must not exceed 1500 kg */
            assume constraint { fuelMass == 0[kg] }
            require constraint { totalVehicleMass <= 1500[kg] }
        }
    }

    requirement def EngineTorqueRequirement {
        doc /* Engine must generate specified torque characteristics at various speeds */

        attribute engineSpeed : AngularVelocityValue;
        attribute generatedTorque : TorqueValue;

        requirement torqueGeneration {
            doc /* Engine must be capable of generating specified torque at various speeds */
            require constraint { generatedTorque == torqueCharacteristic(engineSpeed) }
        }
    }

    requirement def PowerTransmissionRequirement {
        doc /* Generated torque must be effectively transmitted to gearbox through clutch interface */

        attribute inputTorque : TorqueValue;
        attribute outputTorque : TorqueValue;
        attribute clutchEngaged : Boolean;

        requirement smoothPowerDelivery {
            doc /* Ensure smooth power delivery through clutch to gearbox */
            require constraint { 
                clutchEngaged implies (outputTorque >= 0.95 * inputTorque and outputTorque <= inputTorque)
            }
        }
    }

    requirement vehicleSystemRequirements {
        doc /* Top-level vehicle system requirements for mass control and power transmission */

        subject vehicle;

        require massControl : VehicleMassRequirement;
        require torqueGeneration : EngineTorqueRequirement;
        require powerTransmission : PowerTransmissionRequirement;
    }
}

