        attribute isQualified : Boolean;
    }

    part def Vehicle {
        attribute actualMass : MassValue;
        attribute verificationStatus : VerificationStatus;
    }

    part def WeighingEquipment {
        attribute measuredMass : MassValue;

        action measureMass {
            in vehicle : Vehicle;
            out measurement : MassValue;
        }
    }

    part def MassVerificationUnit {
        attribute massLimit : MassValue = MassValue(actualValue = 2500.0, unit = kg);

        action evaluateMass {
            in measuredMass : MassValue;
            in limit : MassValue;
            out status : VerificationStatus;

            bind status.isQualified = (measuredMass.actualValue <= limit.actualValue);
        }
    }

    part def VehicleMassVerificationSystem {
        part vehicles : Vehicle[*];
        part weighingEquipment : WeighingEquipment;
        part verificationUnit : MassVerificationUnit;

        action verifyVehicleMass {
            in vehicle : Vehicle;

            perform weighingEquipment.measureMass {
                in vehicle = vehicle;
                out measurement;
            }
            then perform verificationUnit.evaluateMass {
                in measuredMass = measurement;
                in limit = verificationUnit.massLimit;
                out status;
            }
            then assign vehicle.actualMass := measurement;
            then assign vehicle.verificationStatus := status;
        }

        requirement massLimitRequirement {
            doc /* All vehicles must not exceed 2500 kg */
            require constraint {
                vehicles->forAll(v | v.actualMass.actualValue <= 2500.0)
            }
        }

        requirement independentVerificationRequirement {
            doc /* Each vehicle must be verified independently */
            require constraint {
                vehicles->forAll(v | v.verificationStatus != null)
            }
        }
    }
}

