    part def Vehicle {
        attribute capacity : Integer = 5;
        attribute fuelLevel : Real;
        attribute location : String;

        port driverInterface : DriverVehicleInterface;
        port passengerInterface : PassengerVehicleInterface[0..4];
        port fuelPort : FuelInterface;
    }

    part def Driver {
        attribute licensed : Boolean;
        port vehicleControl : DriverVehicleInterface;
    }

    part def Passenger {
        port vehicleAccess : PassengerVehicleInterface;
    }

    part def GasStation {
        attribute fuelAvailable : Boolean;
        port fuelDispenser : FuelInterface;
    }

    part def Environment {
        attribute weatherCondition : String;
        attribute roadCondition : String;
    }

    interface def DriverVehicleInterface {
        end driverEnd;
        end vehicleEnd;
    }

    interface def PassengerVehicleInterface {
        end passengerEnd;
        end vehicleEnd;
    }

    interface def FuelInterface {
        end stationEnd;
        end vehicleEnd;
    }

    use case def TransportationScenario {
        subject vehicle : Vehicle;
        actor driver : Driver;
        actor passengers : Passenger[0..4];
        actor gasStation : GasStation;
        actor environment : Environment;

        objective {
            doc /* Transport driver and passengers safely from starting point to destination */
        }

        part enterVehicleAction : EnterVehicle {
            in actors : Person[1..5];
            out occupiedVehicle : Vehicle;
        }

        part operateVehicleAction : OperateVehicle {
            in operator : Driver;
            in vehicle : Vehicle;
            in envConditions : Environment;
            out vehicleInMotion : Vehicle;
        }

        part refuelAction : Refuel {
            in vehicle : Vehicle;
            in station : GasStation;
            out refueledVehicle : Vehicle;
        }

        part exitVehicleAction : ExitVehicle {
            in occupiedVehicle : Vehicle;
            out actors : Person[1..5];
        }

        flow from enterVehicleAction.occupiedVehicle to operateVehicleAction.vehicle;
        flow from operateVehicleAction.vehicleInMotion to refuelAction.vehicle;
        flow from refuelAction.refueledVehicle to exitVehicleAction.occupiedVehicle;
    }

    action def EnterVehicle {
        in actors : Person[1..5];
        out occupiedVehicle : Vehicle;
    }

    action def OperateVehicle {
        in operator : Driver;
        in vehicle : Vehicle;
        in envConditions : Environment;
        out vehicleInMotion : Vehicle;
    }

    action def Refuel {
        in vehicle : Vehicle;
        in station : GasStation;
        out refueledVehicle : Vehicle;
    }

    action def ExitVehicle {
        in occupiedVehicle : Vehicle;
        out actors : Person[1..5];
    }

    abstract part def Person;

    requirement def SafetyRequirement {
        doc /* System shall ensure safe transportation of all occupants */
        subject vehicle : Vehicle;
    }

    requirement def UsabilityRequirement {
        doc /* System interactions shall be simple and convenient */
        subject vehicle : Vehicle;
    }

    requirement def CapacityRequirement {
        doc /* Vehicle shall accommodate 1 driver and up to 4 passengers */
        subject vehicle : Vehicle;
        assume constraint { vehicle.capacity == 5 }
    }
}

