package RiskFailureManagementSystem {
    import ScalarValues::*;
    import ISQ::*;
    import SI::*;

    attribute def Probability :> Real {
        constraint { 0.0 <= self <= 1.0 }
    }

    attribute def SeverityLevel :> Integer {
        constraint { 1 <= self <= 10 }
    }

    enum def RiskCategory {
        enum low;
        enum medium;
        enum high;
        enum critical;
    }

    item def Cause {
        attribute id : String;
        attribute description : Text;
        attribute probability : Probability;
        attribute category : String;
    }

    item def Situation {
        attribute id : String;
        attribute description : Text;
        attribute context : Text;
        attribute timeframe : String;
    }

    item def Failure {
        attribute id : String;
        attribute description : Text;
        attribute severity : SeverityLevel;
        attribute impact : Text;
        attribute domain : String;
    }

    item def Risk {
        attribute id : String;
        attribute name : String;
        attribute riskScore : Real;
        attribute category : RiskCategory;

        calc riskScore {
            causedBy.probability * resultsIn.severity
        }
    }

    connection def CausalRelationship {
        end cause : Cause[1..*];
        end situation : Situation[1..*];
        attribute strength : Real;
        attribute mechanism : Text;
    }

    connection def FailureRelationship {
        end situation : Situation[1..*];
        end failure : Failure[1..*];
        attribute likelihood : Probability;
        attribute propagationPath : Text;
    }

    part def RiskScenario {
        part causes : Cause[*];
        part situations : Situation[*];
        part failures : Failure[*];
        part risks : Risk[*];

        connection causalLinks : CausalRelationship[*]
            connect cause references causes to situation references situations;

        connection failureLinks : FailureRelationship[*]
            connect situation references situations to failure references failures;
    }

    analysis def RiskAnalysis {
        subject scenario : RiskScenario;

        calc totalRiskScore : Real {
            scenario.risks.riskScore->sum()
        }

        calc highRiskCount : Integer {
            scenario.risks->select(r | r.category == RiskCategory::high or r.category == RiskCategory::critical)->size()
        }

        return totalScore = totalRiskScore;
        return criticalRisks = highRiskCount;
    }

    part def RiskManagementSystem {
        part scenarios : RiskScenario[*];

        action identifyRisks {
            in inputData : Text;
            out identifiedRisks : Risk[*];
        }

        action analyzeCausality {
            in causes : Cause[*];
            in situations : Situation[*];
            out relationships : CausalRelationship[*];
        }

        action evaluateFailures {
            in situations : Situation[*];
            out failures : Failure[*];
            out failureLinks : FailureRelationship[*];
        }

        action generateRiskReport {
            in scenario : RiskScenario;
            out report : Text;
            out recommendations : Text[*];
        }

        state riskStates {
            entry state initial;
            state identifying;
            state analyzing;
            state evaluating;
            state reporting;
            state monitoring;

            transition initial then identifying;
            transition identifying then analyzing;
            transition analyzing then evaluating;
            transition evaluating then reporting;
            transition reporting then monitoring;
            transition monitoring then identifying;
        }
    }

    requirement def SystemRequirements {
        doc /* The system shall support comprehensive risk and failure management */

        requirement identificationReq {
            doc /* System shall identify and describe various possible situations */
            subject system : RiskManagementSystem;
        }

        requirement causalityReq {
            doc /* System shall establish and analyze causal relationships */
            subject system : RiskManagementSystem;
        }

        requirement probabilityReq {
            doc /* System shall record probability of each cause */
            subject cause : Cause;
            constraint { cause.probability >= 0.0 and cause.probability <= 1.0 }
        }

        requirement severityReq {
            doc /* System shall record severity level of each failure */
            subject failure : Failure;
            constraint { failure.severity >= 1 and failure.severity <= 10 }
        }

        requirement analysisReq {
            doc /* System shall analyze sequence and impact mechanisms */
            subject analysis : RiskAnalysis;
        }

        requirement scenarioReq {
            doc /* System shall support modeling of different risk scenarios */
            subject scenarios : RiskScenario[*];
        }
    }

    verification def RiskSystemVerification {
        subject system : RiskManagementSystem;

        verify requirement SystemRequirements::identificationReq {
            return system.scenarios.situations->notEmpty();
        }

        verify requirement SystemRequirements::causalityReq {
            return system.scenarios.causalLinks->notEmpty();
        }
    }
}

