    }

    assoc def ScenarioFailureAssociation {
        end scenario : Scenario[1];
        end failure : Failure[1..*];
        attribute likelihood : Real;
    }

    // Risk scenario composition
    item def RiskScenario :> RiskElement {
        ref scenarios : Scenario[1..*] ordered;
        ref propagationPaths : CausalRelationship[0..*];
        attribute aggregatedRisk : Real;
        attribute status : RiskStatus;

        calc def calculateAggregatedRisk : Real {
            // Calculation logic would be implemented here
            return 0.0;
        }
    }

    enum def RiskStatus {
        enum identified;
        enum analyzed;
        enum mitigated;
        enum accepted;
        enum closed;
    }

    // System capabilities
    abstract action def RiskManagementAction {
        in item target : RiskElement;
        out item result : RiskElement;
    }

    action def IdentifyScenario :> RiskManagementAction {
        in item scenarioData : Scenario;
        out item identifiedScenario : Scenario;
    }

    action def AssociateCause {
        in item scenario : Scenario;
        in item cause : Cause;
        in item probability : Real;
        out item association : ScenarioCauseAssociation;
    }

    action def AssociateFailure {
        in item scenario : Scenario;
        in item failure : Failure;
        out item association : ScenarioFailureAssociation;
    }

    action def EstablishCausalRelationship {
        in item source : Scenario;
        in item target : Scenario;
        in item strength : Real;
        out item relationship : CausalRelationship;
    }

    action def ComposeRiskScenario {
        in item scenarios : Scenario[1..*];
        in item relationships : CausalRelationship[0..*];
        out item riskScenario : RiskScenario;
    }

    // Analysis capabilities
    action def AnalyzeRiskPropagation {
        in item startScenario : Scenario;
        out item propagationChain : Scenario[1..*] ordered;
        out item cumulativeRisk : Real;
    }

    action def EvaluateRiskSeverity {
        in item riskScenario : RiskScenario;
        out item severityScore : Real;
        out item recommendations : String[0..*];
    }

    // System interface
    interface def RiskMetadataInterface {
        port scenarioInput : ~Scenario;
        port causeInput : ~Cause;
        port failureInput : ~Failure;
        port riskOutput : RiskScenario;
    }

    // Integration support
    metadata def ModelingLanguageCompatibility {
        attribute supportedLanguages : String[1..*] = ["SysML", "UML", "BPMN"];
        attribute exportFormats : String[1..*] = ["XMI", "JSON", "XML"];
    }

    // System constraints
    constraint def RiskConsistency {
        doc /* All causes must have at least one associated scenario */
        constraint { 
            Cause.all().forAll(c | c.triggeringScenarios.size() > 0)
        }
    }

    constraint def ProbabilityNormalization {
        doc /* Sum of cause probabilities for a scenario should not exceed 1.0 */
        constraint {
            Scenario.all().forAll(s | 
                s.causes.sum(c | c.probability) <= 1.0
            )
        }
    }
}

package EquipmentRiskAnalysisSystem {

    import ScalarValues::*;
    import ISQ::*;
    import SI::*;

    attribute def BatteryLevel :> Real {
        attribute unit = percent;
    }

    attribute def FailureProbability :> Real {
        attribute unit = one;
        constraint { self >= 0.0 and self <= 1.0 }
    }

    attribute def SeverityLevel :> Integer {
        constraint { self >= 1 and self <= 5 }
    }

    enum def EquipmentState {
        enum normal;
        enum lowBattery;
        enum shutdown;
    }

    item def Battery {
        attribute currentLevel : BatteryLevel;
        attribute ageInMonths : Real;
        attribute failureProbability : FailureProbability;
    }

    item def Equipment {
        part battery : Battery;
        attribute operationalState : EquipmentState = EquipmentState::normal;
        attribute minimumBatteryThreshold : BatteryLevel = 20.0;
    }

    concern def Risk {
        doc /* Represents a potential risk in the system */
        attribute probability : FailureProbability;
        attribute impact : String;
    }

    concern batteryAgingRisk : Risk {
        doc /* Risk of battery aging leading to low battery level */
        attribute redefines impact = "Low battery level due to aging";
    }

    concern def FailureMode {
        attribute cause : String;
        attribute effect : String;
        attribute severity : SeverityLevel;
    }

    concern deviceShutdownFailure : FailureMode {
        attribute redefines cause = "Prolonged low battery state";
        attribute redefines effect = "Device shutdown";
        attribute redefines severity = 4;
    }

    analysis def RiskAnalysis {
        subject equipment : Equipment;

        calc batteryFailureProbability : FailureProbability {
            equipment.battery.failureProbability
        }

        constraint lowBatteryDetection {
            equipment.battery.currentLevel < equipment.minimumBatteryThreshold 
            implies 
            equipment.operationalState == EquipmentState::lowBattery
        }
    }

    action def MonitorBatteryLevel {
        in equipment : Equipment;
        out detectedState : EquipmentState;

        action checkBatteryLevel {
            if (equipment.battery.currentLevel < equipment.minimumBatteryThreshold) {
                detectedState := EquipmentState::lowBattery;
            } else {
                detectedState := EquipmentState::normal;
            }
        }
    }

    action def AnalyzeFailureRisk {
        in equipment : Equipment;
        in duration : Real;
        out riskAssessment : FailureMode;

        action evaluateShutdownRisk {
            if (equipment.operationalState == EquipmentState::lowBattery and duration > 300.0) {
                riskAssessment := deviceShutdownFailure;
            }
        }
    }

    requirement def ContinuousMonitoring {
        doc /* System shall continuously monitor battery level during operation */
        subject system : Equipment;

        require constraint {
            system.battery.currentLevel > 0.0
        }
    }

    requirement def BatteryAgingDetection {
        doc /* System shall detect battery aging and associated failure probability */
        subject system : Equipment;

        require constraint {
            system.battery.ageInMonths > 24.0 implies system.battery.failureProbability > 0.3
        }
    }

    requirement def LowBatteryStateIdentification {
        doc /* System shall identify low battery state when below threshold */
        subject system : Equipment;

        require constraint {
            system.battery.currentLevel < system.minimumBatteryThreshold 
            implies 
            system.operationalState == EquipmentState::lowBattery
        }
    }

    requirement def HighSeverityAlertRequirement {
        doc /* System shall assign appropriate severity level for critical failures */
        subject failure : FailureMode;

        require constraint {
            failure.effect == "Device shutdown" implies failure.severity >= 3
        }
    }

    verification def VerifyRiskDetection {
        subject riskAnalysis : RiskAnalysis;

        objective {
            doc /* Verify that battery aging risk is properly detected */
        }
    }
}

