package FuelManagementSystem {
    import ScalarValues::*;
    import ISQ::*;
    import SI::*;

    part def FuelTank {
        attribute fuelLevel : Real;
        port fuelOutlet : FuelInterface;
        port fuelInlet : FuelInterface;
    }

    part def Engine {
        port fuelInlet : FuelInterface;
        port fuelReturn : FuelInterface;
    }

    interface def FuelInterface {
        end supplierEnd : FuelFlowEnd;
        end consumerEnd : FuelFlowEnd;
    }

    port def FuelFlowEnd {
        out item fuelFlow : Fuel;
        in item returnFlow : Fuel;
    }

    item def Fuel {
        attribute temperature : TemperatureValue;
        attribute flowRate : Real;
    }

    part fuelManagementSystem : FuelManagementSystem {
        part fuelTank : FuelTank;
        part engine : Engine;

        interface fuelSupplyInterface : FuelInterface connect 
            supplierEnd ::> fuelTank.fuelOutlet to
            consumerEnd ::> engine.fuelInlet;

        interface fuelReturnInterface : FuelInterface connect
            supplierEnd ::> engine.fuelReturn to
            consumerEnd ::> fuelTank.fuelInlet;

        action manageFuelFlow {
            action deliverFuel {
                flow fuelTank.fuelOutlet.fuelFlow to engine.fuelInlet.fuelFlow;
            }

            action returnFuel {
                flow engine.fuelReturn.returnFlow to fuelTank.fuelInlet.returnFlow;
            }

            action monitorFuelTemperature {
                in fuel : Fuel;
                out temperature : TemperatureValue = fuel.temperature;
            }

            action manageFuelTemperature {
                in currentTemp : TemperatureValue;
                out controlSignal : Real;
            }
        }
    }

    requirement fuelFlowReliability {
        doc /* The system shall ensure reliability of fuel flow */
        subject fuelManagementSystem;
    }

    requirement fuelFlowSafety {
        doc /* The system shall ensure safety of fuel flow */
        subject fuelManagementSystem;
    }

    requirement standardizedInterfaces {
        doc /* Fuel tank and engine shall be connected via standardized fuel interfaces */
        subject fuelSupplyInterface, fuelReturnInterface;
    }
}

