                ref rightFrontWheel : Wheel = wheels#(2);
            }
        }

        part :>> rearAxleAssembly {
            part :>> wheels {
                ref leftRearWheel : Wheel = wheels#(1);
                ref rightRearWheel : Wheel = wheels#(2);
            }
        }
    }

    part vehicle2 : Vehicle {
        attribute :>> mass = 2000.0;
    }
}
