package 'InformationClassificationSystem' {
    enum def ClassificationLevel {
        enum unclassified;
        enum confidential;
        enum secret;
    }

    enum def ClassificationCode {
        enum uncl;
        enum conf;
        enum secr;
    }

    enum def ClassificationColor {
        enum green;
        enum yellow;
        enum red;
    }

    enum def GradeLevel {
        enum A;
        enum B;
        enum C;
        enum D;
        enum F;
    }

    attribute def GradePoints : Real;

    attribute def Information {
        attribute level : ClassificationLevel;
        attribute code : ClassificationCode;
        attribute color : ClassificationColor;
    }

    attribute def Grade {
        attribute level : GradeLevel;
        attribute points : GradePoints;
    }

    constraint def ClassificationMapping {
        constraint unclassifiedMapping {
            (level == ClassificationLevel::unclassified) == 
            (code == ClassificationCode::uncl) == 
            (color == ClassificationColor::green)
        }
        constraint confidentialMapping {
            (level == ClassificationLevel::confidential) == 
            (code == ClassificationCode::conf) == 
            (color == ClassificationColor::yellow)
        }
        constraint secretMapping {
            (level == ClassificationLevel::secret) == 
            (code == ClassificationCode::secr) == 
            (color == ClassificationColor::red)
        }
    }

    constraint def GradeMapping {
        constraint gradeAMapping {
            (level == GradeLevel::A) == (points == 4.0)
        }
        constraint gradeBMapping {
            (level == GradeLevel::B) == (points == 3.0)
        }
        constraint gradeCMapping {
            (level == GradeLevel::C) == (points == 2.0)
        }
        constraint gradeDMapping {
            (level == GradeLevel::D) == (points == 1.0)
        }
        constraint gradeFMapping {
            (level == GradeLevel::F) == (points == 0.0)
        }
    }

    part def InformationManager {
        attribute classifiedInfo : Information[*];
        attribute grades : Grade[*];

        assert constraint classificationConstraint : ClassificationMapping;
        assert constraint gradeConstraint : GradeMapping;
    }
}

