    part def Tire {
        part bead[2];
    }
    part def Hub {
        part rim[2];
        part mountingHole[*];
    }
    part def Bolt;
    part def ThreadedHole;

    interface def BeadRimConnection {
        end bead;
        end rim;
    }

    interface def BoltConnection {
        end bolt : Bolt;
        end threadedHole : ThreadedHole;
    }

    part wheelAssembly {
        part wheelBearingUnit : WheelBearingUnit;
        part tire : Tire;
        part hub : Hub;
        part threadedHole[*] : ThreadedHole;
