        interface boltConnection[*] : BoltConnection
            connect bolt to threadedHole;

        bind hub.mountingHole[1..*] = threadedHole[1..*];
    }
}

package 'FuelFlowManagement' {
	attribute def Fuel;
	attribute def Temperature;
	attribute def FlowData;

	port def FuelSupplyPort {
		out fuel: Fuel;
		out temperature: Temperature;
	}

	port def FuelReturnPort {
		in fuel: Fuel;
		out temperature: Temperature;
	}

	port def DataPort {
		out flowData: FlowData;
	}

	part context {
		part fuelTankAssembly {
			port fuelSupplyPort: FuelSupplyPort;
			port fuelReturnPort: FuelReturnPort;
			port dataPort: DataPort;

			perform manageFuelFlow.supplyFuel {
				out fuel = fuelSupplyPort.fuel;
				out temperature = fuelSupplyPort.temperature;
				out flowData = dataPort.flowData;
			}

			perform manageFuelFlow.receiveReturnFuel {
				in fuel = fuelReturnPort.fuel;
				out temperature = fuelReturnPort.temperature;
				out flowData = dataPort.flowData;
			}
		}

		interface fuelSupplyInterface connect fuelTankAssembly.fuelSupplyPort to engine.fuelSupplyPort {
			ref flow references manageFuelFlow.fuelSupplyFlow
				from source.fuel to target.fuel;
			ref flow references manageFuelFlow.supplyTempFlow
				from source.temperature to target.temperature;
		}

		interface fuelReturnInterface connect engine.fuelReturnPort to fuelTankAssembly.fuelReturnPort {
			ref flow references manageFuelFlow.fuelReturnFlow
				from source.fuel to target.fuel;
			ref flow references manageFuelFlow.returnTempFlow
				from source.temperature to target.temperature;
		}

		part engine {
			port fuelSupplyPort: ~FuelSupplyPort;
			port fuelReturnPort: ~FuelReturnPort;

			perform manageFuelFlow.consumeFuel {
				in fuel = fuelSupplyPort.fuel;
				in temperature = fuelSupplyPort.temperature;
				out returnFuel = fuelReturnPort.fuel;
				out returnTemperature = fuelReturnPort.temperature;
			}
		}

		part user {
			port dataPort: ~DataPort;

			perform manageFuelFlow.monitorFuelFlow {
				in flowData = dataPort.flowData;
			}
		}

		interface dataInterface connect fuelTankAssembly.dataPort to user.dataPort {
			ref flow references manageFuelFlow.dataFlow
				from source.flowData to target.flowData;
		}
	}

	action manageFuelFlow {
		action supplyFuel { out fuel: Fuel; out temperature: Temperature; out flowData: FlowData; }
		succession flow fuelSupplyFlow from supplyFuel.fuel to consumeFuel.fuel;
		succession flow supplyTempFlow from supplyFuel.temperature to consumeFuel.temperature;

		action consumeFuel { in fuel: Fuel; in temperature: Temperature; out returnFuel: Fuel; out returnTemperature: Temperature; }
		succession flow fuelReturnFlow from consumeFuel.returnFuel to receiveReturnFuel.fuel;
		succession flow returnTempFlow from consumeFuel.returnTemperature to receiveReturnFuel.temperature;

		action receiveReturnFuel { in fuel: Fuel; out temperature: Temperature; out flowData: FlowData; }
		succession flow dataFlow from supplyFuel.flowData to monitorFuelFlow.flowData;
		succession flow dataFlow2 from receiveReturnFuel.flowData to monitorFuelFlow.flowData;

		action monitorFuelFlow { in flowData: FlowData; }
	}
}

