package 'VehicleTrailerCoupling' {
    attribute def CouplingForce;
    attribute def ConnectionStatus;

    port def CouplingBallPort {
        out couplingForce : CouplingForce;
        out connectionStatus : ConnectionStatus;
    }

    port def CouplingConnectorPort {
        in couplingForce : CouplingForce;
        in connectionStatus : ConnectionStatus;
    }

    part def Hitch {
        part couplingBall {
            port ballPort : CouplingBallPort;
        }
        part couplingConnector {
            port connectorPort : CouplingConnectorPort;
        }
    }

    part context {
        part vehicle {
            ref hitchBall : Hitch::couplingBall;
            perform towingOperation.provideCouplingForce {
                out couplingForce = hitchBall.ballPort.couplingForce;
                out connectionStatus = hitchBall.ballPort.connectionStatus;
            }
        }

        part hitch : Hitch;

        part trailer {
            ref hitchConnector : Hitch::couplingConnector;
            perform towingOperation.receiveCouplingForce {
                in couplingForce = hitchConnector.connectorPort.couplingForce;
                in connectionStatus = hitchConnector.connectorPort.connectionStatus;
            }
        }

        interface ballToConnector connect hitch.couplingBall.ballPort to hitch.couplingConnector.connectorPort {
            ref flow references towingOperation.couplingForceFlow
                from source.couplingForce to target.couplingForce;
            ref flow references towingOperation.connectionStatusFlow
                from source.connectionStatus to target.connectionStatus;
        }
    }

    action towingOperation {
        action provideCouplingForce { 
            out couplingForce : CouplingForce; 
            out connectionStatus : ConnectionStatus;
        }
        succession flow couplingForceFlow from provideCouplingForce.couplingForce to receiveCouplingForce.couplingForce;
        succession flow connectionStatusFlow from provideCouplingForce.connectionStatus to receiveCouplingForce.connectionStatus;
        action receiveCouplingForce { 
            in couplingForce : CouplingForce;
            in connectionStatus : ConnectionStatus;
        }
    }
}

package 'VehiclePowerTransmission' {
    attribute def FuelCommand;
    attribute def Power;
    attribute def ClutchSignal;
    attribute def Torque;
    attribute def RoadForce;

    port def FuelCommandPort {
        in fuelCmd : FuelCommand;
    }

    port def PowerPort {
        out power : Power;
    }

    port def PowerInputPort {
        in power : Power;
    }

    port def ClutchPort {
        out clutchSignal : ClutchSignal;
    }

    port def ClutchInputPort {
        in clutchSignal : ClutchSignal;
    }

    port def TorquePort {
        out torque : Torque;
    }

    port def TorqueInputPort {
        in torque : Torque;
    }

    port def WheelGroundPort {
        out roadForce : RoadForce;
    }

    port def RoadSurfacePort {
        in roadForce : RoadForce;
    }

    part def Engine {
        port fuelCommandPort : FuelCommandPort;
        port powerPort : PowerPort;
        port clutchPort : ClutchPort;
    }

    part def Transmission {
        port powerInputPort : PowerInputPort;
        port clutchInputPort : ClutchInputPort;
        port powerOutputPort : PowerPort;
    }

    part def Clutch {
        port clutchInputPort : ClutchInputPort;
        port powerInputPort : PowerInputPort;
        port powerOutputPort : PowerPort;
    }

    part def DriveShaft {
        port powerInputPort : PowerInputPort;
        port torqueOutputPort : TorquePort;
    }

    part def Differential {
        port torqueInputPort : TorqueInputPort;
        port leftTorquePort : TorquePort;
        port rightTorquePort : TorquePort;
    }

    part def HalfShaft {
        port torqueInputPort : TorqueInputPort;
        port torqueOutputPort : TorquePort;
    }

    part def Wheel {
        port torqueInputPort : TorqueInputPort;
        port wheelGroundPort : WheelGroundPort;
    }

    part def RearAxleAssembly {
        port torqueInputPort : TorqueInputPort;

        part differential : Differential;
        part leftHalfShaft : HalfShaft;
        part rightHalfShaft : HalfShaft;

        interface diffToLeftShaft connect differential.leftTorquePort to leftHalfShaft.torqueInputPort;
        interface diffToRightShaft connect differential.rightTorquePort to rightHalfShaft.torqueInputPort;

        bind torqueInputPort = differential.torqueInputPort;

        port leftTorqueOutput : TorquePort;
        port rightTorqueOutput : TorquePort;

        bind leftTorqueOutput = leftHalfShaft.torqueOutputPort;
        bind rightTorqueOutput = rightHalfShaft.torqueOutputPort;
    }

    part vehiclePowerSystem {
        part engine : Engine;
        part clutch : Clutch;
        part transmission : Transmission;
        part driveShaft : DriveShaft;
        part rearAxle : RearAxleAssembly;
        part leftRearWheel : Wheel;
        part rightRearWheel : Wheel;
        part roadSurface {
            port leftRoadPort : RoadSurfacePort;
            port rightRoadPort : RoadSurfacePort;
        }

        interface engineToClutch connect engine.powerPort to clutch.powerInputPort;
        interface engineClutchControl connect engine.clutchPort to clutch.clutchInputPort;
        interface clutchToTransmission connect clutch.powerOutputPort to transmission.powerInputPort;
        interface transmissionClutchControl connect engine.clutchPort to transmission.clutchInputPort;
        interface transmissionToDriveShaft connect transmission.powerOutputPort to driveShaft.powerInputPort;
        interface driveShaftToRearAxle connect driveShaft.torqueOutputPort to rearAxle.torqueInputPort;
        interface rearAxleToLeftWheel connect rearAxle.leftTorqueOutput to leftRearWheel.torqueInputPort;
        interface rearAxleToRightWheel connect rearAxle.rightTorqueOutput to rightRearWheel.torqueInputPort;
        interface leftWheelToRoad connect leftRearWheel.wheelGroundPort to roadSurface.leftRoadPort;
        interface rightWheelToRoad connect rightRearWheel.wheelGroundPort to roadSurface.rightRoadPort;
    }
}

package 'MultiArchitectureSystem' {
    port def SignalPort;

    part def C1 {
        port pb: SignalPort;
    }

    part def C2 {
        port pc: SignalPort;
    }

    part def C3 {
        port pd: SignalPort;
    }

    part def C4 {
        port pe: SignalPort;
    }

    part def B11 {
        part c1: C1;
        part c2: C2;
        port pe: SignalPort;

        interface c1ToC2 connect c1.pb to c2.pc;
        bind pe = c1.pb;
    }

    part def B12 {
        part c3: C3;
        part c4: C4;
        port pf: SignalPort;

        interface c3ToC4 connect c3.pd to c4.pe;
        bind pf = c3.pd;
    }

    part def B21 {
        ref part c1: C1;
        ref part c2: C2;
        ref part c3: C3;
        ref part c4: C4;
    }

    part def B22 {
        ref part c1: C1;
        ref part c2: C2;
        ref part c3: C3;
        ref part c4: C4;
    }

    part system {
        variant part decomposition1 {
            part b11: B11;
            part b12: B12;

            interface b11ToB12 connect b11.pe to b12.pf;
        }

        variant part decomposition2 {
            part b21: B21;
            part b22: B22;
        }
    }
}

package 'AutomotivePowertrain' {
	attribute def FuelCommand;
	attribute def StartStopCommand;
	attribute def EngineTorque;
	attribute def TransmissionTorque;
	attribute def DriveshaftTorque;
	attribute def WheelTorque;

	port def FuelCommandPort {
		out fuelCommand: FuelCommand;
	}
	port def StartStopCommandPort {
		out startStopCommand: StartStopCommand;
	}
	port def EngineTorquePort {
		out engineTorque: EngineTorque;
	}
	port def TransmissionTorquePort {
		out transmissionTorque: TransmissionTorque;
	}
	port def DriveshaftTorquePort {
		out driveshaftTorque: DriveshaftTorque;
	}
	port def WheelTorquePort {
		out wheelTorque: WheelTorque;
	}

	part context {
		part user {
			port fuelCommandPort: FuelCommandPort;
			port startStopCommandPort: StartStopCommandPort;
			perform powerTransmission.sendFuelCommand {
				out fuelCommand = fuelCommandPort.fuelCommand;
			}
			perform powerTransmission.sendStartStopCommand {
				out startStopCommand = startStopCommandPort.startStopCommand;
			}
		}

		interface userToEngine connect user.fuelCommandPort to engine.fuelCommandPort {
			ref flow references powerTransmission.fuelCommandFlow
				from source.fuelCommand to target.fuelCommand;
		}
		interface userToEngineControl connect user.startStopCommandPort to engine.startStopCommandPort {
			ref flow references powerTransmission.startStopCommandFlow
				from source.startStopCommand to target.startStopCommand;
		}

		part engine {
			port fuelCommandPort: ~FuelCommandPort;
			port startStopCommandPort: ~StartStopCommandPort;
			port engineTorquePort: EngineTorquePort;
			perform powerTransmission.generateEngineTorque {
				in fuelCommand = fuelCommandPort.fuelCommand;
				in startStopCommand = startStopCommandPort.startStopCommand;
				out engineTorque = engineTorquePort.engineTorque;
			}
		}

		interface engineToTransmission connect engine.engineTorquePort to transmission.engineTorquePort {
			ref flow references powerTransmission.engineTorqueFlow
				from source.engineTorque to target.engineTorque;
		}

		part transmission {
			port engineTorquePort: ~EngineTorquePort;
			port transmissionTorquePort: TransmissionTorquePort;
			perform powerTransmission.amplifyTorque {
				in engineTorque = engineTorquePort.engineTorque;
				out transmissionTorque = transmissionTorquePort.transmissionTorque;
			}
		}

		interface transmissionToDriveshaft connect transmission.transmissionTorquePort to driveshaft.transmissionTorquePort {
			ref flow references powerTransmission.transmissionTorqueFlow
				from source.transmissionTorque to target.transmissionTorque;
		}

		part driveshaft {
			port transmissionTorquePort: ~TransmissionTorquePort;
			port driveshaftTorquePort: DriveshaftTorquePort;
			perform powerTransmission.transmitTorque {
				in transmissionTorque = transmissionTorquePort.transmissionTorque;
				out driveshaftTorque = driveshaftTorquePort.driveshaftTorque;
			}
		}

		interface driveshaftToLeftWheel connect driveshaft.driveshaftTorquePort to leftWheel.driveshaftTorquePort {
			ref flow references powerTransmission.leftWheelTorqueFlow
				from source.driveshaftTorque to target.driveshaftTorque;
		}

		interface driveshaftToRightWheel connect driveshaft.driveshaftTorquePort to rightWheel.driveshaftTorquePort {
			ref flow references powerTransmission.rightWheelTorqueFlow
				from source.driveshaftTorque to target.driveshaftTorque;
		}

		part leftWheel {
			port driveshaftTorquePort: ~DriveshaftTorquePort;
			perform powerTransmission.distributeLeftWheelTorque {
				in driveshaftTorque = driveshaftTorquePort.driveshaftTorque;
			}
		}

		part rightWheel {
			port driveshaftTorquePort: ~DriveshaftTorquePort;
			perform powerTransmission.distributeRightWheelTorque {
				in driveshaftTorque = driveshaftTorquePort.driveshaftTorque;
			}
		}
	}

	action powerTransmission {
		action sendFuelCommand { out fuelCommand: FuelCommand; }
		action sendStartStopCommand { out startStopCommand: StartStopCommand; }
		succession flow fuelCommandFlow from sendFuelCommand.fuelCommand to generateEngineTorque.fuelCommand;
		succession flow startStopCommandFlow from sendStartStopCommand.startStopCommand to generateEngineTorque.startStopCommand;
		action generateEngineTorque { in fuelCommand; in startStopCommand; out engineTorque: EngineTorque; }
		succession flow engineTorqueFlow from generateEngineTorque.engineTorque to amplifyTorque.engineTorque;
		action amplifyTorque { in engineTorque; out transmissionTorque: TransmissionTorque; }
		succession flow transmissionTorqueFlow from amplifyTorque.transmissionTorque to transmitTorque.transmissionTorque;
		action transmitTorque { in transmissionTorque; out driveshaftTorque: DriveshaftTorque; }
		succession flow leftWheelTorqueFlow from transmitTorque.driveshaftTorque to distributeLeftWheelTorque.driveshaftTorque;
		succession flow rightWheelTorqueFlow from transmitTorque.driveshaftTorque to distributeRightWheelTorque.driveshaftTorque;
		action distributeLeftWheelTorque { in driveshaftTorque; }
		action distributeRightWheelTorque { in driveshaftTorque; }
	}
}

package 'AutomotivePowertrain' {
	attribute def FuelControlCmd;
	attribute def StallSignal;
	attribute def Torque;
	attribute def AmplifiedTorque;
	attribute def TransmittedTorque;
	attribute def WheelTorque;

	port def FuelControlCmdPort {
		in fuelControlCmd : FuelControlCmd;
	}
	port def StallSignalPort {
		in stallSignal : StallSignal;
	}
	port def TorquePort {
		out torque : Torque;
	}
	port def AmplifiedTorquePort {
		out amplifiedTorque : AmplifiedTorque;
	}
	port def TransmittedTorquePort {
		out transmittedTorque : TransmittedTorque;
	}
	port def WheelTorquePort {
		out wheelTorque : WheelTorque;
	}

	part context {
		part controller {
			port fuelControlCmdPort : FuelControlCmdPort;
			port stallSignalPort : StallSignalPort;
			perform powerDelivery.sendFuelCommand {
				out fuelControlCmd = fuelControlCmdPort.fuelControlCmd;
			}
			perform powerDelivery.sendStallSignal {
				out stallSignal = stallSignalPort.stallSignal;
			}
		}

		interface controllerToEngine connect controller.fuelControlCmdPort to engine.fuelControlCmdPort {
			ref flow references powerDelivery.fuelCmdFlow
				from source.fuelControlCmd to target.fuelControlCmd;
		}

		interface controllerToEngineStall connect controller.stallSignalPort to engine.stallSignalPort {
			ref flow references powerDelivery.stallFlow
				from source.stallSignal to target.stallSignal;
		}

		part engine {
			port fuelControlCmdPort : ~FuelControlCmdPort;
			port stallSignalPort : ~StallSignalPort;
			port torquePort : TorquePort;
			perform powerDelivery.startEngine {
				in fuelControlCmd = fuelControlCmdPort.fuelControlCmd;
				in stallSignal = stallSignalPort.stallSignal;
			}
			perform powerDelivery.generateTorque {
				in stallSignal = stallSignalPort.stallSignal;
				out torque = torquePort.torque;
			}
		}

		interface engineToTransmission connect engine.torquePort to transmission.torquePort {
			ref flow references powerDelivery.torqueFlow
				from source.torque to target.torque;
		}

		part transmission {
			port torquePort : ~TorquePort;
			port amplifiedTorquePort : AmplifiedTorquePort;
			perform powerDelivery.amplifyTorque {
				in torque = torquePort.torque;
				out amplifiedTorque = amplifiedTorquePort.amplifiedTorque;
			}
		}

		interface transmissionToDriveline connect transmission.amplifiedTorquePort to driveline.amplifiedTorquePort {
			ref flow references powerDelivery.amplifiedTorqueFlow
				from source.amplifiedTorque to target.amplifiedTorque;
		}

		part driveline {
			port amplifiedTorquePort : ~AmplifiedTorquePort;
			port transmittedTorquePort : TransmittedTorquePort;
			perform powerDelivery.transmitTorque {
				in amplifiedTorque = amplifiedTorquePort.amplifiedTorque;
				out transmittedTorque = transmittedTorquePort.transmittedTorque;
			}
		}

		interface drivelineToWheels connect driveline.transmittedTorquePort to wheels.transmittedTorquePort {
			ref flow references powerDelivery.transmittedTorqueFlow
				from source.transmittedTorque to target.transmittedTorque;
		}

		part wheels {
			port transmittedTorquePort : ~TransmittedTorquePort;
			port wheelTorquePort : WheelTorquePort;
			perform powerDelivery.distributeTorque {
				in transmittedTorque = transmittedTorquePort.transmittedTorque;
				out wheelTorque = wheelTorquePort.wheelTorque;
			}
		}
	}

	action powerDelivery {
		action sendFuelCommand { out fuelControlCmd : FuelControlCmd; }
		action sendStallSignal { out stallSignal : StallSignal; }
		succession flow fuelCmdFlow from sendFuelCommand.fuelControlCmd to startEngine.fuelControlCmd;
		succession flow stallFlow from sendStallSignal.stallSignal to startEngine.stallSignal;
		action startEngine { in fuelControlCmd : FuelControlCmd; in stallSignal : StallSignal; }
		succession from startEngine to generateTorque;
		action generateTorque { in stallSignal : StallSignal; out torque : Torque; }
		succession flow torqueFlow from generateTorque.torque to amplifyTorque.torque;
		action amplifyTorque { in torque : Torque; out amplifiedTorque : AmplifiedTorque; }
		succession flow amplifiedTorqueFlow from amplifyTorque.amplifiedTorque to transmitTorque.amplifiedTorque;
		action transmitTorque { in amplifiedTorque : AmplifiedTorque; out transmittedTorque : TransmittedTorque; }
		succession flow transmittedTorqueFlow from transmitTorque.transmittedTorque to distributeTorque.transmittedTorque;
		action distributeTorque { in transmittedTorque : TransmittedTorque; out wheelTorque : WheelTorque; }
	}
}

package 'VehiclePowertrainSystem' {
	attribute def FuelCommand;
	attribute def EngineStartSignal;
	attribute def EngineShutdownSignal;
	attribute def EngineTorque;
	attribute def TransmissionTorque;
	attribute def WheelTorque;

	port def FuelCommandPort {
		out fuelCommand: FuelCommand;
	}
	port def EngineControlPort {
		out engineStartSignal: EngineStartSignal;
		out engineShutdownSignal: EngineShutdownSignal;
	}
	port def EngineTorquePort {
		out engineTorque: EngineTorque;
	}
	port def TransmissionTorquePort {
		out transmissionTorque: TransmissionTorque;
	}
	port def WheelTorquePort {
		out wheelTorque: WheelTorque;
	}

	part context {
		part driver {
			port fuelCommandPort: FuelCommandPort;
			port engineControlPort: EngineControlPort;
			perform powerManagement.provideFuelCommand {
				out fuelCommand = fuelCommandPort.fuelCommand;
			}
			perform powerManagement.provideEngineControl {
				out engineStartSignal = engineControlPort.engineStartSignal;
				out engineShutdownSignal = engineControlPort.engineShutdownSignal;
			}
		}

		interface driverToEngine connect driver.fuelCommandPort to engine.fuelCommandPort {
			ref flow references powerManagement.fuelCommandFlow
				from source.fuelCommand to target.fuelCommand;
		}
		interface driverToEngineControl connect driver.engineControlPort to engine.engineControlPort {
			ref flow references powerManagement.engineStartFlow
				from source.engineStartSignal to target.engineStartSignal;
			ref flow references powerManagement.engineShutdownFlow
				from source.engineShutdownSignal to target.engineShutdownSignal;
		}

		part engine {
			port fuelCommandPort: ~FuelCommandPort;
			port engineControlPort: ~EngineControlPort;
			port engineTorquePort: EngineTorquePort;
			perform powerManagement.generateEngineTorque {
				in fuelCommand = fuelCommandPort.fuelCommand;
				in engineStartSignal = engineControlPort.engineStartSignal;
				in engineShutdownSignal = engineControlPort.engineShutdownSignal;
				out engineTorque = engineTorquePort.engineTorque;
			}
		}

		interface engineToTransmission connect engine.engineTorquePort to transmission.engineTorquePort {
			ref flow references powerManagement.engineTorqueFlow
				from source.engineTorque to target.engineTorque;
		}

		part transmission {
			port engineTorquePort: ~EngineTorquePort;
			port transmissionTorquePort: TransmissionTorquePort;
			perform powerManagement.amplifyTransmitTorque {
				in engineTorque = engineTorquePort.engineTorque;
				out transmissionTorque = transmissionTorquePort.transmissionTorque;
			}
		}

		interface transmissionToWheels connect transmission.transmissionTorquePort to wheels.transmissionTorquePort {
			ref flow references powerManagement.transmissionTorqueFlow
				from source.transmissionTorque to target.transmissionTorque;
		}

		part wheels {
			port transmissionTorquePort: ~TransmissionTorquePort;
			port wheelTorquePort: WheelTorquePort;
			perform powerManagement.distributeTorque {
				in transmissionTorque = transmissionTorquePort.transmissionTorque;
				out wheelTorque = wheelTorquePort.wheelTorque;
			}
		}
	}

	action powerManagement {
		action provideFuelCommand { out fuelCommand: FuelCommand; }
		action provideEngineControl { 
			out engineStartSignal: EngineStartSignal; 
			out engineShutdownSignal: EngineShutdownSignal;
		}
		succession flow fuelCommandFlow from provideFuelCommand.fuelCommand to generateEngineTorque.fuelCommand;
		succession flow engineStartFlow from provideEngineControl.engineStartSignal to generateEngineTorque.engineStartSignal;
		succession flow engineShutdownFlow from provideEngineControl.engineShutdownSignal to generateEngineTorque.engineShutdownSignal;
		action generateEngineTorque { 
			in fuelCommand: FuelCommand;
			in engineStartSignal: EngineStartSignal;
			in engineShutdownSignal: EngineShutdownSignal;
			out engineTorque: EngineTorque; 
		}
		succession flow engineTorqueFlow from generateEngineTorque.engineTorque to amplifyTransmitTorque.engineTorque;
		action amplifyTransmitTorque { 
			in engineTorque: EngineTorque; 
			out transmissionTorque: TransmissionTorque; 
		}
		succession flow transmissionTorqueFlow from amplifyTransmitTorque.transmissionTorque to distributeTorque.transmissionTorque;
		action distributeTorque { 
			in transmissionTorque: TransmissionTorque; 
			out wheelTorque: WheelTorque;
		}
	}
}

package 'VehicleTrailerHitchSystem' {
	attribute def ConnectionState;
	attribute def Force;

	port def ConnectionStatePort {
		out connectionState : ConnectionState;
	}

	port def ForcePort {
		inout force : Force;
	}

	part context {
		part vehicle {
			part vehicleFrame {
				part hitchBall {
					port forcePort : ForcePort;
					port connectionStatePort : ConnectionStatePort;
				}
			}
		}

		part trailer {
			part trailerFrame {
				part trailerCoupler {
					port forcePort : ~ForcePort;
					port connectionStatePort : ~ConnectionStatePort;
				}
			}
		}

		interface hitchConnection connect vehicle.vehicleFrame.hitchBall.forcePort to trailer.trailerFrame.trailerCoupler.forcePort {
			ref flow references powerCoupling.forceTransmission
				from source.force to target.force;
		}

		interface connectionStatus connect vehicle.vehicleFrame.hitchBall.connectionStatePort to trailer.trailerFrame.trailerCoupler.connectionStatePort {
			ref flow references powerCoupling.connectionFlow
				from source.connectionState to target.connectionState;
		}

		part user {
			perform powerCoupling.initiateConnection;
			perform powerCoupling.initiateDisconnection;
		}
	}

	action powerCoupling {
		action initiateConnection {
			out connectionState : ConnectionState;
		}

		action initiateDisconnection {
			out connectionState : ConnectionState;
		}

		flow connectionFlow from initiateConnection.connectionState to maintainConnection.connectionState;
		flow connectionFlow from initiateDisconnection.connectionState to maintainConnection.connectionState;

		action maintainConnection {
			in connectionState : ConnectionState;
			inout force : Force;
		}

		flow forceTransmission from maintainConnection.force to maintainConnection.force;
	}
}

package 'VehicleTrailerConnection' {
