		part user {
			port connectionCmdPort : ConnectionCmdPort;
			perform manageConnection.sendConnectionCmd {
				out connectionCmd = connectionCmdPort.connectionCmd;
			}
		}

		interface userToVehicle connect user.connectionCmdPort to vehicle.connectionCmdPort {
			ref flow references manageConnection.connectionCmdFlow
				from source.connectionCmd to target.connectionCmd;
		}

		part vehicle {
			part chassis {
				part towBall : TowBall;
			}
			port connectionCmdPort : ~ConnectionCmdPort;
			port connectionStatusPort : ConnectionStatusPort;
			perform manageConnection.executeConnection {
				in connectionCmd = connectionCmdPort.connectionCmd;
				out connectionStatus = connectionStatusPort.connectionStatus;
			}
		}

		interface vehicleToTrailer connect vehicle.connectionStatusPort to trailer.connectionStatusPort {
			ref flow references manageConnection.connectionStatusFlow
				from source.connectionStatus to target.connectionStatus;
		}

		part trailer {
			part trailerFrame {
				part coupler : Coupler;
			}
