package 'VehicleTrailerConnection' {
	attribute def HitchCmd;
	attribute def UnhitchCmd;
	attribute def LatchStatus;

	port def HitchCmdPort {
		out hitchCmd : HitchCmd;
	}

	port def UnhitchCmdPort {
		out unhitchCmd : UnhitchCmd;
	}

	port def LatchStatusPort {
		out latchStatus : LatchStatus;
	}

	part context {
		part user {
			port hitchCmdPort : HitchCmdPort;
			port unhitchCmdPort : UnhitchCmdPort;

			perform trailerOperation.sendHitchCmd {
				out hitchCmd = hitchCmdPort.hitchCmd;
			}

			perform trailerOperation.sendUnhitchCmd {
				out unhitchCmd = unhitchCmdPort.unhitchCmd;
			}
		}

		part vehicle {
			part chassis {
				part towBall {
					port latchStatusPort : ~LatchStatusPort;

					perform trailerOperation.provideLatchPoint {
						in latchStatus = latchStatusPort.latchStatus;
					}
				}
			}
		}

		part trailer {
			part trailerChassis {
				part couplerMechanism {
					port hitchCmdPort : ~HitchCmdPort;
					port unhitchCmdPort : ~UnhitchCmdPort;
					port latchStatusPort : LatchStatusPort;

					perform trailerOperation.engageWithTowBall {
						in hitchCmd = hitchCmdPort.hitchCmd;
						out latchStatus = latchStatusPort.latchStatus;
					}

					perform trailerOperation.disengageFromTowBall {
						in unhitchCmd = unhitchCmdPort.unhitchCmd;
						out latchStatus = latchStatusPort.latchStatus;
					}
				}
			}
		}

		interface userToTrailer connect user.hitchCmdPort to trailer.trailerChassis.couplerMechanism.hitchCmdPort {
			ref flow references trailerOperation.hitchCmdFlow
				from source.hitchCmd to target.hitchCmd;
		}

		interface userToTrailerUnhitch connect user.unhitchCmdPort to trailer.trailerChassis.couplerMechanism.unhitchCmdPort {
			ref flow references trailerOperation.unhitchCmdFlow
				from source.unhitchCmd to target.unhitchCmd;
		}

		interface trailerToVehicle connect trailer.trailerChassis.couplerMechanism.latchStatusPort to vehicle.chassis.towBall.latchStatusPort {
			ref flow references trailerOperation.latchStatusFlow
				from source.latchStatus to target.latchStatus;
		}
	}

	action trailerOperation {
		action sendHitchCmd { out hitchCmd : HitchCmd; }
		succession flow hitchCmdFlow from sendHitchCmd.hitchCmd to engageWithTowBall.hitchCmd;

		action engageWithTowBall { in hitchCmd : HitchCmd; out latchStatus : LatchStatus; }
		succession flow latchStatusFlow from engageWithTowBall.latchStatus to provideLatchPoint.latchStatus;

		action provideLatchPoint { in latchStatus : LatchStatus; }

		action sendUnhitchCmd { out unhitchCmd : UnhitchCmd; }
		succession flow unhitchCmdFlow from sendUnhitchCmd.unhitchCmd to disengageFromTowBall.unhitchCmd;

		action disengageFromTowBall { in unhitchCmd : UnhitchCmd; out latchStatus : LatchStatus; }
	}
}

package 'FuelSystem' {
	attribute def Fuel;
	attribute def Temperature;

	port def FuelSupplyPort {
		out fuel : Fuel;
		out temperature : Temperature;
	}

	port def FuelReturnPort {
		in fuel : Fuel;
		in temperature : Temperature;
	}

	part def FuelTankAssembly {
		port fuelSupplyPort : FuelSupplyPort;
		port fuelReturnPort : FuelReturnPort;

		perform fuelCirculation.supplyFuel {
			out fuel = fuelSupplyPort.fuel;
			out temperature = fuelSupplyPort.temperature;
		}

		perform fuelCirculation.receiveFuel {
			in fuel = fuelReturnPort.fuel;
			in temperature = fuelReturnPort.temperature;
		}
	}

	part def Engine {
		port fuelSupplyPort : ~FuelSupplyPort;
		port fuelReturnPort : ~FuelReturnPort;

		perform fuelCirculation.consumeFuel {
			in fuel = fuelSupplyPort.fuel;
			in temperature = fuelSupplyPort.temperature;
			out returnFuel = fuelReturnPort.fuel;
			out returnTemperature = fuelReturnPort.temperature;
		}
	}

	part motorVehicle {
		part fuelTank : FuelTankAssembly;
		part engine : Engine;

		interface fuelSupplyInterface connect fuelTank.fuelSupplyPort to engine.fuelSupplyPort {
			ref flow references fuelCirculation.fuelSupplyFlow
				from source.fuel to target.fuel;
			ref flow references fuelCirculation.tempSupplyFlow
				from source.temperature to target.temperature;
		}

		interface fuelReturnInterface connect engine.fuelReturnPort to fuelTank.fuelReturnPort {
			ref flow references fuelCirculation.fuelReturnFlow
				from source.fuel to target.fuel;
			ref flow references fuelCirculation.tempReturnFlow
				from source.temperature to target.temperature;
		}
	}

	action fuelCirculation {
		action supplyFuel { out fuel : Fuel; out temperature : Temperature; }
		succession flow fuelSupplyFlow from supplyFuel.fuel to consumeFuel.fuel;
		succession flow tempSupplyFlow from supplyFuel.temperature to consumeFuel.temperature;

		action consumeFuel { 
			in fuel : Fuel; 
			in temperature : Temperature;
			out returnFuel : Fuel;
			out returnTemperature : Temperature;
		}
		succession flow fuelReturnFlow from consumeFuel.returnFuel to receiveFuel.fuel;
		succession flow tempReturnFlow from consumeFuel.returnTemperature to receiveFuel.temperature;

		action receiveFuel { in fuel : Fuel; in temperature : Temperature; }
	}
}

package 'FuelDeliverySystem' {
	attribute def Fuel;
	attribute def FuelLevel;
	attribute def MaxCapacity;

	port def FuelOutPort {
		out fuel: Fuel;
	}

	port def FuelInPort {
		in fuel: Fuel;
	}

	port def FuelTransferPort {
		in fuelIn: Fuel;
		out fuelOut: Fuel;
	}

	part context {
		part storageTank {
			port fuelOutPort: FuelOutPort;
			perform fuelDelivery.storeFuel {
				out fuel = fuelOutPort.fuel;
			}
		}

		interface storageTankToPump connect storageTank.fuelOutPort to fuelPump.fuelInPort {
			ref flow references fuelDelivery.storageToPumpFlow
				from source.fuel to target.fuel;
		}

		part fuelPump {
			port fuelInPort: FuelInPort;
			port fuelOutPort: FuelOutPort;
			perform fuelDelivery.pumpFuel {
				in fuel = fuelInPort.fuel;
				out fuel = fuelOutPort.fuel;
			}
		}

		interface pumpToVehicle connect fuelPump.fuelOutPort to vehicle.fuelInPort {
			ref flow references fuelDelivery.pumpToVehicleFlow
				from source.fuel to target.fuel;
		}

		part vehicle {
			part fuelTank {
				attribute currentLevel: FuelLevel;
				attribute maxCapacity: MaxCapacity;
				port fuelInPort: FuelInPort;
				perform fuelDelivery.receiveFuel {
					in fuel = fuelInPort.fuel;
				}
			}
			port fuelInPort: FuelInPort = fuelTank.fuelInPort;
		}
	}

	action fuelDelivery {
