		succession flow storageToPumpFlow from storeFuel.fuel to pumpFuel.fuel;
		action pumpFuel { in fuel: Fuel; out fuel: Fuel; }
		succession flow pumpToVehicleFlow from pumpFuel.fuel to receiveFuel.fuel;
