package 'AutomobileAssembly' {
	part def VehicleAssembly;
	part def Gearbox;
	part def Engine;
	part def VehicleWithoutEngine;
	part def CompleteVehicle;

	action def InstallGearbox {
		in vehicleAssembly : VehicleAssembly;
		in gearbox : Gearbox;
		out vehicleWithoutEngine : VehicleWithoutEngine;
	}

	action def InstallEngine {
		in vehicleWithoutEngine : VehicleWithoutEngine;
		in engine : Engine;
		out completeVehicle : CompleteVehicle;
	}

	part assemblyLine {
		part initialAssembly : VehicleAssembly;
		part gearboxUnit : Gearbox;
		part engineUnit : Engine;

		perform assembleVehicle {
			action installGearbox : InstallGearbox {
				in vehicleAssembly = initialAssembly;
				in gearbox = gearboxUnit;
				out vehicleWithoutEngine;
			}

			flow from installGearbox.vehicleWithoutEngine to installEngine.vehicleWithoutEngine;

			action installEngine : InstallEngine {
				in vehicleWithoutEngine;
				in engine = engineUnit;
				out completeVehicle;
			}

			out finalVehicle = installEngine.completeVehicle;
		}

		part finalVehicle : CompleteVehicle {
			part gearbox : Gearbox;
			part engine : Engine;
		}
	}
}

package 'VehicleControlSystem' {
    attribute def StartSignal;
    attribute def BrakePedalSignal;
    attribute def VehicleOnSignal;
    attribute def Temperature;
    attribute def OverTempSignal;
    attribute def RestoreSignal;
    attribute def ShutdownSignal;
    attribute def PowerSupply;
    attribute def ParkingBrakeSignal;

    port def ControlSignalPort {
        in startSignal: StartSignal;
        in brakePedalSignal: BrakePedalSignal;
        in vehicleOnSignal: VehicleOnSignal;
        in restoreSignal: RestoreSignal;
        in shutdownSignal: ShutdownSignal;
    }

    port def HealthMonitorPort {
        out temperature: Temperature;
        out overTempSignal: OverTempSignal;
    }

    port def ActuatorPort {
        out powerSupply: PowerSupply;
        out parkingBrakeSignal: ParkingBrakeSignal;
    }

    part def VehicleA {
        port controlPort: ControlSignalPort;
        port healthPort: HealthMonitorPort;
        port actuatorPort: ActuatorPort;

        exhibit state vehicleStates {
            entry state off;
            state starting;
            state running {
                entry action {
                    perform selfCheck;
                    perform supplyPower;
                }
                exit action {
                    perform applyParkingBrake;
                }
            }

            transition off_to_starting
                first off
                accept controlPort.brakePedalSignal
                accept controlPort.startSignal
                then starting;

            transition starting_to_running
                first starting
                accept controlPort.vehicleOnSignal
                then running;

            transition running_to_off
                first running
                accept controlPort.shutdownSignal
                then off;
        }

        exhibit state healthStates parallel {
            state normal;
            state degraded;
            state maintenance;

            transition normal_to_degraded
                first normal
                when temperature > safetyThreshold
                do send overTempSignal to healthPort.overTempSignal
                then degraded;

            transition degraded_to_normal
                first degraded
                accept controlPort.restoreSignal
                then normal;

            transition maintenance_to_normal
                first maintenance
                accept controlPort.restoreSignal
                then normal;
        }

        action selfCheck;
        action supplyPower {
            out powerSupply = actuatorPort.powerSupply;
        }
        action applyParkingBrake {
            out parkingBrakeSignal = actuatorPort.parkingBrakeSignal;
        }
        action monitorTemperature {
            out temperature = healthPort.temperature;
        }

        attribute safetyThreshold: Temperature;
        attribute temperature: Temperature;
    }

    part def VehicleController {
        port controlOutPort: ~ControlSignalPort;
        port healthInPort: ~HealthMonitorPort;

        exhibit state controllerStates {
            state idle;
            state active;
            state emergency;

            transition idle_to_active
                first idle
                do send startSignal to controlOutPort.startSignal
                then active;

            transition active_to_emergency
                first active
                accept healthInPort.overTempSignal
                then emergency;

            transition active_to_idle
                first active
                do send shutdownSignal to controlOutPort.shutdownSignal
                then idle;
        }
    }

    part context {
        part vehicleA: VehicleA;
        part controller: VehicleController;

        interface controlInterface connect controller.controlOutPort to vehicleA.controlPort;
        interface healthInterface connect vehicleA.healthPort to controller.healthInPort;
    }
}

package 'VehicleSystem' {
    attribute def StartSignal;
    attribute def BrakePedalSignal;
    attribute def Temperature;
    attribute def WarningSignal;
    attribute def HealthStatus;
    attribute def Fuel;
    attribute def MaintenanceInterval;
    attribute def TemperatureThreshold;

    port def ControlSignalPort {
        in startSignal: StartSignal;
        in brakePedalSignal: BrakePedalSignal;
    }

    port def StatusSignalPort {
        out warningSignal: WarningSignal;
        out healthStatus: HealthStatus;
    }

    port def SensorDataPort {
        in temperature: Temperature;
    }

    part def Vehicle {
        attribute fuel: Fuel;
        attribute maintenanceInterval: MaintenanceInterval;
        attribute temperatureThreshold: TemperatureThreshold;

        port controlPort: ControlSignalPort;
        port statusPort: StatusSignalPort;
        port sensorPort: SensorDataPort;

        state vehicleStates {
            entry state shutdown;
            state running;
            state degraded;

            transition shutdown_to_running
                first shutdown
                accept controlPort.startSignal
                if controlPort.brakePedalSignal
                then running;

            transition running_to_degraded
                first running
                if sensorPort.temperature > temperatureThreshold
                do send statusPort.warningSignal
                then degraded;

            transition degraded_to_running
                first degraded
                if sensorPort.temperature <= temperatureThreshold
                then running;
        }
    }

    part def VehicleController {
        port controlOutPort: ~ControlSignalPort;
        port statusInPort: ~StatusSignalPort;

        perform action processVehicleStatus {
            in warningSignal = statusInPort.warningSignal;
            in healthStatus = statusInPort.healthStatus;
        }

        perform action sendControlSignals {
            out startSignal = controlOutPort.startSignal;
            out brakePedalSignal = controlOutPort.brakePedalSignal;
        }
    }

    part vehicleSystem {
        part vehicle: Vehicle;
        part controller: VehicleController;

        interface controlInterface connect controller.controlOutPort to vehicle.controlPort;
        interface statusInterface connect vehicle.statusPort to controller.statusInPort;

        perform action manageVehicleOperation {
            action startup {
                in brakePedalSignal: BrakePedalSignal;
                in startSignal: StartSignal;
            }

            action monitorTemperature {
                in temperature: Temperature;
                out warningSignal: WarningSignal;
            }

            action performMaintenance {
                in healthStatus: HealthStatus;
            }

            succession flow startupFlow from startup to monitorTemperature;
            succession flow monitorFlow from monitorTemperature to performMaintenance;
        }
    }
}

package 'VehicleSimulation' {
    attribute def Mass;
    attribute def Position;
    attribute def Velocity;
    attribute def Acceleration;
    attribute def Angle;
    attribute def FrictionCoefficient;
    attribute def Time;
    attribute def VehicleState;
    attribute def SimulationParameters {
        mass: Mass;
        position: Position;
        velocity: Velocity;
        acceleration: Acceleration;
    }
    attribute def RoadParameters {
        slope: Angle;
        friction: FrictionCoefficient;
    }
    attribute def Snapshot {
        time: Time;
        vehicleState: VehicleState;
        position: Position;
        velocity: Velocity;
        acceleration: Acceleration;
    }

    port def SimulationInputPort {
        in simulationParams: SimulationParameters;
        in roadParams: RoadParameters;
    }
    port def SnapshotOutputPort {
        out snapshot: Snapshot;
    }

    part context {
        part user {
            port simulationInputPort: SimulationInputPort;
            perform spatiotemporalSimulation.defineParameters {
                out simulationParams = simulationInputPort.simulationParams;
                out roadParams = simulationInputPort.roadParams;
            }
        }

        interface userToSimulator connect user.simulationInputPort to simulator.simulationInputPort {
            ref flow references spatiotemporalSimulation.simulationParamsFlow
                from source.simulationParams to target.simulationParams;
            ref flow references spatiotemporalSimulation.roadParamsFlow
                from source.roadParams to target.roadParams;
        }

        part simulator {
            port simulationInputPort: ~SimulationInputPort;
            port snapshotOutputPort: SnapshotOutputPort;

            perform spatiotemporalSimulation.simulateVehicleDynamics {
                in simulationParams = simulationInputPort.simulationParams;
                in roadParams = simulationInputPort.roadParams;
                out snapshot = snapshotOutputPort.snapshot;
            }
        }

        part dataRecorder {
            port snapshotOutputPort: ~SnapshotOutputPort;
            perform spatiotemporalSimulation.recordSnapshots {
                in snapshot = snapshotOutputPort.snapshot;
            }
        }

        interface simulatorToRecorder connect simulator.snapshotOutputPort to dataRecorder.snapshotOutputPort {
            ref flow references spatiotemporalSimulation.snapshotFlow
                from source.snapshot to target.snapshot;
        }
    }

    action spatiotemporalSimulation {
        action defineParameters {
            out simulationParams: SimulationParameters;
            out roadParams: RoadParameters;
        }
        succession flow simulationParamsFlow from defineParameters.simulationParams to simulateVehicleDynamics.simulationParams;
        succession flow roadParamsFlow from defineParameters.roadParams to simulateVehicleDynamics.roadParams;

        action simulateVehicleDynamics {
            in simulationParams: SimulationParameters;
            in roadParams: RoadParameters;
            out snapshot: Snapshot;
        }
        succession flow snapshotFlow from simulateVehicleDynamics.snapshot to recordSnapshots.snapshot;

        action recordSnapshots {
            in snapshot: Snapshot;
        }
    }
}

package 'AutomotiveConfigurationManagement' {
    part def Component1;
    part def Component2;
    part def Component3;
    part def Component4;
    part def Component5;
    part def Component6;

    part def SubsystemA {
        variant part config1 {
            part component1: Component1;
            part component2: Component2;
        }
        variant part config2 {
            part component2: Component2;
            part component3: Component3;
        }
    }

    part def SubsystemB {
        variant part config1 {
            part component4: Component4;
            part component5: Component5;
        }
        variant part config2 {
            part component5: Component5;
            part component6: Component6;
        }
    }

    part def Automobile {
        part subsystemA: SubsystemA[0..1];
        part subsystemB: SubsystemB[0..1];

        constraint configurationConstraint {
            (subsystemA.config2 == null) or (subsystemB.config2 != null);
        }
    }

    part configurationA: Automobile {
        part :>> subsystemA {
            part :>> config1;
        }
        part :>> subsystemB {
            part :>> config1;
        }
    }

    part configurationB: Automobile {
        part :>> subsystemA {
            part :>> config2;
        }
        part :>> subsystemB {
            part :>> config1;
        }
    }
}

package 'ModularVehiclePlatform' {
    part def Part1;
    part def Part2;
    part def Part3 {
        port p1;
    }
    part def Part4;
    part def Part5 {
        port p2;
        variant action behaviorVariant1;
        variant action behaviorVariant2;
    }
    part def Part6;

    abstract part def SubsystemA {
        abstract part components[*];
        abstract port subsystemPortA;
    }

    abstract part def SubsystemB {
        abstract part components[*];
        abstract port subsystemPortB;
    }

    part def SubsystemAVariant1 :> SubsystemA {
        part components[2] {
            part component1 : Part1;
            part component2 : Part3;
        }
    }

    part def SubsystemAVariant2 :> SubsystemA {
        part components[3] {
            part component1 : Part2;
            part component2 : Part4;
            part component3 : Part5;
        }
    }

    part def SubsystemBVariant1 :> SubsystemB {
        part components[2] {
            part component1 : Part4;
            part component2 : Part6;
        }
    }

    part def SubsystemBVariant2 :> SubsystemB {
        part components[3] {
            part component1 : Part3;
            part component2 : Part5;
            part component3 : Part6;
        }
    }

    part def VehicleConfiguration {
        abstract part subsystemA : SubsystemA;
        abstract part subsystemB : SubsystemB;
        interface subsystemInterface connect subsystemA.subsystemPortA to subsystemB.subsystemPortB;
    }

    part def VehicleConfigA :> VehicleConfiguration {
        part subsystemA : SubsystemAVariant1;
        part subsystemB : SubsystemBVariant1;
    }

    part def VehicleConfigB :> VehicleConfiguration {
        part subsystemA : SubsystemAVariant2;
        part subsystemB : SubsystemBVariant2;
    }

    constraint def ConfigurationConsistency {
        doc /* Ensures configuration consistency and rationality */
    }

    allocation def ComponentAllocation {
        end feature components : Part1[*];
        end feature subsystems : SubsystemA[*];
    }
}

package 'VehicleProductLine' {
    attribute def PerformanceLevel;
    attribute def EngineType;
    attribute def TransmissionType;
    attribute def WheelType;
    attribute def ConfigurationRequest;
    attribute def VehicleConfiguration;

    port def ConfigRequestPort {
        in configRequest : ConfigurationRequest;
    }

    port def VehicleConfigPort {
        out vehicleConfig : VehicleConfiguration;
    }

    part def Engine {
        attribute cylinders : Integer;
        attribute engineType : EngineType;
    }

    part def Transmission {
        attribute transmissionType : TransmissionType;
    }

    part def Wheel {
        attribute rimType : String;
        attribute wheelType : WheelType;
    }

    part context {
        part customer {
            port configRequestPort : ConfigRequestPort;
            perform configureVehicle.specifyRequirements {
                out configRequest = configRequestPort.configRequest;
            }
        }

        interface customerToConfigurator connect customer.configRequestPort to configurator.configRequestPort {
            ref flow references configureVehicle.configRequestFlow
                from source.configRequest to target.configRequest;
        }

        part configurator {
            port configRequestPort : ~ConfigRequestPort;
            port vehicleConfigPort : VehicleConfigPort;

            perform configureVehicle.selectComponents {
                in configRequest = configRequestPort.configRequest;
                out vehicleConfig = vehicleConfigPort.vehicleConfig;
            }

            constraint highPerformanceConfig {
                doc /* When high performance is required, configure 6-cylinder engine, 
                       automatic transmission, and wide-rim wheels */
            }

            constraint standardPerformanceConfig {
                doc /* For standard performance, configure 4-cylinder engine, 
                       manual transmission, and narrow-rim wheels */
            }

            constraint compatibilityConstraints {
                doc /* Ensure compatibility among engine, transmission, and wheels */
            }
        }

        interface configuratorToVehicle connect configurator.vehicleConfigPort to vehicle.vehicleConfigPort {
            ref flow references configureVehicle.vehicleConfigFlow
                from source.vehicleConfig to target.vehicleConfig;
        }

        part vehicle {
            port vehicleConfigPort : ~VehicleConfigPort;

            part engine : Engine;
            part transmission : Transmission;
            part wheels : Wheel[4];

            perform configureVehicle.assembleVehicle {
                in vehicleConfig = vehicleConfigPort.vehicleConfig;
            }
        }
    }

    action configureVehicle {
        action specifyRequirements { out configRequest : ConfigurationRequest; }
        succession flow configRequestFlow from specifyRequirements.configRequest to selectComponents.configRequest;
        action selectComponents { in configRequest; out vehicleConfig : VehicleConfiguration; }
        succession flow vehicleConfigFlow from selectComponents.vehicleConfig to assembleVehicle.vehicleConfig;
        action assembleVehicle { in vehicleConfig : VehicleConfiguration; }
    }
}

package 'VehicleManagementSystem' {
    attribute def Mass;
    attribute def FuelStatus;
    attribute def Torque;
    attribute def Reliability;

    enum def MassGrade {
        enum mass2000kg;
        enum mass2500kg;
    }

    enum def FuelState {
        enum fullyFueled;
        enum emptyFuel;
    }

    requirement def VehicleMassRequirement {
        doc /* Vehicle must reflect actual mass and comply with maximum mass limitations */
        attribute massGrade: MassGrade;
        attribute maxMass: Mass;
    }

    requirement def FuelStatusRequirement {
        doc /* Vehicle must meet mass requirements based on fuel state */
        attribute requiredFuelState: FuelState;
    }

    requirement def PowerDeliveryRequirement {
        doc /* Engine must output torque and connect to transmission */
        attribute minTorque: Torque;
    }

    requirement def ReliabilityRequirement {
        doc /* Vehicle must maintain performance throughout lifecycle */
        attribute reliabilityTarget: Reliability;
    }

    port def TorquePort {
        out torque: Torque;
    }

    interface def DriveInterface {
        end engineEnd: TorquePort;
        end transmissionEnd: ~TorquePort;
    }

    part def Engine {
        port torqueOut: TorquePort;
        attribute outputTorque: Torque;

        perform action produceTorque {
            out torque = torqueOut.torque;
        }
    }

    part def Transmission {
        port torqueIn: ~TorquePort;

        perform action receiveTorque {
            in torque = torqueIn.torque;
        }
    }

    part def Vehicle {
        attribute actualMass: Mass;
        attribute fuelStatus: FuelState;
        attribute reliability: Reliability;

        part engine: Engine;
        part transmission: Transmission;

        interface driveConnection: DriveInterface connect 
            engine.torqueOut to transmission.torqueIn;

        satisfy massReq: VehicleMassRequirement;
        satisfy fuelReq: FuelStatusRequirement;
        satisfy powerReq: PowerDeliveryRequirement;
        satisfy reliabilityReq: ReliabilityRequirement;
    }

    part vehicleManagementContext {
        part vehicle2000kg: Vehicle {
            :>> actualMass = 2000;
            :>> fuelStatus = FuelState::fullyFueled;
        }

        part vehicle2500kg: Vehicle {
            :>> actualMass = 2500;
            :>> fuelStatus = FuelState::emptyFuel;
        }
    }
}

package 'VehicleMassCompliance' {
	attribute def Mass;
	attribute def ComplianceStatus;
	attribute def MaxPermissibleMass;

	port def VehiclePort {
		in vehicle : Vehicle;
	}

	port def MassPort {
		out mass : Mass;
	}

	port def CompliancePort {
		out status : ComplianceStatus;
	}

	part def Vehicle {
		attribute actualMass : Mass;
	}

	part context {
		part testOperator {
			port vehiclePort : VehiclePort;
			port massPort : ~MassPort;

			perform verifyCompliance.measureMass {
				in vehicle = vehiclePort.vehicle;
				out mass = massPort.mass;
			}
		}

		part weighingEquipment {
			port vehiclePort : ~VehiclePort;
			port massPort : MassPort;
		}

		interface operatorToEquipment connect testOperator.vehiclePort to weighingEquipment.vehiclePort {
			ref flow references verifyCompliance.vehicleFlow
				from source.vehicle to target.vehicle;
		}

		interface equipmentToOperator connect weighingEquipment.massPort to testOperator.massPort {
			ref flow references verifyCompliance.massFlow
				from source.mass to target.mass;
		}

		part complianceSystem {
			attribute maxPermissibleMass : MaxPermissibleMass = 2500;
			port massPort : ~MassPort;
			port compliancePort : CompliancePort;

			perform verifyCompliance.determineCompliance {
				in mass = massPort.mass;
				in maxMass = maxPermissibleMass;
				out status = compliancePort.status;
			}
		}

		interface operatorToSystem connect testOperator.massPort to complianceSystem.massPort {
			ref flow references verifyCompliance.massToSystemFlow
				from source.mass to target.mass;
		}
	}

	action verifyCompliance {
		action measureMass { 
			in vehicle : Vehicle; 
			out mass : Mass;
		}

		succession flow vehicleFlow from measureMass.vehicle to measureMass;
		succession flow massFlow from measureMass.mass to measureMass;
		succession flow massToSystemFlow from measureMass.mass to determineCompliance.mass;

		action determineCompliance { 
			in mass : Mass;
			in maxMass : MaxPermissibleMass;
			out status : ComplianceStatus;
		}
	}
}

package 'WaterSupplyNetwork' {
	attribute def HotWater;
	attribute def ColdWater;

	port def HotWaterPort {
		out hotWater: HotWater;
	}

	port def ColdWaterPort {
		out coldWater: ColdWater;
	}

	port def FaucetInlet {
		in hotWater: HotWater;
		in coldWater: ColdWater;
	}

	part def Spigot {
		port hotWaterOut: HotWaterPort;
		port coldWaterOut: ColdWaterPort;
	}

	part def Faucet {
		port inlet: FaucetInlet;
	}

	part def SpigotBank {
		part hotSpigot: Spigot;
		part coldSpigot: Spigot;
	}

	part context {
		part spigotBank: SpigotBank {
			perform supplyWater.provideHotWater {
				out hotWater = hotSpigot.hotWaterOut.hotWater;
			}
			perform supplyWater.provideColdWater {
				out coldWater = coldSpigot.coldWaterOut.coldWater;
			}
		}

		part faucets: Faucet[1..*] {
			perform supplyWater.receiveWater {
				in hotWater = inlet.hotWater;
				in coldWater = inlet.coldWater;
			}
		}

		interface hotWaterConnection connect spigotBank.hotSpigot.hotWaterOut to faucets.inlet {
			ref flow references supplyWater.hotWaterFlow
				from source.hotWater to target.hotWater;
		}

		interface coldWaterConnection connect spigotBank.coldSpigot.coldWaterOut to faucets.inlet {
			ref flow references supplyWater.coldWaterFlow
				from source.coldWater to target.coldWater;
		}
	}

	action supplyWater {
		action provideHotWater { out hotWater: HotWater; }
		action provideColdWater { out coldWater: ColdWater; }
		action receiveWater { in hotWater: HotWater; in coldWater: ColdWater; }

		succession flow hotWaterFlow from provideHotWater.hotWater to receiveWater.hotWater;
		succession flow coldWaterFlow from provideColdWater.coldWater to receiveWater.coldWater;
	}
}

package 'VehicleQualityAnalysis' {
    attribute def Mass;
    attribute def QualityData;
    attribute def AnalysisRequirement;
    attribute def AnalysisObjective;
    attribute def AnalysisTask;
    attribute def AnalysisPlan;
    attribute def QualityEvaluation;

    port def QualityDataPort {
        in qualityData: QualityData;
    }

    port def MassPort {
        out mass: Mass;
    }

    port def AnalysisPort {
        in analysisRequirement: AnalysisRequirement;
        in analysisObjective: AnalysisObjective;
        out analysisTask: AnalysisTask;
        out analysisPlan: AnalysisPlan;
        out qualityEvaluation: QualityEvaluation;
    }

    part def Component {
        port qualityDataPort: QualityDataPort;
        port massPort: MassPort;
        attribute componentMass: Mass;
    }

    part vehicle {
        part engine: Component;
        part transmission: Component;
        part frontAxleAssembly: Component;
        part rearAxleAssembly: Component;

        attribute totalMass: Mass;

        calc constraint {
            totalMass == engine.componentMass + transmission.componentMass + 
                        frontAxleAssembly.componentMass + rearAxleAssembly.componentMass
        }

        port vehicleQualityPort: QualityDataPort;
        port vehicleMassPort: MassPort {
            out mass = totalMass;
        }
    }

    part qualityAnalysisSystem {
        port analysisPort: AnalysisPort;

        perform analyzeVehicleQuality {
            in analysisRequirement = analysisPort.analysisRequirement;
            in analysisObjective = analysisPort.analysisObjective;
            out analysisTask = analysisPort.analysisTask;
            out analysisPlan = analysisPort.analysisPlan;
            out qualityEvaluation = analysisPort.qualityEvaluation;
        }
    }

    part user {
        port qualityInputPort: ~QualityDataPort;
        port analysisInputPort: ~AnalysisPort;

        perform inputQualityData {
            out qualityData = qualityInputPort.qualityData;
        }

        perform defineAnalysisRequirements {
            out analysisRequirement = analysisInputPort.analysisRequirement;
            out analysisObjective = analysisInputPort.analysisObjective;
        }

        perform receiveAnalysisResults {
            in analysisTask = analysisInputPort.analysisTask;
            in analysisPlan = analysisInputPort.analysisPlan;
            in qualityEvaluation = analysisInputPort.qualityEvaluation;
        }
    }

    interface userToVehicle connect user.qualityInputPort to vehicle.vehicleQualityPort;
    interface userToEngine connect user.qualityInputPort to vehicle.engine.qualityDataPort;
    interface userToTransmission connect user.qualityInputPort to vehicle.transmission.qualityDataPort;
    interface userToFrontAxle connect user.qualityInputPort to vehicle.frontAxleAssembly.qualityDataPort;
    interface userToRearAxle connect user.qualityInputPort to vehicle.rearAxleAssembly.qualityDataPort;
    interface userToAnalysisSystem connect user.analysisInputPort to qualityAnalysisSystem.analysisPort;

    action analyzeVehicleQuality {
        in analysisRequirement: AnalysisRequirement;
        in analysisObjective: AnalysisObjective;
        out analysisTask: AnalysisTask;
        out analysisPlan: AnalysisPlan;
        out qualityEvaluation: QualityEvaluation;
    }
}

package 'PowertrainOptimization' {
    attribute def PowerOutput;
    attribute def Mass;
    attribute def Efficiency;
    attribute def Reliability;
    attribute def Cost;
    attribute def EngineConfig;
    attribute def OptimalSolution;
    attribute def PerformanceData;
    attribute def TradeOffResult;

    port def ConfigRequestPort {
        out configRequest : EngineConfig;
    }

    port def PerformanceDataPort {
        out performanceData : PerformanceData;
    }

    port def OptimalSolutionPort {
        out optimalSolution : OptimalSolution;
    }

    port def TradeOffResultPort {
        out tradeOffResult : TradeOffResult;
    }

    part context {
        part user {
            port configRequestPort : ConfigRequestPort;
            perform optimizePowertrain.requestOptimization {
                out configRequest = configRequestPort.configRequest;
            }
        }

        interface userToSystem connect user.configRequestPort to optimizationSystem.configRequestPort {
            ref flow references optimizePowertrain.configFlow
                from source.configRequest to target.configRequest;
        }

        part optimizationSystem {
            port configRequestPort : ~ConfigRequestPort;
            port performanceDataPort : PerformanceDataPort;
            port tradeOffResultPort : TradeOffResultPort;
            port optimalSolutionPort : OptimalSolutionPort;

            perform optimizePowertrain.modelEngineStructures {
                in configRequest = configRequestPort.configRequest;
                out performanceData = performanceDataPort.performanceData;
            }

            perform optimizePowertrain.conductTradeOffAnalysis {
                in performanceData = performanceDataPort.performanceData;
                out tradeOffResult = tradeOffResultPort.tradeOffResult;
            }

            perform optimizePowertrain.recommendOptimalSolution {
                in tradeOffResult = tradeOffResultPort.tradeOffResult;
                out optimalSolution = optimalSolutionPort.optimalSolution;
            }
        }

        interface systemToEngineModeler connect optimizationSystem.performanceDataPort to engineModeler.performanceDataPort {
            ref flow references optimizePowertrain.performanceFlow
                from source.performanceData to target.performanceData;
        }

        part engineModeler {
            port performanceDataPort : ~PerformanceDataPort;

            part fourCylinderEngine {
                attribute power : PowerOutput;
                attribute mass : Mass;
                attribute efficiency : Efficiency;
                attribute reliability : Reliability;
                attribute cost : Cost;
            }

            part sixCylinderEngine {
                attribute power : PowerOutput;
                attribute mass : Mass;
                attribute efficiency : Efficiency;
                attribute reliability : Reliability;
                attribute cost : Cost;
            }
        }

        interface systemToAnalyzer connect optimizationSystem.tradeOffResultPort to tradeOffAnalyzer.tradeOffResultPort {
            ref flow references optimizePowertrain.tradeOffFlow
                from source.tradeOffResult to target.tradeOffResult;
        }

        part tradeOffAnalyzer {
            port tradeOffResultPort : ~TradeOffResultPort;
        }
    }

    action optimizePowertrain {
        action requestOptimization { out configRequest : EngineConfig; }
        succession flow configFlow from requestOptimization.configRequest to modelEngineStructures.configRequest;
        action modelEngineStructures { in configRequest; out performanceData : PerformanceData; }
        succession flow performanceFlow from modelEngineStructures.performanceData to conductTradeOffAnalysis.performanceData;
        action conductTradeOffAnalysis { in performanceData; out tradeOffResult : TradeOffResult; }
        succession flow tradeOffFlow from conductTradeOffAnalysis.tradeOffResult to recommendOptimalSolution.tradeOffResult;
        action recommendOptimalSolution { in tradeOffResult; out optimalSolution : OptimalSolution; }
    }
}

package 'FuelEconomySystem' {
    import ISQ::*;
    import SI::*;
    import USCustomaryUnits::*;

    attribute def FuelVolume :> ScalarQuantityValue {
        attribute redefines num: Real;
        attribute redefines mRef: VolumeUnit;
    }

    attribute def Distance :> ScalarQuantityValue {
        attribute redefines num: Real;
        attribute redefines mRef: LengthUnit;
    }

    attribute def Mass :> ScalarQuantityValue {
        attribute redefines num: Real;
        attribute redefines mRef: MassUnit;
    }

    attribute def FuelEconomy :> ScalarQuantityValue {
        attribute redefines num: Real;
        attribute redefines mRef: LengthUnit / VolumeUnit;
    }

    attribute def DrivingCondition {
        attribute scenario: String;
    }

    port def FuelConsumptionPort {
        in fuelVolume: FuelVolume;
        in distance: Distance;
        out fuelEconomy: FuelEconomy;
    }

    port def LoadPort {
        in additionalLoad: Mass;
    }

    port def DrivingConditionPort {
        in condition: DrivingCondition;
    }

    requirement def FuelEconomyRequirement {
        doc /* The vehicle fuel economy must meet regulatory requirements */

        attribute urbanMinimum: FuelEconomy = 25 [mi/gal];
        attribute highwayMinimum: FuelEconomy = 30 [mi/gal];
        attribute testLoad: Mass = 1000 [lb];

        requirement urbanFuelEconomy {
            doc /* Under urban driving conditions, range per gallon must not be less than 25 miles */
            assume constraint { condition.scenario == "urban" }
            require constraint { measuredFuelEconomy >= urbanMinimum }
        }

        requirement highwayFuelEconomy {
            doc /* Under highway driving conditions, range per gallon must not be less than 30 miles */
            assume constraint { condition.scenario == "highway" }
            require constraint { measuredFuelEconomy >= highwayMinimum }
        }

        attribute condition: DrivingCondition;
        attribute measuredFuelEconomy: FuelEconomy;
    }

    part vehicle {
        port fuelPort: FuelConsumptionPort;
        port loadPort: LoadPort;
        port conditionPort: DrivingConditionPort;

        part powertrain {
            perform fuelEconomyAnalysis.consumeFuel {
                in fuelVolume = fuelPort.fuelVolume;
                in distance = fuelPort.distance;
                in load = loadPort.additionalLoad;
                in condition = conditionPort.condition;
                out fuelEconomy = fuelPort.fuelEconomy;
            }
        }

        satisfy req: FuelEconomyRequirement {
            attribute redefines measuredFuelEconomy = fuelPort.fuelEconomy;
            attribute redefines condition = conditionPort.condition;
        }
    }

    part testEnvironment {
        port fuelPort: ~FuelConsumptionPort;
        port loadPort: ~LoadPort;
        port conditionPort: ~DrivingConditionPort;

        perform fuelEconomyAnalysis.setupTest {
            out load = loadPort.additionalLoad;
            out condition = conditionPort.condition;
        }

        perform fuelEconomyAnalysis.measureConsumption {
            out fuelVolume = fuelPort.fuelVolume;
            out distance = fuelPort.distance;
            in fuelEconomy = fuelPort.fuelEconomy;
        }
    }

    interface vehicleToTest connect vehicle.fuelPort to testEnvironment.fuelPort;
    interface vehicleLoadInterface connect vehicle.loadPort to testEnvironment.loadPort;
    interface vehicleConditionInterface connect vehicle.conditionPort to testEnvironment.conditionPort;

    action fuelEconomyAnalysis {
        action setupTest {
            out load: Mass;
            out condition: DrivingCondition;
        }

        action measureConsumption {
            out fuelVolume: FuelVolume;
            out distance: Distance;
            in fuelEconomy: FuelEconomy;
        }

        action consumeFuel {
            in fuelVolume: FuelVolume;
            in distance: Distance;
            in load: Mass;
            in condition: DrivingCondition;
            out fuelEconomy: FuelEconomy;
        }

        flow from setupTest.load to consumeFuel.load;
        flow from setupTest.condition to consumeFuel.condition;
        flow from measureConsumption.fuelVolume to consumeFuel.fuelVolume;
        flow from measureConsumption.distance to consumeFuel.distance;
        flow from consumeFuel.fuelEconomy to measureConsumption.fuelEconomy;
    }
}

package 'VehicleDynamicsSimulation' {
	attribute def Mass;
	attribute def Position;
	attribute def Velocity;
	attribute def Power;
	attribute def TimeStep;
	attribute def Acceleration;
	attribute def Time;

	port def SimulationInputPort {
		in mass : Mass;
		in initialPosition : Position;
		in initialVelocity : Velocity;
		in powerData : Power[*];
		in timeStep : TimeStep;
	}

	port def SimulationOutputPort {
		out accelerationSequence : Acceleration[*];
		out velocitySequence : Velocity[*];
		out positionSequence : Position[*];
	}

	part context {
		part user {
			port simulationInputPort : SimulationInputPort;
			perform analyzeVehicleDynamics.provideInputData {
				out mass = simulationInputPort.mass;
				out initialPosition = simulationInputPort.initialPosition;
				out initialVelocity = simulationInputPort.initialVelocity;
				out powerData = simulationInputPort.powerData;
				out timeStep = simulationInputPort.timeStep;
			}
		}

		interface userToSimulator connect user.simulationInputPort to simulator.simulationInputPort {
			ref flow references analyzeVehicleDynamics.inputDataFlow
				from source.mass to target.mass;
			ref flow references analyzeVehicleDynamics.inputDataFlow
				from source.initialPosition to target.initialPosition;
			ref flow references analyzeVehicleDynamics.inputDataFlow
				from source.initialVelocity to target.initialVelocity;
			ref flow references analyzeVehicleDynamics.inputDataFlow
				from source.powerData to target.powerData;
			ref flow references analyzeVehicleDynamics.inputDataFlow
				from source.timeStep to target.timeStep;
		}

		part simulator {
			port simulationInputPort : ~SimulationInputPort;
			port simulationOutputPort : SimulationOutputPort;

			perform analyzeVehicleDynamics.computeDynamics {
				in mass = simulationInputPort.mass;
				in initialPosition = simulationInputPort.initialPosition;
				in initialVelocity = simulationInputPort.initialVelocity;
				in powerData = simulationInputPort.powerData;
				in timeStep = simulationInputPort.timeStep;
				out accelerationSequence = simulationOutputPort.accelerationSequence;
				out velocitySequence = simulationOutputPort.velocitySequence;
				out positionSequence = simulationOutputPort.positionSequence;
			}
		}
	}

	action analyzeVehicleDynamics {
		action provideInputData {
			out mass : Mass;
			out initialPosition : Position;
			out initialVelocity : Velocity;
			out powerData : Power[*];
			out timeStep : TimeStep;
		}

		succession flow inputDataFlow from provideInputData to computeDynamics;

		action computeDynamics {
			in mass : Mass;
			in initialPosition : Position;
			in initialVelocity : Velocity;
			in powerData : Power[*];
			in timeStep : TimeStep;
			out accelerationSequence : Acceleration[*];
			out velocitySequence : Velocity[*];
			out positionSequence : Position[*];
		}
	}
}

