package 'VehicleSafetySecuritySystem' {
    attribute def SafetyFeature;
    attribute def SecurityFeature;
    attribute def ComponentInfo;
    attribute def FilterCriteria;
    attribute def DisplayFormat;

    enum def SafetyType {
        mandatory;
        optional;
    }

    enum def DisplayType {
        tree;
        table;
    }

    port def ComponentInfoPort {
        out componentInfo: ComponentInfo;
    }

    port def FilterPort {
        in filterCriteria: FilterCriteria;
    }

    port def DisplayPort {
        out displayFormat: DisplayFormat;
    }

    part def VehicleComponent {
        attribute name: String;
        attribute isSafety: Boolean;
        attribute isSecurity: Boolean;
        attribute safetyType: SafetyType[0..1];
    }

    part def SeatBelt :> VehicleComponent {
        attribute redefines isSafety = true;
        attribute redefines safetyType = SafetyType::mandatory;
    }

    part def Airbag :> VehicleComponent {
        attribute redefines isSafety = true;
        attribute redefines safetyType = SafetyType::optional;
    }

    part def Bumper :> VehicleComponent {
        attribute redefines isSafety = true;
        attribute redefines safetyType = SafetyType::mandatory;
    }

    part def ABS :> VehicleComponent {
        attribute redefines isSafety = true;
        attribute redefines safetyType = SafetyType::optional;
    }

    part def Alarm :> VehicleComponent {
        attribute redefines isSecurity = true;
    }

    part def KeylessEntry :> VehicleComponent {
        attribute redefines isSecurity = true;
    }

    part context {
        part user {
            port filterPort: FilterPort;
            port displayPort: ~DisplayPort;
            perform manageSafetySecurityDisplay.requestFiltering {
                out filterCriteria = filterPort.filterCriteria;
            }
            perform manageSafetySecurityDisplay.viewDisplay {
                in displayFormat = displayPort.displayFormat;
            }
        }

        interface userToSystem connect user.filterPort to safetySecuritySystem.filterPort;
        interface systemToUser connect safetySecuritySystem.displayPort to user.displayPort;

        part vehicle {
            part seatBelts: SeatBelt[1..*];
            part airbags: Airbag[0..*];
            part bumpers: Bumper[1..*];
            part abs: ABS[0..1];
            part alarms: Alarm[0..*];
            part keylessEntries: KeylessEntry[0..*];

            port componentPort: ComponentInfoPort;
        }

        interface vehicleToSystem connect vehicle.componentPort to safetySecuritySystem.componentPort;

        part safetySecuritySystem {
            port filterPort: ~FilterPort;
            port componentPort: ~ComponentInfoPort;
            port displayPort: DisplayPort;

            perform manageSafetySecurityDisplay.filterComponents {
                in filterCriteria = filterPort.filterCriteria;
                in componentInfo = componentPort.componentInfo;
            }

            perform manageSafetySecurityDisplay.organizeComponents {
                in item filterResult;
                out item organizedData;
            }

            perform manageSafetySecurityDisplay.visualizeComponents {
                in item organizedData;
                out displayFormat = displayPort.displayFormat;
            }
        }
    }

    action manageSafetySecurityDisplay {
        action requestFiltering { out filterCriteria: FilterCriteria; }
        action filterComponents { in filterCriteria: FilterCriteria; in componentInfo: ComponentInfo; out filterResult; }
        succession flow filterFlow from requestFiltering.filterCriteria to filterComponents.filterCriteria;
        action organizeComponents { in filterResult; out organizedData; }
        succession flow organizeFlow from filterComponents.filterResult to organizeComponents.filterResult;
        action visualizeComponents { in organizedData; out displayFormat: DisplayFormat; }
        succession flow visualizeFlow from organizeComponents.organizedData to visualizeComponents.organizedData;
        action viewDisplay { in displayFormat: DisplayFormat; }
        succession flow displayFlow from visualizeComponents.displayFormat to viewDisplay.displayFormat;
    }
}

package 'LayeredSystem' {
	attribute def X;
	attribute def Y;
	attribute def Z;
	attribute def Request;
	attribute def Response;
	attribute def Data;

	port def RequestPort {
		out request: Request;
	}
	port def ResponsePort {
		in response: Response;
	}
	port def ServiceRequestPort {
		out serviceRequest: Request;
	}
	port def ServiceResponsePort {
		in serviceResponse: Response;
	}
	port def DataRequestPort {
		out dataRequest: Request;
	}
	port def DataResponsePort {
		in dataResponse: Response;
	}

	part system {
		attribute x: X;
		attribute y: Y;
		attribute z: Z {
			:>> z = derive(x, y);
		}

		part applicationLayer {
			port requestPort: RequestPort;
			port responsePort: ResponsePort;
			perform processFlow.applicationProcess {
				out request = requestPort.request;
				in response = responsePort.response;
			}
		}

		interface appToService connect applicationLayer.requestPort to serviceLayer.serviceRequestPort {
			ref flow references processFlow.appToServiceFlow
				from source.request to target.serviceRequest;
		}

		interface serviceToApp connect serviceLayer.serviceResponsePort to applicationLayer.responsePort {
			ref flow references processFlow.serviceToAppFlow
				from source.serviceResponse to target.response;
		}

		part serviceLayer {
			port serviceRequestPort: ~ServiceRequestPort;
			port serviceResponsePort: ServiceResponsePort;
			port dataRequestPort: DataRequestPort;
			port dataResponsePort: DataResponsePort;
			perform processFlow.serviceProcess {
				in serviceRequest = serviceRequestPort.serviceRequest;
				out serviceResponse = serviceResponsePort.serviceResponse;
				out dataRequest = dataRequestPort.dataRequest;
				in dataResponse = dataResponsePort.dataResponse;
			}
		}

		interface serviceToData connect serviceLayer.dataRequestPort to dataLayer.dataRequestPort {
			ref flow references processFlow.serviceToDataFlow
				from source.dataRequest to target.dataRequest;
		}

		interface dataToService connect dataLayer.dataResponsePort to serviceLayer.dataResponsePort {
			ref flow references processFlow.dataToServiceFlow
				from source.dataResponse to target.dataResponse;
		}

		part dataLayer {
			port dataRequestPort: ~DataRequestPort;
			port dataResponsePort: DataResponsePort;
			perform processFlow.dataProcess {
				in dataRequest = dataRequestPort.dataRequest;
				out dataResponse = dataResponsePort.dataResponse;
			}
		}
	}

	action processFlow {
		action applicationProcess { out request: Request; in response: Response; }
		succession flow appToServiceFlow from applicationProcess.request to serviceProcess.serviceRequest;
		action serviceProcess { 
			in serviceRequest: Request; 
			out serviceResponse: Response;
			out dataRequest: Request;
			in dataResponse: Response;
		}
		succession flow serviceToDataFlow from serviceProcess.dataRequest to dataProcess.dataRequest;
		action dataProcess { in dataRequest: Request; out dataResponse: Response; }
		succession flow dataToServiceFlow from dataProcess.dataResponse to serviceProcess.dataResponse;
		succession flow serviceToAppFlow from serviceProcess.serviceResponse to applicationProcess.response;
	}

	calc def derive(x: X, y: Y): Z;
}

package 'PowertrainSystem' {
	attribute def Torque :> ISQ::TorqueValue;
	attribute def PowerCommand;

	port def PowerCommandPort {
		in powerCommand : PowerCommand;
	}

	port def TorquePort {
		out torque : Torque;
	}

	part def TorqueGenerator {
		port powerCommandPort : PowerCommandPort;
		port torquePort : TorquePort;

		perform generateTorque {
			in powerCommand = powerCommandPort.powerCommand;
			out torque = torquePort.torque;
		}
	}

	part def Engine :> TorqueGenerator;

	part powertrainContext {
		part controller {
			port powerCommandPort : ~PowerCommandPort;
			perform torqueGeneration.sendPowerCommand {
				out powerCommand = powerCommandPort.powerCommand;
			}
		}

		interface controllerToPowertrain connect controller.powerCommandPort to powertrain.powerCommandPort {
			ref flow references torqueGeneration.powerCommandFlow
				from source.powerCommand to target.powerCommand;
		}

		part powertrain : Engine {
			port powerCommandPort : PowerCommandPort;
			port torquePort : TorquePort;

			perform torqueGeneration.generateTorque {
				in powerCommand = powerCommandPort.powerCommand;
				out torque = torquePort.torque;
				assert constraint { torque > 0 [N*m] }
			}
		}

		part drivetrain {
			port torquePort : ~TorquePort;
			perform torqueGeneration.transmitTorque {
				in torque = torquePort.torque;
			}
		}

		interface powertrainToDrivetrain connect powertrain.torquePort to drivetrain.torquePort {
			ref flow references torqueGeneration.torqueFlow
				from source.torque to target.torque;
		}
	}

	action torqueGeneration {
		action sendPowerCommand { out powerCommand : PowerCommand; }
		succession flow powerCommandFlow from sendPowerCommand.powerCommand to generateTorque.powerCommand;
		action generateTorque { in powerCommand : PowerCommand; out torque : Torque; }
		succession flow torqueFlow from generateTorque.torque to transmitTorque.torque;
		action transmitTorque { in torque : Torque; }
	}
}

package 'PowerSystem' {
	attribute def Power;
	attribute def Torque;

	port def PowerPort {
		out power: Power;
	}

	port def TorquePort {
		out torque: Torque;
	}

	part def System {
		port powerPort: PowerPort;
		port torquePort: TorquePort;

		perform providePower {
			out power = powerPort.power;
		}

		perform generateTorque {
			out torque = torquePort.torque;
		}

		part torqueGenerator {
			perform generateTorque :>> System.generateTorque;
		}

		part powertrain {
			part engine {
				perform generateTorque :>> System.torqueGenerator.generateTorque;
			}
		}
	}
}

