package 'FuelSystem' {
	attribute def Fuel;
	attribute def FuelTemperature;

	port def FuelSupplyPort {
		out fuel: Fuel;
	}

	port def FuelReturnPort {
		in fuel: Fuel;
	}

	port def FuelTemperaturePort {
		out temperature: FuelTemperature;
	}

	part vehicle {
		part fuelTankAssembly {
			part fuelTank {
				port supplyPort: FuelSupplyPort;
				port returnPort: FuelReturnPort;
				perform manageFuel.storeFuel {
					out fuel = supplyPort.fuel;
					in returnFuel = returnPort.fuel;
				}
			}

			part fuelPump {
				port intakePort: ~FuelSupplyPort;
				port deliveryPort: FuelSupplyPort;
				port temperaturePort: FuelTemperaturePort;
				perform manageFuel.pressurizeFuel {
					in fuel = intakePort.fuel;
					out pressurizedFuel = deliveryPort.fuel;
					out temperature = temperaturePort.temperature;
				}
			}

			interface tankToPump connect fuelTank.supplyPort to fuelPump.intakePort {
				ref flow references manageFuel.tankToPumpFlow
					from source.fuel to target.fuel;
			}
		}

		part fuelConsumingUnits {
			port supplyPort: ~FuelSupplyPort;
			port returnPort: FuelReturnPort;
			perform manageFuel.consumeFuel {
				in fuel = supplyPort.fuel;
				out unusedFuel = returnPort.fuel;
			}
		}

		interface pumpToConsumers connect fuelTankAssembly.fuelPump.deliveryPort to fuelConsumingUnits.supplyPort {
			ref flow references manageFuel.pumpToConsumersFlow
				from source.fuel to target.fuel;
		}

		interface consumersToTank connect fuelConsumingUnits.returnPort to fuelTankAssembly.fuelTank.returnPort {
			ref flow references manageFuel.returnFlow
				from source.fuel to target.fuel;
		}
	}

	action manageFuel {
		action storeFuel { out fuel: Fuel; in returnFuel: Fuel; }
		succession flow tankToPumpFlow from storeFuel.fuel to pressurizeFuel.fuel;
		action pressurizeFuel { in fuel: Fuel; out pressurizedFuel: Fuel; out temperature: FuelTemperature; }
		succession flow pumpToConsumersFlow from pressurizeFuel.pressurizedFuel to consumeFuel.fuel;
		action consumeFuel { in fuel: Fuel; out unusedFuel: Fuel; }
		succession flow returnFlow from consumeFuel.unusedFuel to storeFuel.returnFuel;
	}
}

