package 'SecurityClassificationSystem' {
	attribute def ClassificationLevel;
	attribute def ComponentID;
	attribute def ClassificationAttribute;

	port def ComponentPort {
		in componentID : ComponentID;
		out classificationLevel : ClassificationLevel;
		out classificationAttribute : ClassificationAttribute;
	}

	port def ClassificationPort {
		in componentID : ComponentID;
		in classificationLevel : ClassificationLevel;
		out classificationAttribute : ClassificationAttribute;
	}

	part context {
		part component {
			port componentPort : ComponentPort;
			perform classifyAndLabel.provideComponent {
				out componentID = componentPort.componentID;
				in classificationLevel = componentPort.classificationLevel;
				in classificationAttribute = componentPort.classificationAttribute;
			}
		}

		interface componentToClassifier connect component.componentPort to classifier.classificationPort {
			ref flow references classifyAndLabel.componentFlow
				from source.componentID to target.componentID;
			ref flow references classifyAndLabel.levelFlow
				from target.classificationLevel to source.classificationLevel;
			ref flow references classifyAndLabel.attributeFlow
				from target.classificationAttribute to source.classificationAttribute;
		}

		part classifier {
			port classificationPort : ~ClassificationPort;
			perform classifyAndLabel.assignClassification {
				in componentID = classificationPort.componentID;
				out classificationLevel = classificationPort.classificationLevel;
				out classificationAttribute = classificationPort.classificationAttribute;
			}
		}
	}

	action classifyAndLabel {
		action provideComponent { 
			out componentID : ComponentID; 
			in classificationLevel : ClassificationLevel;
			in classificationAttribute : ClassificationAttribute;
		}
		succession flow componentFlow from provideComponent.componentID to assignClassification.componentID;
		action assignClassification { 
			in componentID : ComponentID; 
			out classificationLevel : ClassificationLevel;
			out classificationAttribute : ClassificationAttribute;
		}
		succession flow levelFlow from assignClassification.classificationLevel to provideComponent.classificationLevel;
		succession flow attributeFlow from assignClassification.classificationAttribute to provideComponent.classificationAttribute;
	}
}

package 'AutomotiveECUSystem' {
    attribute def ControlSignal;
    attribute def EngineStatus;
    attribute def VehicleStatus;
    attribute def CANMessage;
    attribute def SensorData;

    port def ControlSignalPort {
        out controlSignal: ControlSignal;
    }

    port def EngineStatusPort {
        out engineStatus: EngineStatus;
    }

    port def VehicleStatusPort {
        out vehicleStatus: VehicleStatus;
    }

    port def CANMessagePort {
        inout canMessage: CANMessage;
    }

    port def SensorDataPort {
        in sensorData: SensorData;
    }

    part def BusInterface {
        port systemPort: CANMessagePort;
        port busPort: CANMessagePort;
    }

    part vehicleControlSystem {
        part vcu {
            port controlPort: ControlSignalPort;
            port statusPort: VehicleStatusPort;
            port canPort: CANMessagePort;
            port sensorPort: SensorDataPort;

            part vcuBusIF: BusInterface {
                bind systemPort = canPort;
            }

            perform vehicleOperation.monitorVehicle {
                in sensorData = sensorPort.sensorData;
                out vehicleStatus = statusPort.vehicleStatus;
                out controlSignal = controlPort.controlSignal;
                inout canMessage = canPort.canMessage;
            }
        }

        part ecu {
            port controlPort: ~ControlSignalPort;
            port statusPort: EngineStatusPort;
            port canPort: CANMessagePort;
            port sensorPort: SensorDataPort;

            part ecuBusIF: BusInterface {
                bind systemPort = canPort;
            }

            perform vehicleOperation.controlEngine {
                in controlSignal = controlPort.controlSignal;
                in sensorData = sensorPort.sensorData;
                out engineStatus = statusPort.engineStatus;
                inout canMessage = canPort.canMessage;
            }
        }

        part canBus {
            port vcuPort: CANMessagePort;
            port ecuPort: CANMessagePort;
        }

        interface vcuToCanBus connect vcu.vcuBusIF.busPort to canBus.vcuPort;
        interface ecuToCanBus connect ecu.ecuBusIF.busPort to canBus.ecuPort;
        interface canBusComm connect canBus.vcuPort to canBus.ecuPort;
    }

    action vehicleOperation {
        action monitorVehicle {
            in sensorData: SensorData;
            out vehicleStatus: VehicleStatus;
            out controlSignal: ControlSignal;
            inout canMessage: CANMessage;
        }

        action controlEngine {
            in controlSignal: ControlSignal;
            in sensorData: SensorData;
            out engineStatus: EngineStatus;
            inout canMessage: CANMessage;
        }

        flow from monitorVehicle.canMessage to controlEngine.canMessage;
        flow from controlEngine.canMessage to monitorVehicle.canMessage;
    }
}

package 'BloodGlucoseMeter' {
	attribute def BatteryStatus;
	attribute def Alert;
	attribute def GlucoseReading;
	attribute def MeasurementCommand;

	port def BatteryStatusPort {
		out batteryStatus: BatteryStatus;
	}
	port def AlertPort {
		out alert: Alert;
	}
	port def GlucoseReadingPort {
		out glucoseReading: GlucoseReading;
	}
	port def MeasurementCommandPort {
		out measurementCommand: MeasurementCommand;
	}

	part context {
		part battery {
			port batteryStatusPort: BatteryStatusPort;
			perform ensureReliableOperation.provideBatteryStatus {
				out batteryStatus = batteryStatusPort.batteryStatus;
			}
		}

		interface batteryToMonitor connect battery.batteryStatusPort to batteryMonitor.batteryStatusPort {
			ref flow references ensureReliableOperation.batteryStatusFlow
				from source.batteryStatus to target.batteryStatus;
		}

		part batteryMonitor {
			port batteryStatusPort: ~BatteryStatusPort;
			port alertPort: AlertPort;
			perform ensureReliableOperation.detectBatteryIssues {
				in batteryStatus = batteryStatusPort.batteryStatus;
				out alert = alertPort.alert;
			}
		}

		interface monitorToUser connect batteryMonitor.alertPort to user.alertPort {
			ref flow references ensureReliableOperation.alertFlow
				from source.alert to target.alert;
		}

		part user {
			port alertPort: ~AlertPort;
			port measurementCommandPort: MeasurementCommandPort;
			perform ensureReliableOperation.receiveAlert {
				in alert = alertPort.alert;
			}
			perform ensureReliableOperation.requestMeasurement {
				out measurementCommand = measurementCommandPort.measurementCommand;
			}
		}

		interface userToMeasurementSystem connect user.measurementCommandPort to measurementSystem.measurementCommandPort {
			ref flow references ensureReliableOperation.measurementCommandFlow
				from source.measurementCommand to target.measurementCommand;
		}

		part measurementSystem {
			port measurementCommandPort: ~MeasurementCommandPort;
			port glucoseReadingPort: GlucoseReadingPort;
			port emergencyAlertPort: AlertPort;
			perform ensureReliableOperation.measureGlucose {
				in measurementCommand = measurementCommandPort.measurementCommand;
				out glucoseReading = glucoseReadingPort.glucoseReading;
			}
			perform ensureReliableOperation.triggerEmergencyResponse {
				out alert = emergencyAlertPort.alert;
			}
		}

		interface measurementToUser connect measurementSystem.glucoseReadingPort to user.glucoseReadingPort {
			ref flow references ensureReliableOperation.glucoseReadingFlow
				from source.glucoseReading to target.glucoseReading;
		}

		part user {
			port glucoseReadingPort: ~GlucoseReadingPort;
			perform ensureReliableOperation.receiveGlucoseReading {
				in glucoseReading = glucoseReadingPort.glucoseReading;
			}
		}
	}

	action ensureReliableOperation {
		action provideBatteryStatus { out batteryStatus: BatteryStatus; }
		succession flow batteryStatusFlow from provideBatteryStatus.batteryStatus to detectBatteryIssues.batteryStatus;
		action detectBatteryIssues { in batteryStatus: BatteryStatus; out alert: Alert; }
		succession flow alertFlow from detectBatteryIssues.alert to receiveAlert.alert;
		action receiveAlert { in alert: Alert; }
		action requestMeasurement { out measurementCommand: MeasurementCommand; }
		succession flow measurementCommandFlow from requestMeasurement.measurementCommand to measureGlucose.measurementCommand;
		action measureGlucose { in measurementCommand: MeasurementCommand; out glucoseReading: GlucoseReading; }
		succession flow glucoseReadingFlow from measureGlucose.glucoseReading to receiveGlucoseReading.glucoseReading;
		action receiveGlucoseReading { in glucoseReading: GlucoseReading; }
		action triggerEmergencyResponse { out alert: Alert; }
	}
}

package 'ScientificConstants' {
    attribute def MathematicalConstant :> Real;
    attribute def PhysicalConstant :> Real;
    attribute def ContextConstant :> Real;

    attribute eulerNumber : MathematicalConstant = 2.71828182845904523536;
    attribute pi : MathematicalConstant = 3.14159265358979323846;

    attribute speedOfLight : PhysicalConstant = 299792458.0;
    attribute fineStructureConstant : PhysicalConstant = 0.0072973525693;
    attribute electronProtonMassRatio : PhysicalConstant = 0.00054386734428;

    attribute earthGravity : ContextConstant = 9.80665;
    attribute modelXAmplifierGain : ContextConstant = 25.0;

    part def ConstantsProvider {
        attribute mathConstants[*] : MathematicalConstant;
        attribute physicalConstants[*] : PhysicalConstant;
        attribute contextConstants[*] : ContextConstant;

        port constantsPort : ConstantsAccessPort;
    }

    port def ConstantsAccessPort {
        out providedConstants : Real[*];
    }

    part scientificComputingSystem {
        part constantsLibrary : ConstantsProvider {
            attribute :>> mathConstants = (eulerNumber, pi);
            attribute :>> physicalConstants = (speedOfLight, fineStructureConstant, electronProtonMassRatio);
            attribute :>> contextConstants = (earthGravity, modelXAmplifierGain);
        }

        part computationModule {
            port constantsPort : ~ConstantsAccessPort;

            perform accessConstants {
                in constants = constantsPort.providedConstants;
            }
        }

        interface constantsInterface connect constantsLibrary.constantsPort to computationModule.constantsPort;
    }
}

