        attribute performanceLevel: PerformanceLevel;
        part engine: Engine[1];
        part transmission: Transmission[1];

        constraint engineSelection {
            if performanceLevel.isHighPerformance then
                engine.engineType == EngineType::sixCylinder
            else
                engine.engineType == EngineType::fourCylinder
        }

        constraint validPowertrainCombination {
            (engine.engineType == EngineType::fourCylinder and 
             transmission.transmissionType == TransmissionType::manual) or
            (engine.engineType == EngineType::sixCylinder and 
             transmission.transmissionType == TransmissionType::automatic)
        }
    }
}

