package 'AutomotiveFuelSystem' {
	attribute def Fuel;
	attribute def Temperature;

	port def FuelSupplyPort {
		out fuel : Fuel;
	}

	port def FuelReturnPort {
		in fuel : Fuel;
	}

	port def TemperaturePort {
		out temperature : Temperature;
	}

	part def FuelTankAssembly {
		port fuelSupplyPort : FuelSupplyPort;
		port fuelReturnPort : FuelReturnPort;
		port temperaturePort : TemperaturePort;

		part fuelPump : FuelPump {
			port tankFuelInPort : ~FuelSupplyPort;
			port engineFuelOutPort : FuelSupplyPort;
			port engineFuelReturnPort : FuelReturnPort;
		}

		part fuelTank : FuelTank {
			port fuelOutPort : FuelSupplyPort;
			port fuelInPort : FuelReturnPort;
		}

		interface tankToPump connect fuelTank.fuelOutPort to fuelPump.tankFuelInPort;
		interface pumpToAssemblySupply connect fuelPump.engineFuelOutPort to fuelSupplyPort;
		interface assemblyReturnToPump connect fuelReturnPort to fuelPump.engineFuelReturnPort;
	}

	part def FuelPump {
		port tankFuelInPort : ~FuelSupplyPort;
		port engineFuelOutPort : FuelSupplyPort;
		port engineFuelReturnPort : FuelReturnPort;

		perform fuelCirculation.pumpFuel {
			in fuel = tankFuelInPort.fuel;
			out fuel = engineFuelOutPort.fuel;
		}

		perform fuelCirculation.receiveReturnFuel {
			in fuel = engineFuelReturnPort.fuel;
		}
	}

	part def FuelTank {
		port fuelOutPort : FuelSupplyPort;
		port fuelInPort : FuelReturnPort;

		perform fuelCirculation.storeFuel {
			out fuel = fuelOutPort.fuel;
			in fuel = fuelInPort.fuel;
		}
	}

	part def Engine {
		port fuelInPort : ~FuelSupplyPort;
		port fuelOutPort : ~FuelReturnPort;

		perform fuelCirculation.consumeFuel {
			in fuel = fuelInPort.fuel;
			out fuel = fuelOutPort.fuel;
		}
	}

	part context {
		part fuelTankAssembly : FuelTankAssembly;
		part engine : Engine;

		interface assemblyToEngine connect fuelTankAssembly.fuelSupplyPort to engine.fuelInPort {
			ref flow references fuelCirculation.supplyFlow
				from source.fuel to target.fuel;
		}

		interface engineToAssembly connect engine.fuelOutPort to fuelTankAssembly.fuelReturnPort {
			ref flow references fuelCirculation.returnFlow
				from source.fuel to target.fuel;
		}
	}

	action fuelCirculation {
		action storeFuel { out fuel : Fuel; in fuel : Fuel; }
		action pumpFuel { in fuel : Fuel; out fuel : Fuel; }
		succession flow supplyFlow from pumpFuel.fuel to consumeFuel.fuel;
		action consumeFuel { in fuel : Fuel; out fuel : Fuel; }
		succession flow returnFlow from consumeFuel.fuel to receiveReturnFuel.fuel;
		action receiveReturnFuel { in fuel : Fuel; }
	}
}

package 'PhysicalQuantityManagementSystem' {
    import ISQ::*;

    attribute def PhysicalQuantityData;
    attribute def UnitData;
    attribute def ConversionRequest;
    attribute def ConversionResult;

    port def QuantityDataPort {
        in quantityData: PhysicalQuantityData;
        out processedData: PhysicalQuantityData;
    }

    port def UnitConversionPort {
        in conversionRequest: ConversionRequest;
        out conversionResult: ConversionResult;
    }

    part def PhysicalQuantityManager {
        attribute quantities: PhysicalQuantityData[*];
        attribute units: UnitData[*];

        port quantityPort: QuantityDataPort;
        port conversionPort: UnitConversionPort;

        perform manageQuantities {
            in data = quantityPort.quantityData;
            out processed = quantityPort.processedData;
        }

        perform convertUnits {
            in request = conversionPort.conversionRequest;
            out result = conversionPort.conversionResult;
        }
    }

    part context {
        part user {
            port quantityDataPort: ~QuantityDataPort;
            port unitConversionPort: ~UnitConversionPort;

            perform quantityManagement.inputQuantityData {
                out quantityData = quantityDataPort.quantityData;
            }

            perform quantityManagement.requestConversion {
                out conversionRequest = unitConversionPort.conversionRequest;
            }
        }

        part quantitySystem: PhysicalQuantityManager {
            port quantityPort: QuantityDataPort;
            port conversionPort: UnitConversionPort;

            perform quantityManagement.processQuantityData {
                in quantityData = quantityPort.quantityData;
                out processedData = quantityPort.processedData;
            }

            perform quantityManagement.performConversion {
                in conversionRequest = conversionPort.conversionRequest;
                out conversionResult = conversionPort.conversionResult;
            }
        }

        interface userToSystem connect user.quantityDataPort to quantitySystem.quantityPort {
            ref flow references quantityManagement.quantityDataFlow
                from source.quantityData to target.quantityData;
            ref flow references quantityManagement.processedDataFlow
                from target.processedData to source.processedData;
        }

        interface userToSystemConversion connect user.unitConversionPort to quantitySystem.conversionPort {
            ref flow references quantityManagement.conversionRequestFlow
                from source.conversionRequest to target.conversionRequest;
            ref flow references quantityManagement.conversionResultFlow
                from target.conversionResult to source.conversionResult;
        }
    }

    action quantityManagement {
        action inputQuantityData { out quantityData: PhysicalQuantityData; }
        succession flow quantityDataFlow from inputQuantityData.quantityData to processQuantityData.quantityData;
        action processQuantityData { in quantityData: PhysicalQuantityData; out processedData: PhysicalQuantityData; }
        succession flow processedDataFlow from processQuantityData.processedData to inputQuantityData;

        action requestConversion { out conversionRequest: ConversionRequest; }
        succession flow conversionRequestFlow from requestConversion.conversionRequest to performConversion.conversionRequest;
        action performConversion { in conversionRequest: ConversionRequest; out conversionResult: ConversionResult; }
        succession flow conversionResultFlow from performConversion.conversionResult to requestConversion;
    }
}

package 'UnitSystemLibrary' {
    attribute def Quantity;
    attribute def Unit;
    attribute def ConversionFactor;

    port def QuantityPort {
        inout quantity : Quantity;
    }

    port def UnitPort {
        out unit : Unit;
    }

    port def ConversionPort {
        in sourceUnit : Unit;
        in targetUnit : Unit;
        out conversionFactor : ConversionFactor;
    }

    part context {
        part user {
            port quantityPort : QuantityPort;
            port unitRequestPort : UnitPort;

            perform unitManagement.requestUnitConversion {
                out quantity = quantityPort.quantity;
                in unit = unitRequestPort.unit;
            }
        }

        interface userToUnitSystem connect user.quantityPort to unitSystem.quantityPort {
            ref flow references unitManagement.quantityFlow
                from source.quantity to target.quantity;
        }

        interface userToUnitSystem2 connect user.unitRequestPort to unitSystem.unitProviderPort {
            ref flow references unitManagement.unitFlow
                from target.unit to source.unit;
        }

        part unitSystem {
            port quantityPort : ~QuantityPort;
            port unitProviderPort : ~UnitPort;
            port isqPort : ConversionPort;
            port usCustomaryPort : ConversionPort;

            perform unitManagement.processUnitRequest {
                inout quantity = quantityPort.quantity;
                out unit = unitProviderPort.unit;
            }

            perform unitManagement.convertUnits {
                in sourceUnit = isqPort.sourceUnit;
                in targetUnit = usCustomaryPort.targetUnit;
                out conversionFactor;
            }
        }

        part isqLibrary {
            port conversionPort : ~ConversionPort;

            perform unitManagement.provideISQUnits {
                out unit = conversionPort.sourceUnit;
            }
        }

        part usCustomaryLibrary {
            port conversionPort : ~ConversionPort;

            perform unitManagement.provideUSCustomaryUnits {
                out unit = conversionPort.targetUnit;
            }
        }

        interface systemToISQ connect unitSystem.isqPort to isqLibrary.conversionPort;
        interface systemToUSCustomary connect unitSystem.usCustomaryPort to usCustomaryLibrary.conversionPort;
    }

    action unitManagement {
        action requestUnitConversion { out quantity : Quantity; in unit : Unit; }
        succession flow quantityFlow from requestUnitConversion.quantity to processUnitRequest.quantity;
        succession flow unitFlow from processUnitRequest.unit to requestUnitConversion.unit;

        action processUnitRequest { inout quantity : Quantity; out unit : Unit; }
        succession flow processFlow from processUnitRequest to convertUnits;

        action convertUnits { in sourceUnit : Unit; in targetUnit : Unit; out conversionFactor : ConversionFactor; }
        flow from provideISQUnits.unit to convertUnits.sourceUnit;
        flow from provideUSCustomaryUnits.unit to convertUnits.targetUnit;

        action provideISQUnits { out unit : Unit; }
        action provideUSCustomaryUnits { out unit : Unit; }
    }
}

package 'HeadlampTiltValidation' {
	import ISQ::*;
	import SI::*;
	import ScalarValues::*;

	attribute def TiltAngle :> ScalarQuantityValue {
		attribute quantityDimension = ISQ::angle;
		attribute measurementUnit = SI::degree;
	}

	attribute def ValidationResult {
		attribute isValid: Boolean;
		attribute measuredAngle: TiltAngle;
	}

	port def TiltAnglePort {
		in tiltAngle: TiltAngle;
	}

	port def ValidationResultPort {
		out validationResult: ValidationResult;
	}

	part headlampTiltValidationSystem {
		attribute minValidAngle: TiltAngle = 50 [SI::degree];
		attribute maxValidAngle: TiltAngle = 80 [SI::degree];

		port tiltAnglePort: TiltAnglePort;
		port validationResultPort: ValidationResultPort;

		perform validateTiltAngle {
			in tiltAngle = tiltAnglePort.tiltAngle;
			out validationResult = validationResultPort.validationResult;
		}
	}

	action validateTiltAngle {
		in tiltAngle: TiltAngle;
		out validationResult: ValidationResult;

		bind validationResult.measuredAngle = tiltAngle;
		bind validationResult.isValid = (tiltAngle >= 50 [SI::degree] and tiltAngle <= 80 [SI::degree]);
	}
}

package 'DataManagementSystem' {
    import ScalarValues::*;

    attribute def UnsignedInteger :> Integer {
        assert constraint { self >= 0 }
    }

    attribute def UnsignedFloat :> Real {
        assert constraint { self >= 0.0 }
    }

    attribute def DateTime :> String;

    attribute def StatusLevel :> String {
        assert constraint { self == "critical" or self == "warning" or self == "normal" }
    }

    attribute def ColorLabel :> String {
        assert constraint { self == "red" or self == "yellow" or self == "green" }
    }

    attribute def DiameterSize :> String {
        assert constraint { self == "Small (60 mm)" or self == "Medium (70 mm)" or self == "Large (80 mm)" }
    }

    attribute def Diameter {
        attribute size: DiameterSize default "Small (60 mm)";
        attribute value: UnsignedFloat;
        assert constraint {
            (size == "Small (60 mm)" implies value == 60.0) and
            (size == "Medium (70 mm)" implies value == 70.0) and
            (size == "Large (80 mm)" implies value == 80.0)
        }
    }

    calc def statusToColor {
        in status: StatusLevel;
        return color: ColorLabel = 
            if status == "critical" then "red"
            else if status == "warning" then "yellow" 
            else "green";
    }

    part dataManager {
        attribute unsignedInt: UnsignedInteger;
        attribute unsignedFloat: UnsignedFloat;
        attribute dateTime: DateTime;
        attribute stringValue: String;
        attribute booleanValue: Boolean;
        attribute currentStatus: StatusLevel;
        attribute statusColor: ColorLabel = statusToColor(currentStatus);
        attribute diameter: Diameter;
    }
}

